/* eslint-disable @typescript-eslint/no-unused-vars */
'use client';

import React, { useState, useEffect, useRef } from 'react';
import { useTheme } from 'next-themes';
import { motion, AnimatePresence } from 'framer-motion';
import { ShoppingCart, Menu, Globe, ChevronLeft, Search, X, MonitorSmartphone, Facebook, Instagram, X as LucideX, Plus, Minus, AlertCircle, FileText, Moon, Sun, MapPin, PhoneCall } from 'lucide-react';
import SharedMarquee from '../menu/SharedMarquee';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, Pagination } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/pagination';

type MenuItem = {
    id: string | number;
    title_ar: string;
    title_en?: string;
    description_ar?: string;
    description_en?: string;
    image?: string;
    image_url?: string;
    prices: number[];
    size_labels?: string[];
    extras?: { id?: number | string; name_ar: string; name_en?: string; price: number }[];
    is_available?: boolean;
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    [key: string]: any;
};

interface CategoryWithItemsType {
    id: string | number;
    name_ar: string;
    name_en?: string;
    items?: MenuItem[];
    image_url?: string;
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    [key: string]: any;
}

interface RestaurantType {
    name: string;
    theme?: string;
    theme_colors?: {
        main_color?: string;
        bg_color?: string;
        text_color?: string;
    };
    cover_images?: string[];
    marquee_enabled?: boolean;
    marquee_text_ar?: string;
    marquee_text_en?: string;
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    [key: string]: any;
}

interface Theme12MenuProps {
    config: RestaurantType;
    categories: CategoryWithItemsType[];
}

export default function Theme12Menu({ config, categories }: Theme12MenuProps) {
    const { theme, setTheme } = useTheme();
    const [mounted, setMounted] = useState(false);
    const [isPhoneMenuOpen, setIsPhoneMenuOpen] = useState(false);
    useEffect(() => setMounted(true), []);

    const isAr = config.default_language === 'ar' || true; // Defaulting to true for now since design is RTL primary
    const isDark = mounted && theme === 'dark';
    const cur = '';
    const bgBody = isDark ? '#1a1a2e' : '#f5f6fa';
    const textMain = isDark ? '#f5f6fa' : '#2d3436';
    const primaryColor = config.theme_colors?.main_color || '#6c63ff';

    const getPrimaryColor = () => primaryColor;
    const getLogo = () => config.logo_url || '/asn-logo.png';
    const getRestaurantName = () => isAr ? config.name : (config.name_en || config.name);
    const coverImages = config.cover_images || [];
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
    const [isCartOpen, setIsCartOpen] = useState(false);
    const [sliderLoaded] = useState(true);
    const [isLangMenuOpen, setIsLangMenuOpen] = useState(false);
    const [isSearchOpen, setIsSearchOpen] = useState(false);
    const [searchQuery, setSearchQuery] = useState('');

    // Cart state 
    const [cart, setCart] = useState<{ id: string, item: MenuItem, catName: string, price: number, sizeLabel: string, quantity: number, notes: string, catImg?: string }[]>([]);
    const cartItemCount = cart.reduce((acc, curr) => acc + curr.quantity, 0);
    const cartTotal = cart.reduce((acc, curr) => acc + (curr.price * curr.quantity), 0);

    // Modal state
    const [selectedItem, setSelectedItem] = useState<{ item: MenuItem; catName: string; catImg?: string } | null>(null);
    const [qty, setQty] = useState(1);
    const [sizeIdx, setSizeIdx] = useState(0);
    const [notes, setNotes] = useState('');
    const [selectedExtras, setSelectedExtras] = useState<{ id: number | string, name: string, price: number }[]>([]);

    const openModal = (item: MenuItem, cName: string, cImg?: string) => {
        setSelectedItem({ item, catName: cName, catImg: cImg });
        setQty(1);
        setSizeIdx(0);
        setSelectedExtras([]);
        setNotes('');
        document.body.style.overflow = 'hidden';
    };

    const closeModal = () => {
        setSelectedItem(null);
        document.body.style.overflow = 'auto';
    };

    const addToCart = () => {
        if (!selectedItem) return;
        const itemP = selectedItem.item.prices[sizeIdx] || 0;
        const extP = selectedExtras.reduce((sum, e) => sum + e.price, 0);
        const finalPrice = itemP + extP;

        let sizeLbl = selectedItem.item.size_labels?.[sizeIdx] || '';
        if (selectedExtras.length > 0) {
            const extNames = selectedExtras.map(e => e.name).join(' + ');
            sizeLbl = sizeLbl ? `${sizeLbl} | +${extNames}` : `+${extNames}`;
        }

        const cId = `${selectedItem.item.id}-${sizeIdx}-${selectedExtras.map(e => e.id).sort().join('-')}`;

        setCart(prev => {
            const ex = prev.find(c => c.id === cId && c.notes === notes);
            if (ex) return prev.map(c => c.id === cId && c.notes === notes ? { ...c, quantity: c.quantity + qty } : c);
            return [...prev, {
                id: cId,
                item: selectedItem.item,
                catName: selectedItem.catName,
                price: finalPrice,
                sizeLabel: sizeLbl,
                quantity: qty,
                notes,
                catImg: selectedItem.catImg
            }];
        });
        closeModal();
        setIsCartOpen(true);
    };

    const itemName = (item: MenuItem) => isAr ? item.title_ar : (item.title_en || item.title_ar);
    const catName = (cat: CategoryWithItemsType) => isAr ? cat.name_ar : (cat.name_en || cat.name_ar);

    const headerRef = useRef<HTMLElement>(null);
    const currentLang = isAr ? 'ar' : 'en';

    // Handle scroll for header
    useEffect(() => {
        const handleScroll = () => {
            if (headerRef.current) {
                if (window.scrollY > 50) {
                    headerRef.current.classList.add('scrolled');
                } else {
                    headerRef.current.classList.remove('scrolled');
                }
            }
        };
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    const toggleTheme = () => {
        setTheme(isDark ? 'light' : 'dark');
    };

    const handleLanguageChange = (lang: string) => {
        document.cookie = `NEXT_LOCALE=${lang}; path=/`;
        window.location.reload();
    };

    const scrollToCategory = (categoryId: string) => {
        const element = document.getElementById(`category-${categoryId}`);
        if (element) {
            const headerOffset = 180; // Header + Banner
            const elementPosition = element.getBoundingClientRect().top;
            const offsetPosition = elementPosition + window.pageYOffset - headerOffset;
            window.scrollTo({
                top: offsetPosition,
                behavior: 'smooth'
            });
            setIsMobileMenuOpen(false);
        }
    };

    return (
        <div className={`min-h-screen font-sans transition-colors duration-300`} style={{ backgroundColor: bgBody, color: textMain, '--primary-color': getPrimaryColor() } as React.CSSProperties} dir={isAr ? 'rtl' : 'ltr'}>

            {/* Global Theme 12 Styles are in theme12.css */}

            {/* Marquee Header */}
            {config.marquee_enabled && (
                <div className="bg-[#6c63ff] font-tajawal text-sm text-white">
                    <SharedMarquee
                        text={isAr ? (config.marquee_text_ar || '') : (config.marquee_text_en || config.marquee_text_ar || '')}
                    />
                </div>
            )}

            {/* Header */}
            <header ref={headerRef} className="sticky top-0 z-50 transition-all duration-300 bg-white/90 dark:bg-[#1a1a2e]/90 backdrop-blur-md border-b border-[#dfe6e9] dark:border-white/5 py-3">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex justify-between items-center h-16">

                        {/* Left: Mobile Menu & Actions */}
                        <div className="flex items-center gap-2 lg:gap-4">
                            <button
                                onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                                className="lg:hidden p-2 rounded-xl bg-gray-100 dark:bg-white/5 text-gray-800 dark:text-gray-200"
                            >
                                {isMobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
                            </button>

                            <button
                                onClick={toggleTheme}
                                className="p-2 rounded-xl bg-gray-100 dark:bg-white/5 text-gray-800 dark:text-gray-200 hover:-translate-y-1 transition-transform"
                            >
                                {isDark ? <Sun size={20} /> : <Moon size={20} />}
                            </button>

                            <div className="flex items-center gap-2">
                                {/* Social & Contact Icons */}
                                {config.whatsapp_number && (
                                    <a
                                        href={`https://wa.me/${config.whatsapp_number}`}
                                        target="_blank"
                                        rel="noopener noreferrer"
                                        className="w-10 h-10 rounded-full bg-[#25D366]/10 text-[#25D366] flex items-center justify-center hover:bg-[#25D366] hover:text-white transition-colors"
                                    >
                                        <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path d="M17.472 14.382c-.297-.149-1.758-.867-2.03-.967-.273-.099-.471-.148-.67.15-.197.297-.767.966-.94 1.164-.173.199-.347.223-.644.075-.297-.15-1.255-.463-2.39-1.475-.883-.788-1.48-1.761-1.653-2.059-.173-.297-.018-.458.13-.606.134-.133.298-.347.446-.52.149-.174.198-.298.298-.497.099-.198.05-.371-.025-.52-.075-.149-.669-1.612-.916-2.207-.242-.579-.487-.5-.669-.51-.173-.008-.371-.01-.57-.01-.198 0-.52.074-.792.372-.272.297-1.04 1.016-1.04 2.479 0 1.462 1.065 2.875 1.213 3.074.149.198 2.096 3.2 5.077 4.487.709.306 1.262.489 1.694.625.712.227 1.36.195 1.871.118.571-.085 1.758-.719 2.006-1.413.248-.694.248-1.289.173-1.413-.074-.124-.272-.198-.57-.347m-5.421 7.403h-.004a9.87 9.87 0 01-5.031-1.378l-.361-.214-3.741.982.998-3.648-.235-.374a9.86 9.86 0 01-1.51-5.26c.001-5.45 4.436-9.884 9.888-9.884 2.64 0 5.122 1.03 6.988 2.898a9.825 9.825 0 012.893 6.994c-.003 5.45-4.437 9.884-9.885 9.884m8.413-18.297A11.815 11.815 0 0012.05 0C5.495 0 .16 5.335.157 11.892c0 2.096.547 4.142 1.588 5.945L.057 24l6.305-1.654a11.882 11.882 0 005.683 1.448h.005c6.554 0 11.89-5.335 11.893-11.893a11.821 11.821 0 00-3.48-8.413z" /></svg>
                                    </a>
                                )}
                                {/* Phone Icon with Dropdown for Delivery Numbers */}
                                {(config.phone || (config.phones && config.phones.length > 0)) && (
                                    <div className="relative">
                                        <button
                                            onClick={() => setIsPhoneMenuOpen(!isPhoneMenuOpen)}
                                            className="w-10 h-10 rounded-full bg-blue-500/10 text-blue-500 flex items-center justify-center hover:bg-blue-500 hover:text-white transition-colors"
                                        >
                                            <PhoneCall size={20} />
                                        </button>

                                        <AnimatePresence>
                                            {isPhoneMenuOpen && (
                                                <motion.div
                                                    initial={{ opacity: 0, y: 10 }}
                                                    animate={{ opacity: 1, y: 0 }}
                                                    exit={{ opacity: 0, y: 10 }}
                                                    className="absolute top-full mt-2 left-1/2 -translate-x-1/2 w-48 bg-white dark:bg-[#16213e] rounded-xl shadow-lg border border-gray-100 dark:border-white/10 py-2 z-50 overflow-hidden"
                                                >
                                                    <div className="px-4 py-2 border-b border-gray-100 dark:border-white/10 text-xs font-bold text-gray-500 uppercase text-center">
                                                        أرقام الديلفري
                                                    </div>
                                                    {config.phones && config.phones.length > 0 ? (
                                                        config.phones.map((phoneNum: string, idx: number) => (
                                                            <a
                                                                key={idx}
                                                                href={`tel:${phoneNum}`}
                                                                className="block px-4 py-2.5 text-center text-sm font-bold text-gray-800 dark:text-gray-200 hover:bg-gray-50 dark:hover:bg-white/5 transition-colors"
                                                            >
                                                                {phoneNum}
                                                            </a>
                                                        ))
                                                    ) : (
                                                        <a
                                                            href={`tel:${config.phone}`}
                                                            className="block px-4 py-2.5 text-center text-sm font-bold text-gray-800 dark:text-gray-200 hover:bg-gray-50 dark:hover:bg-white/5 transition-colors"
                                                        >
                                                            {config.phone}
                                                        </a>
                                                    )}
                                                </motion.div>
                                            )}
                                        </AnimatePresence>
                                    </div>
                                )}
                                {config.map_link ? (
                                    <a
                                        href={config.map_link}
                                        target="_blank"
                                        rel="noopener noreferrer"
                                        className="w-10 h-10 rounded-full bg-red-500/10 text-red-500 flex items-center justify-center hover:bg-red-500 hover:text-white transition-colors"
                                    >
                                        <MapPin size={20} />
                                    </a>
                                ) : (config.latitude && config.longitude && (
                                    <a
                                        href={`https://www.google.com/maps/search/?api=1&query=${config.latitude},${config.longitude}`}
                                        target="_blank"
                                        rel="noopener noreferrer"
                                        className="w-10 h-10 rounded-full bg-red-500/10 text-red-500 flex items-center justify-center hover:bg-red-500 hover:text-white transition-colors"
                                    >
                                        <MapPin size={20} />
                                    </a>
                                ))}
                                {config.facebook_url && (
                                    <a
                                        href={config.facebook_url}
                                        target="_blank"
                                        rel="noopener noreferrer"
                                        className="w-10 h-10 rounded-full bg-[#1877F2]/10 text-[#1877F2] flex items-center justify-center hover:bg-[#1877F2] hover:text-white transition-colors"
                                    >
                                        <Facebook size={20} />
                                    </a>
                                )}
                                {config.website_url && (
                                    <a
                                        href={config.website_url}
                                        target="_blank"
                                        rel="noopener noreferrer"
                                        className="w-10 h-10 rounded-full bg-gray-500/10 text-gray-500 flex items-center justify-center hover:bg-gray-500 hover:text-white transition-colors dark:bg-white/10 dark:text-gray-300 dark:hover:bg-white dark:hover:text-[#1a1a2e]"
                                    >
                                        <Globe size={20} />
                                    </a>
                                )}
                            </div>
                        </div>

                        {/* Center: Logo */}
                        <div className="absolute left-1/2 -translate-x-1/2 lg:relative lg:left-0 lg:translate-x-0">
                            <a href="#" className="flex items-center gap-2">
                                <div className="w-12 h-12 lg:w-14 lg:h-14 relative rounded-full overflow-hidden border-2 border-[#6c63ff] p-0.5 bg-white">
                                    <img src={getLogo()} alt={getRestaurantName()} className="w-full h-full object-cover rounded-full" />
                                </div>
                                <span className="hidden lg:block text-xl font-extrabold text-[#6c63ff] uppercase tracking-wider">{getRestaurantName()}</span>
                            </a>
                        </div>

                        {/* Right: Cart */}
                        <div className="flex items-center gap-4 lg:gap-6">
                            <button
                                onClick={() => setIsCartOpen(true)}
                                className="relative p-2.5 lg:p-3 lg:px-5 rounded-xl lg:rounded-[20px] bg-gradient-to-r from-[#6366f1] to-[#4f46e5] text-white shadow-[0_4px_15px_rgba(99,102,241,0.3)] hover:-translate-y-1 transition-all flex items-center gap-3"
                            >
                                <div className="relative">
                                    <ShoppingCart size={20} />
                                    {cartItemCount > 0 && (
                                        <span className="absolute -top-2 -right-2 bg-[#ff4757] text-white text-[10px] font-bold w-5 h-5 flex items-center justify-center rounded-full border-2 border-white dark:border-[#1a1a2e]">
                                            {cartItemCount}
                                        </span>
                                    )}
                                </div>
                                <span className="hidden lg:block font-bold">السلة</span>
                            </button>
                        </div>

                    </div>
                </div>

                {/* Mobile Navigation Dropdown */}
                <div className={`lg:hidden absolute top-full left-0 w-full bg-white dark:bg-[#16213e] border-b border-gray-100 dark:border-white/5 shadow-lg transition-all duration-300 ${isMobileMenuOpen ? 'max-h-[400px] opacity-100 visible' : 'max-h-0 opacity-0 invisible'} `}>
                    <div className="p-4 flex flex-col gap-2">
                        {config.whatsapp_number && (
                            <a href={`https://wa.me/${config.whatsapp_number}`} className="px-4 py-3 text-gray-800 dark:text-gray-200 font-semibold rounded-xl text-center hover:bg-gray-50 dark:hover:bg-white/5 flex items-center justify-center gap-2">
                                واتس اب
                            </a>
                        )}
                        {config.phone && (
                            <a href={`tel:${config.phone}`} className="px-4 py-3 text-gray-800 dark:text-gray-200 font-semibold rounded-xl text-center hover:bg-gray-50 dark:hover:bg-white/5 flex items-center justify-center gap-2">
                                <PhoneCall size={18} /> اتصال
                            </a>
                        )}
                        <div className="pt-2 mt-2 border-t border-gray-100 dark:border-white/5 flex justify-center gap-4">
                            {config.facebook_url && <a href={config.facebook_url} target="_blank" className="w-10 h-10 rounded-full bg-[#1877F2]/10 text-[#1877F2] flex items-center justify-center"><Facebook size={20} /></a>}
                            {config.website_url && <a href={config.website_url} target="_blank" className="w-10 h-10 rounded-full bg-gray-500/10 text-gray-500 flex items-center justify-center"><Globe size={20} /></a>}
                        </div>
                    </div>
                </div>
            </header >

            {/* Main Content */}
            < main className="pb-24" >

                {/* Banner Carousel */}
                {
                    coverImages.length > 0 && sliderLoaded && (
                        <div className="relative w-full h-[60vw] max-h-[450px] min-h-[200px] bg-gray-100 dark:bg-gray-800 overflow-hidden">
                            <Swiper
                                modules={[Autoplay, Pagination]}
                                pagination={{ clickable: true }}
                                autoplay={{ delay: 5000, disableOnInteraction: false }}
                                loop={coverImages.length > 1}
                                className="w-full h-full"
                            >
                                {coverImages.map((src, i) => (
                                    <SwiperSlide key={i} className="w-full h-full">
                                        <div className="relative w-full h-full">
                                            <img src={src} alt={`Banner ${i + 1} `} className="w-full h-full object-cover" />
                                            {/* Optional overlay gradient for better text readability if titles exist */}
                                            <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent"></div>
                                        </div>
                                    </SwiperSlide>
                                ))}
                            </Swiper>
                        </div>
                    )
                }

                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 -mt-6 relative z-10">

                    {/* Categories Section */}
                    <div className="bg-white dark:bg-[#16213e] rounded-[24px] shadow-[0_15px_35px_rgba(0,0,0,0.08)] dark:shadow-[0_15px_35px_rgba(0,0,0,0.3)] p-4 sm:p-6 mb-10 overflow-hidden">
                        <div className="flex gap-4 sm:gap-6 overflow-x-auto pb-4 hide-scrollbar snap-x snap-mandatory">
                            {categories.map((cat) => (
                                <button
                                    key={cat.id}
                                    onClick={() => scrollToCategory(String(cat.id))}
                                    className="snap-start flex flex-col items-center gap-3 min-w-[90px] group flex-shrink-0"
                                >
                                    <div className="w-20 h-20 sm:w-24 sm:h-24 rounded-full p-1 bg-gradient-to-tr from-[#6c63ff] to-[#00b894] shadow-[0_8px_15px_rgba(108,99,255,0.25)] group-hover:-translate-y-2 group-hover:shadow-[0_12px_20px_rgba(108,99,255,0.4)] transition-all duration-300">
                                        <div className="w-full h-full rounded-full overflow-hidden bg-white dark:bg-gray-800 border-[3px] border-white dark:border-[#16213e] relative">
                                            <img src={cat.image_url || '/default-category.png'} alt={cat.name_en || cat.name_ar} className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500" />
                                        </div>
                                    </div>
                                    <span className="font-bold text-sm text-center text-gray-800 dark:text-gray-200 group-hover:text-[#6c63ff] bg-gray-50 dark:bg-white/5 px-4 py-1.5 rounded-full transition-colors whitespace-nowrap">
                                        {catName(cat)}
                                    </span>
                                </button>
                            ))}
                        </div>
                    </div>

                    {/* Search Section */}
                    <div className="mb-10 relative max-w-2xl mx-auto">
                        <div className="relative group">
                            <input
                                type="text"
                                placeholder={isAr ? "البحث..." : "Search..."}
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                onFocus={() => setIsSearchOpen(true)}
                                className="w-full h-14 pl-14 pr-6 rounded-full bg-white dark:bg-[#16213e] border-2 border-transparent focus:border-[#6c63ff] shadow-[0_8px_20px_rgba(0,0,0,0.05)] dark:shadow-[0_8px_20px_rgba(0,0,0,0.2)] text-gray-800 dark:text-white font-medium outline-none transition-all placeholder:text-gray-400 group-hover:shadow-[0_12px_25px_rgba(108,99,255,0.15)] focus:shadow-[0_0_0_4px_rgba(108,99,255,0.15)]"
                            />
                            <Search className="absolute left-5 top-1/2 -translate-y-1/2 text-gray-400 group-hover:text-[#6c63ff] transition-colors" size={24} />
                            {searchQuery && (
                                <button onClick={() => { setSearchQuery(''); setIsSearchOpen(false) }} className="absolute right-5 top-1/2 -translate-y-1/2 w-8 h-8 flex items-center justify-center rounded-full bg-gray-100 dark:bg-white/10 text-gray-500 hover:text-red-500 hover:bg-red-50 dark:hover:bg-red-500/20 transition-colors">
                                    <LucideX size={16} />
                                </button>
                            )}
                        </div>

                        {/* Search Results Dropdown */}
                        {isSearchOpen && searchQuery && (
                            <div className="absolute top-full left-0 right-0 mt-3 bg-white dark:bg-[#16213e] rounded-2xl shadow-[0_15px_40px_rgba(0,0,0,0.1)] dark:shadow-[0_15px_40px_rgba(0,0,0,0.4)] border border-gray-100 dark:border-white/5 overflow-hidden z-40 max-h-[400px] overflow-y-auto">
                                <div className="p-8 text-center text-gray-500 dark:text-gray-400 flex flex-col items-center gap-3">
                                    <Search size={40} className="opacity-30" />
                                    <p>لم يتم العثور على نتائج</p>
                                </div>
                            </div>
                        )}
                    </div>

                    {/* Menu Items */}
                    <div className="space-y-12">
                        {categories.map(category => {
                            const categoryItems = categories.find(c => c.id === category.id)?.items || [];
                            if (categoryItems.length === 0) return null;

                            return (
                                <div key={category.id} id={`category-${category.id}`} className="scroll-mt-32">
                                    <h2 className="text-2xl md:text-3xl font-black mb-8 text-[#2d3436] dark:text-[#f5f6fa] relative inline-block">
                                        <span className="relative z-10">{catName(category)}</span>
                                        <span className="absolute bottom-1 left-0 w-full h-3 bg-[#6c63ff]/20 -rotate-1 rounded-sm -z-0"></span>
                                    </h2>
                                    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                                        {categoryItems.map(item => (
                                            <div key={item.id} id={`item-${item.id}`} className="bg-white dark:bg-[#16213e] rounded-2xl overflow-hidden shadow-[0_8px_20px_rgba(0,0,0,0.08)] dark:shadow-[0_8px_20px_rgba(0,0,0,0.3)] hover:-translate-y-2 hover:shadow-[0_15px_30px_rgba(108,99,255,0.2)] transition-all duration-300 group flex flex-col">
                                                <div className="relative aspect-[4/3] w-full overflow-hidden">
                                                    <img
                                                        src={item.image_url || category.image_url || 'https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=400'}
                                                        alt={itemName(item)}
                                                        className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500"
                                                    />
                                                    {/* Badges styling from theme 12 */}
                                                </div>
                                                <div className="p-5 flex-1 flex flex-col justify-between">
                                                    <div>
                                                        <h3 className="text-lg font-bold text-gray-800 dark:text-gray-100 mb-2 line-clamp-2 leading-tight group-hover:text-[#6c63ff] transition-colors">{itemName(item)}</h3>
                                                        {(item.desc_ar || item.desc_en) && (
                                                            <p className="text-sm text-gray-500 dark:text-gray-400 line-clamp-2 leading-relaxed mb-4">{isAr ? item.desc_ar : (item.desc_en || item.desc_ar)}</p>
                                                        )}
                                                        {item.size_labels && item.size_labels.length > 0 && (
                                                            <div className="flex flex-wrap gap-2 mb-4">
                                                                {item.size_labels.map((label, idx) => (
                                                                    <div key={idx} className="flex items-center gap-1 bg-gray-50 dark:bg-white/5 border border-gray-100 dark:border-white/10 px-2.5 py-1 rounded-lg">
                                                                        <span className="text-xs font-semibold text-gray-600 dark:text-gray-400">{label}</span>
                                                                        <span className="text-xs font-bold text-[#6c63ff]">{cur}{item.prices[idx]}</span>
                                                                    </div>
                                                                ))}
                                                            </div>
                                                        )}
                                                    </div>

                                                    <div className="flex items-center justify-between mt-auto pt-4 border-t border-gray-100 dark:border-white/10">
                                                        <div className="font-black text-lg text-[#6c63ff]">
                                                            {item.prices?.[0] || 0} <span className="text-sm font-semibold">EGP</span>
                                                        </div>
                                                        <button
                                                            onClick={() => openModal(item, catName(category), category.image_url)}
                                                            className="w-10 h-10 rounded-full bg-gray-50 dark:bg-white/5 border border-gray-200 dark:border-white/10 text-[#6c63ff] flex items-center justify-center hover:bg-[#6c63ff] hover:text-white hover:border-transparent transition-all group-hover:rgb-btn"
                                                        >
                                                            <Plus size={18} />
                                                        </button>
                                                    </div>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            );
                        })}
                    </div>
                </div>
            </main >
            {/* Modal */}
            <AnimatePresence>
                {
                    selectedItem && (
                        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}
                            className="fixed inset-0 z-[200] flex items-end sm:items-center justify-center bg-black/60 backdrop-blur-sm sm:p-4"
                            onClick={closeModal}>
                            <motion.div initial={{ y: '100%' }} animate={{ y: 0 }} exit={{ y: '100%' }}
                                transition={{ type: 'spring', damping: 25, stiffness: 200 }}
                                className="relative w-full sm:max-w-[500px] h-[90vh] sm:h-auto sm:max-h-[90vh] rounded-t-3xl sm:rounded-[24px] overflow-hidden flex flex-col shadow-2xl bg-white dark:bg-[#1a1a2e]"
                                onClick={e => e.stopPropagation()}>

                                {/* Mobile DRAG Handle */}
                                <div className="w-full flex justify-center pt-3 pb-1 sm:hidden absolute top-0 z-30" onClick={closeModal}>
                                    <div className="w-12 h-1.5 rounded-full bg-white/50 backdrop-blur-md" />
                                </div>

                                {/* Close Button UI */}
                                <button onClick={closeModal} className="absolute top-4 left-4 z-20 w-8 h-8 rounded-full bg-white/20 backdrop-blur-md text-white flex items-center justify-center hover:bg-white/40 transition-colors">
                                    <X className="w-5 h-5" />
                                </button>

                                {/* Modal Header Image */}
                                <div className="w-full h-[35vh] sm:h-[250px] shrink-0 relative bg-slate-100 dark:bg-slate-800">
                                    <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent z-10" />
                                    <img src={selectedItem.item.image_url || selectedItem.catImg || 'https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=600'}
                                        alt={itemName(selectedItem.item)} className="w-full h-full object-cover" />

                                    <div className="absolute bottom-4 left-4 right-4 z-20" dir={isAr ? 'rtl' : 'ltr'}>
                                        <h2 className="text-2xl sm:text-3xl font-black text-white drop-shadow-md mb-1">{itemName(selectedItem.item)}</h2>
                                        <p className="text-white/90 text-sm sm:text-base font-bold drop-shadow-sm line-clamp-2">
                                            {isAr ? selectedItem.item.description_ar : (selectedItem.item.description_en || selectedItem.item.description_ar)}
                                        </p>
                                    </div>
                                </div>

                                {/* Modal Content */}
                                <div className="flex-1 overflow-y-auto p-5 pb-24 text-gray-800 dark:text-[#f5f6fa]">

                                    {/* Base Price */}
                                    <div className="flex justify-between items-center bg-gray-50 dark:bg-[#16213e] border border-gray-100 dark:border-white/10 p-4 rounded-xl mb-6">
                                        <span className="font-bold">{isAr ? 'السعر' : 'Price'}</span>
                                        <span className="text-xl font-black text-[#6c63ff]">{cur}{selectedItem.item.prices[sizeIdx]?.toFixed?.(0)}</span>
                                    </div>

                                    {/* Sizes */}
                                    {selectedItem.item.prices.length > 1 && (
                                        <div className="mb-6">
                                            <h3 className="font-bold mb-3 flex items-center gap-2">
                                                {isAr ? 'اختر الحجم' : 'Select Size'}
                                            </h3>
                                            <div className="grid grid-cols-2 gap-3">
                                                {selectedItem.item.prices.map((p, idx) => {
                                                    const label = selectedItem.item.size_labels?.[idx] || (isAr ? `حجم ${idx + 1}` : `Size ${idx + 1}`);
                                                    return (
                                                        <label key={idx} className="flex flex-col items-center justify-center p-3 rounded-xl border-2 cursor-pointer transition-all text-center"
                                                            style={{ borderColor: sizeIdx === idx ? '#6c63ff' : 'transparent', backgroundColor: sizeIdx === idx ? `#6c63ff15` : 'rgba(108, 99, 255, 0.05)' }}
                                                            onClick={() => setSizeIdx(idx)}>
                                                            <span className="font-bold text-sm mb-1">{label}</span>
                                                            <span className="text-xs font-semibold">{cur}{p}</span>
                                                            <input type="radio" checked={sizeIdx === idx} readOnly className="hidden" />
                                                        </label>
                                                    );
                                                })}
                                            </div>
                                        </div>
                                    )}

                                    {/* Extras */}
                                    {selectedItem.item.extras && selectedItem.item.extras.length > 0 && (
                                        <div className="mb-6">
                                            <h3 className="font-bold mb-3 flex items-center gap-2">
                                                <Plus className="w-4 h-4 text-slate-400" />
                                                {isAr ? 'إضافات (اختياري)' : 'Extras (Optional)'}
                                            </h3>
                                            <div className="space-y-3">
                                                {selectedItem.item.extras.map((ext, idx) => {
                                                    const id = ext.id || idx;
                                                    const isSel = selectedExtras.some(e => e.id === id);
                                                    return (
                                                        <label key={idx} className="flex items-center justify-between p-3 rounded-xl border border-dashed cursor-pointer transition-colors"
                                                            style={{ borderColor: isSel ? '#6c63ff' : 'rgba(255,255,255,0.1)', backgroundColor: isSel ? `#6c63ff05` : 'transparent' }}>
                                                            <input type="checkbox" className="hidden" checked={isSel}
                                                                onChange={() => {
                                                                    if (isSel) setSelectedExtras(p => p.filter(e => e.id !== id));
                                                                    else setSelectedExtras(p => [...p, { id, name: isAr ? ext.name_ar : (ext.name_en || ext.name_ar), price: ext.price }]);
                                                                }} />
                                                            <div className="flex items-center gap-3">
                                                                <div className="w-5 h-5 rounded border flex items-center justify-center transition-colors"
                                                                    style={{ borderColor: isSel ? '#6c63ff' : 'gray', backgroundColor: isSel ? '#6c63ff' : 'transparent' }}>
                                                                    {isSel && <X className="w-3 h-3 text-white" style={{ transform: 'rotate(45deg)' }} />}
                                                                </div>
                                                                <span className="text-sm font-semibold">{isAr ? ext.name_ar : (ext.name_en || ext.name_ar)}</span>
                                                            </div>
                                                            <span className="text-sm font-bold text-[#6c63ff]">{ext.price > 0 ? `+${cur}${ext.price}` : (isAr ? 'مجاناً' : 'Free')}</span>
                                                        </label>
                                                    );
                                                })}
                                            </div>
                                        </div>
                                    )}

                                    {/* Notes */}
                                    <div className="mb-6">
                                        <h3 className="font-bold mb-3 flex items-center gap-2">
                                            <FileText className="w-4 h-4 text-slate-400" />
                                            {isAr ? 'ملاحظات' : 'Notes'}
                                        </h3>
                                        <textarea value={notes} onChange={e => setNotes(e.target.value)}
                                            placeholder={isAr ? 'أضف ملاحظاتك الخاصة هنا...' : 'Special instructions...'}
                                            className="w-full rounded-xl p-3 outline-none resize-none h-24 text-sm border focus:ring-1 transition-all bg-gray-50 dark:bg-[#16213e] border-gray-100 dark:border-white/10"
                                            style={{ '--tw-ring-color': '#6c63ff' } as React.CSSProperties} />
                                    </div>
                                </div>

                                {/* Sticky Bottom Actions */}
                                <div className="absolute bottom-0 w-full p-4 border-t shadow-[0_-10px_20px_rgba(0,0,0,0.05)] bg-white dark:bg-[#1a1a2e] border-gray-100 dark:border-white/10">
                                    <div className="flex gap-4 items-center">
                                        <div className="flex items-center bg-gray-50 dark:bg-[#16213e] rounded-full h-12 px-2 border border-gray-100 dark:border-white/10">
                                            <button onClick={() => setQty(Math.max(1, qty - 1))} className="w-8 h-8 rounded-full flex flex-col items-center justify-center text-gray-500 hover:bg-gray-200 dark:hover:bg-gray-700">
                                                <Minus className="w-4 h-4" />
                                            </button>
                                            <span className="w-8 text-center font-bold text-lg">{qty}</span>
                                            <button onClick={() => setQty(qty + 1)} className="w-8 h-8 rounded-full flex flex-col items-center justify-center text-gray-500 hover:bg-gray-200 dark:hover:bg-gray-700">
                                                <Plus className="w-4 h-4" />
                                            </button>
                                        </div>
                                        <button onClick={addToCart} className="flex-1 h-12 rounded-full text-white font-bold text-sm sm:text-base flex items-center justify-center gap-2 transition-transform active:scale-95 shadow-md hover:shadow-lg bg-[#6c63ff]">
                                            <ShoppingCart className="w-5 h-5" />
                                            {isAr ? 'إضافة إلى السلة' : 'Add to Cart'} — {cur}{(((selectedItem.item.prices[sizeIdx] || 0) + selectedExtras.reduce((s, e) => s + e.price, 0)) * qty).toFixed?.(0)}
                                        </button>
                                    </div>
                                </div>
                            </motion.div>
                        </motion.div>
                    )
                }
            </AnimatePresence >

            {/* Cart Overlay */}
            < div className={`fixed inset-0 bg-black/60 backdrop-blur-sm z-[10000] transition-opacity duration-300 flex justify-end ${isCartOpen ? 'opacity-100 visible' : 'opacity-0 invisible'}`
            }>
                <div className={`w-full max-w-md h-full bg-white dark:bg-[#1a1a2e] flex flex-col shadow-[-4px_0_30px_rgba(0,0,0,0.2)] dark:shadow-[-4px_0_30px_rgba(0,0,0,0.5)] transition-transform duration-400 ease-out ${isCartOpen ? 'translate-x-0' : 'translate-x-full'}`}>
                    {/* Cart Header */}
                    <div className="bg-gradient-to-br from-[#6366f1] to-[#4f46e5] dark:from-[#4f46e5] dark:to-[#4338ca] text-white p-5 flex items-center justify-between">
                        <div className="flex items-center gap-3">
                            <ShoppingCart size={24} />
                            <div>
                                <h2 className="text-xl font-bold m-0">السلة</h2>
                                <span className="text-sm opacity-90">{cartItemCount} عنصر</span>
                            </div>
                        </div>
                        <button
                            onClick={() => setIsCartOpen(false)}
                            className="w-10 h-10 rounded-xl bg-white/20 hover:bg-white/30 flex items-center justify-center transition-all hover:rotate-90"
                        >
                            <LucideX size={20} />
                        </button>
                    </div>

                    {/* Cart Body */}
                    <div className="flex-1 overflow-y-auto p-5 bg-gray-50 dark:bg-[#16213e]">
                        {cartItemCount === 0 ? (
                            <div className="h-full flex flex-col items-center justify-center text-center p-8">
                                <div className="w-32 h-32 bg-gradient-to-br from-[#e0e7ff] to-[#c7d2fe] dark:from-[#16213e] dark:to-[#0f3460] rounded-full flex items-center justify-center mb-6">
                                    <ShoppingCart size={50} className="text-[#6366f1] dark:text-[#818cf8]" />
                                </div>
                                <h3 className="text-xl font-bold text-gray-800 dark:text-[#f5f6fa] mb-2">السلة فارغة</h3>
                                <p className="text-gray-500 dark:text-[#a4b0be] mb-8">ابدأ بإضافة المنتجات المفضلة لديك</p>
                                <button
                                    onClick={() => setIsCartOpen(false)}
                                    className="bg-gradient-to-r from-[#6366f1] to-[#4f46e5] text-white px-8 py-3 rounded-xl font-bold flex items-center gap-2 hover:-translate-y-1 hover:shadow-lg transition-all"
                                >
                                    تصفح المنيو
                                </button>
                            </div>
                        ) : (
                            <div className="space-y-4">
                                {cart.map((c) => (
                                    <div key={c.id} className="bg-white dark:bg-[#1a1a2e] border border-gray-100 dark:border-white/10 p-4 rounded-xl flex gap-4 shadow-sm hover:shadow-md transition-shadow">
                                        <div className="w-20 h-20 rounded-lg overflow-hidden flex-shrink-0 relative border border-gray-100 dark:border-white/10">
                                            <img src={c.item.image_url || c.catImg || 'https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=200'} alt={itemName(c.item)} className="w-full h-full object-cover" />
                                        </div>
                                        <div className="flex-1 flex flex-col justify-between">
                                            <div className="flex justify-between items-start gap-2">
                                                <h4 className="font-bold text-gray-800 dark:text-[#f5f6fa] text-sm line-clamp-2">{itemName(c.item)}</h4>
                                                <button
                                                    onClick={() => setCart(cart.filter(cartItem => cartItem.id !== c.id))}
                                                    className="text-red-500 bg-red-50 dark:bg-red-500/10 hover:bg-red-100 dark:hover:bg-red-500/20 w-8 h-8 rounded-lg flex items-center justify-center flex-shrink-0 transition-colors"
                                                >
                                                    <X size={16} />
                                                </button>
                                            </div>
                                            {(c.sizeLabel) && (
                                                <div className="text-xs text-[#6c63ff] font-medium mt-1">
                                                    {c.sizeLabel}
                                                </div>
                                            )}
                                            <div className="flex items-center justify-between mt-3 pt-3 border-t border-gray-100 dark:border-white/10">
                                                <div className="font-bold text-[#6366f1] dark:text-[#818cf8] text-sm whitespace-nowrap">
                                                    {c.price * c.quantity} EGP
                                                </div>
                                                <div className="flex items-center gap-2 bg-gray-50 dark:bg-white/5 rounded-lg p-1">
                                                    <button
                                                        onClick={() => setCart(cart.map(cartItem => cartItem.id === c.id ? { ...cartItem, quantity: Math.max(1, cartItem.quantity - 1) } : cartItem))}
                                                        className="w-7 h-7 rounded flex items-center justify-center bg-white dark:bg-white/10 shadow-sm text-red-500 hover:bg-red-500 hover:text-white transition-colors"
                                                        disabled={c.quantity <= 1}
                                                    >
                                                        -
                                                    </button>
                                                    <span className="w-6 text-center font-bold text-gray-800 dark:text-[#f5f6fa] text-sm">{c.quantity}</span>
                                                    <button
                                                        onClick={() => setCart(cart.map(cartItem => cartItem.id === c.id ? { ...cartItem, quantity: cartItem.quantity + 1 } : cartItem))}
                                                        className="w-7 h-7 rounded flex items-center justify-center bg-white dark:bg-white/10 shadow-sm text-green-500 hover:bg-green-500 hover:text-white transition-colors"
                                                    >
                                                        +
                                                    </button>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>

                    {/* Cart Footer */}
                    {cartItemCount > 0 && (
                        <div className="bg-white dark:bg-[#16213e] p-5 border-t-2 border-gray-100 dark:border-white/10 flex flex-col gap-4">
                            <div className="bg-gray-50 dark:bg-white/5 p-4 rounded-xl space-y-3">
                                <div className="flex justify-between text-sm text-gray-600 dark:text-[#a4b0be]">
                                    <span>المجموع الفرعي</span>
                                    <span className="font-bold text-gray-800 dark:text-[#f5f6fa]">{cartTotal} EGP</span>
                                </div>
                                <div className="flex justify-between text-sm text-gray-600 dark:text-[#a4b0be]">
                                    <span>رسوم التوصيل</span>
                                    <span className="font-bold text-gray-800 dark:text-[#f5f6fa]">0.00 EGP</span>
                                </div>
                                <div className="h-px w-full bg-gray-200 dark:bg-white/10"></div>
                                <div className="flex justify-between items-center text-lg">
                                    <span className="font-bold text-gray-800 dark:text-[#f5f6fa]">الإجمالي</span>
                                    <span className="font-black text-[#6366f1] dark:text-[#818cf8] text-xl">{cartTotal} EGP</span>
                                </div>
                            </div>
                            <button className="w-full bg-gradient-to-r from-[#6366f1] to-[#4f46e5] text-white py-4 rounded-xl font-bold text-lg flex items-center justify-center gap-3 hover:-translate-y-1 hover:shadow-[0_8px_20px_rgba(99,102,241,0.3)] transition-all">
                                <span>إتمام الطلب</span>
                                <ChevronLeft size={20} />
                            </button>
                        </div>
                    )}
                </div>
            </div >

            {/* Temporarily hidden */}
        </div >
    );
};

'use client';

import React, { useState, useEffect } from 'react';
import { useTheme } from 'next-themes';
import { motion, AnimatePresence } from 'framer-motion';
import { UtensilsCrossed, AlertCircle, Moon, Sun, ShoppingCart, Plus, Minus, Trash2, X, FileText, MapPin as MapIcon, List, Globe, PhoneCall } from 'lucide-react';
import { FaWhatsapp, FaFacebookF, FaSnapchatGhost } from 'react-icons/fa';
import SharedMarquee from './SharedMarquee';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, FreeMode } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/free-mode';

type MenuItem = {
    id: string | number;
    title_ar: string;
    title_en?: string;
    description_ar?: string;
    description_en?: string;
    image?: string;
    image_url?: string;
    prices: number[];
    size_labels?: string[];
    extras?: { id?: number | string; name_ar: string; name_en?: string; price: number }[];
    is_available?: boolean;
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    [key: string]: any;
};

// ================= THEME 10 CONSTANTS =================
const T10_PRIMARY = '#ea580c'; // orange-600

interface CategoryWithItemsType {
    id: string | number;
    name_ar: string;
    name_en?: string;
    items?: MenuItem[];
    image_url?: string;
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    [key: string]: any;
}

interface RestaurantType {
    name: string;
    theme?: string;
    theme_colors?: {
        main_color?: string;
        bg_color?: string;
        text_color?: string;
    };
    cover_images?: string[];
    marquee_enabled?: boolean;
    marquee_text_ar?: string;
    marquee_text_en?: string;
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    [key: string]: any;
}

interface Theme10MenuProps {
    config: RestaurantType;
    categories: CategoryWithItemsType[];
}

export default function Theme10Menu({ config, categories }: Theme10MenuProps) {
    const { theme, setTheme } = useTheme();
    const [mounted, setMounted] = useState(false);
    const [isPhoneMenuOpen, setIsPhoneMenuOpen] = useState(false);
    useEffect(() => setMounted(true), []);

    const isAr = config.default_language === 'ar' || true; // Defaulting to true for now since design is RTL primary
    const isDark = mounted && theme === 'dark';
    const cur = '';

    // Theme Variables - matching HTML
    const bgBody = isDark ? '#0f172a' : '#f8fafc'; // slate-900 / slate-50
    const bgCard = isDark ? '#1e293b' : '#ffffff'; // slate-800 / white
    const textMain = isDark ? '#f1f5f9' : '#0f172a'; // slate-100 / slate-900
    const textMuted = isDark ? '#94a3b8' : '#64748b'; // slate-400 / slate-500
    const borderColor = isDark ? '#334155' : '#e2e8f0'; // slate-700 / slate-200
    const primaryColor = config.theme_colors?.main_color || T10_PRIMARY;

    // State
    const [activeCategory, setActiveCategory] = useState<string>('all');

    // Drawers & Modals
    const [isCartOpen, setIsCartOpen] = useState(false);
    const [selectedItem, setSelectedItem] = useState<{ item: MenuItem; catName: string; catImg?: string } | null>(null);

    // Item Detail Modal
    const [qty, setQty] = useState(1);
    const [sizeIdx, setSizeIdx] = useState(0);
    const [notes, setNotes] = useState('');
    const [selectedExtras, setSelectedExtras] = useState<{ id: number | string, name: string, price: number }[]>([]);

    // Checkout
    const [customerInfo, setCustomerInfo] = useState({ name: '', phone: '', address: '' });
    const [cart, setCart] = useState<{ id: string, item: MenuItem, catName: string, price: number, sizeLabel: string, quantity: number, notes: string }[]>([]);

    const itemName = (item: MenuItem) => isAr ? item.title_ar : (item.title_en || item.title_ar);
    const catName = (cat: CategoryWithItemsType) => isAr ? cat.name_ar : (cat.name_en || cat.name_ar);

    // Derived cart values
    const cartCount = cart.reduce((acc, curr) => acc + curr.quantity, 0);
    const cartTotal = cart.reduce((acc, curr) => acc + (curr.price * curr.quantity), 0);

    // Modals
    const openModal = (item: MenuItem, cName: string, cImg?: string) => {
        setSelectedItem({ item, catName: cName, catImg: cImg });
        setQty(1);
        setSizeIdx(0);
        setSelectedExtras([]);
        setNotes('');
        document.body.style.overflow = 'hidden';
    };

    const closeModal = () => {
        setSelectedItem(null);
        document.body.style.overflow = 'auto';
    };

    // Cart Handlers
    const addToCart = () => {
        if (!selectedItem) return;
        const itemP = selectedItem.item.prices[sizeIdx] || 0;
        const extP = selectedExtras.reduce((sum, e) => sum + e.price, 0);
        const finalPrice = itemP + extP;

        let sizeLbl = selectedItem.item.size_labels?.[sizeIdx] || '';
        if (selectedExtras.length > 0) {
            const extNames = selectedExtras.map(e => e.name).join(' + ');
            sizeLbl = sizeLbl ? `${sizeLbl} | +${extNames}` : `+${extNames}`;
        }

        const cId = `${selectedItem.item.id}-${sizeIdx}-${selectedExtras.map(e => e.id).sort().join('-')}`;

        setCart(prev => {
            const ex = prev.find(c => c.id === cId && c.notes === notes);
            if (ex) return prev.map(c => c.id === cId && c.notes === notes ? { ...c, quantity: c.quantity + qty } : c);
            return [...prev, {
                id: cId,
                item: selectedItem.item,
                catName: selectedItem.catName,
                price: finalPrice,
                sizeLabel: sizeLbl,
                quantity: qty,
                notes
            }];
        });
        closeModal();
        setIsCartOpen(true);
    };

    const updateQty = (id: string, itemNotes: string, delta: number) => {
        setCart(prev => prev.map(c => {
            if (c.id === id && c.notes === itemNotes) {
                const n = c.quantity + delta;
                return n > 0 ? { ...c, quantity: n } : c;
            }
            return c;
        }).filter(c => c.quantity > 0));
    };

    const checkOutWhatsApp = () => {
        if (!config.phone) {
            alert(isAr ? 'رقم المطعم غير متوفر' : 'Restaurant phone unavailable');
            return;
        }
        if (!customerInfo.name || !customerInfo.phone || !customerInfo.address) {
            alert(isAr ? 'يرجى إكمال بيانات التوصيل' : 'Please complete delivery details');
            return;
        }

        let txt = isAr ? `*طلب جديد من ${customerInfo.name}*%0A` : `*New Order from ${customerInfo.name}*%0A`;
        txt += isAr ? `رقم الهاتف: ${customerInfo.phone}%0A` : `Phone: ${customerInfo.phone}%0A`;
        txt += isAr ? `العنوان: ${customerInfo.address}%0A%0A` : `Address: ${customerInfo.address}%0A%0A`;

        cart.forEach(c => {
            txt += `▪ ${itemName(c.item)} (${c.quantity}x)\n`;
            if (c.sizeLabel) txt += `   ${c.sizeLabel}\n`;
            if (c.notes) txt += `   📝 ${c.notes}\n`;
            txt += `   ${cur}${(c.price * c.quantity).toFixed?.(0)}\n\n`;
        });

        txt += isAr ? `*الإجمالي: ${cur}${cartTotal.toFixed?.(0)}*` : `*Total: ${cur}${cartTotal.toFixed?.(0)}*`;

        const tel = config.phone.replace(/\D/g, '');
        window.open(`https://wa.me/${tel}?text=${encodeURIComponent(txt)}`, '_blank');
    };

    // Filter Logic
    const activeCatList = activeCategory === 'all'
        ? categories
        : categories.filter(c => c.id === activeCategory);

    if (!mounted) return <div className="min-h-screen bg-slate-50 dark:bg-slate-900" />;

    return (
        <div className={`min-h-screen font-tajawal pb-24`} style={{ backgroundColor: bgBody, color: textMain }} dir={isAr ? 'rtl' : 'ltr'}>

            {/* Header Marquee */}
            {config.marquee_enabled && (
                <div className="bg-red-600 font-tajawal text-sm text-white">
                    <SharedMarquee
                        text={isAr ? (config.marquee_text_ar || '') : (config.marquee_text_en || config.marquee_text_ar || '')}
                    />
                </div>
            )}

            {/* Top Navigation Bar */}
            <div className="sticky top-0 z-[100] shadow-sm backdrop-blur-md border-b transition-colors duration-300" style={{ backgroundColor: isDark ? 'rgba(30, 41, 59, 0.85)' : 'rgba(255, 255, 255, 0.85)', borderColor }}>
                <div className="max-w-[1200px] mx-auto px-4 py-3 flex items-center justify-between">
                    <div className="flex items-center gap-3">
                        <img src={config.logo_url} alt="Logo" className="w-10 h-10 md:w-12 md:h-12 rounded-xl object-cover border-2 shadow-sm" style={{ borderColor: primaryColor }} />
                        <div>
                            <h1 className="font-extrabold text-base md:text-xl line-clamp-1">{config.name}</h1>
                            <p className="text-[10px] md:text-sm font-semibold flex items-center gap-1" style={{ color: textMuted }}>
                                <UtensilsCrossed className="w-3 h-3" />
                                {isAr ? 'القائمة الرقمية' : 'Digital Menu'}
                            </p>
                        </div>
                    </div>
                    <div className="flex items-center gap-2 md:gap-4">
                        <div className="flex items-center gap-1.5 md:gap-2 mr-2 md:mr-4 border-r pr-2 md:pr-4 border-gray-200 dark:border-slate-700">
                            {config.map_link ? (
                                <a href={config.map_link} target="_blank" rel="noreferrer" className="w-8 h-8 md:w-9 md:h-9 rounded-full flex items-center justify-center bg-gray-100 dark:bg-slate-800 text-red-500 hover:bg-red-50 dark:hover:bg-slate-700 transition-colors border border-gray-200 dark:border-slate-700">
                                    <MapIcon className="w-4 h-4 md:w-4.5 md:h-4.5" />
                                </a>
                            ) : (config.latitude && config.longitude && (
                                <a href={`https://www.google.com/maps/search/?api=1&query=${config.latitude},${config.longitude}`} target="_blank" rel="noreferrer" className="w-8 h-8 md:w-9 md:h-9 rounded-full flex items-center justify-center bg-gray-100 dark:bg-slate-800 text-red-500 hover:bg-red-50 dark:hover:bg-slate-700 transition-colors border border-gray-200 dark:border-slate-700">
                                    <MapIcon className="w-4 h-4 md:w-4.5 md:h-4.5" />
                                </a>
                            ))}

                            {(config.social_links?.facebook || config.facebook_url) && (
                                <a href={config.social_links?.facebook || config.facebook_url} target="_blank" rel="noreferrer" className="w-8 h-8 md:w-9 md:h-9 rounded-full flex items-center justify-center bg-gray-100 dark:bg-slate-800 text-[#1877f2] hover:bg-blue-50 dark:hover:bg-slate-700 transition-colors border border-gray-200 dark:border-slate-700">
                                    <FaFacebookF className="text-sm md:text-base" />
                                </a>
                            )}

                            {(config.social_links?.whatsapp || config.whatsapp_number) && (
                                <a href={`https://wa.me/${(config.social_links?.whatsapp || config.whatsapp_number).replace(/\+/g, '')}`} target="_blank" rel="noreferrer" className="w-8 h-8 md:w-9 md:h-9 rounded-full flex items-center justify-center bg-gray-100 dark:bg-slate-800 text-[#25D366] hover:bg-green-50 dark:hover:bg-slate-700 transition-colors border border-gray-200 dark:border-slate-700">
                                    <FaWhatsapp className="text-sm md:text-base md:text-lg" />
                                </a>
                            )}

                            {config.social_links?.snapchat && (
                                <a href={config.social_links.snapchat} target="_blank" rel="noreferrer" className="w-8 h-8 md:w-9 md:h-9 rounded-full flex items-center justify-center bg-gray-100 dark:bg-slate-800 text-[#fffc00] hover:bg-yellow-50 dark:hover:bg-slate-700 transition-colors border border-gray-200 dark:border-slate-700">
                                    <FaSnapchatGhost className="text-sm md:text-base" />
                                </a>
                            )}

                            {config.website_url && (
                                <a href={config.website_url} target="_blank" rel="noreferrer" className="w-8 h-8 md:w-9 md:h-9 rounded-full flex items-center justify-center bg-gray-100 dark:bg-slate-800 text-gray-600 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-slate-700 transition-colors border border-gray-200 dark:border-slate-700">
                                    <Globe className="w-4 h-4 md:w-4.5 md:h-4.5" />
                                </a>
                            )}

                            {(config.phone || (config.phones && config.phones.length > 0)) && (
                                <div className="relative">
                                    <button
                                        onClick={() => setIsPhoneMenuOpen(!isPhoneMenuOpen)}
                                        className="w-8 h-8 md:w-9 md:h-9 rounded-full flex items-center justify-center bg-gray-100 dark:bg-slate-800 text-blue-500 hover:bg-blue-50 dark:hover:bg-slate-700 transition-colors border border-gray-200 dark:border-slate-700"
                                    >
                                        <PhoneCall className="w-4 h-4 md:w-4.5 md:h-4.5" />
                                    </button>

                                    <AnimatePresence>
                                        {isPhoneMenuOpen && (
                                            <motion.div
                                                initial={{ opacity: 0, y: 10 }}
                                                animate={{ opacity: 1, y: 0 }}
                                                exit={{ opacity: 0, y: 10 }}
                                                className="absolute top-full mt-2 left-1/2 -translate-x-1/2 w-48 bg-white dark:bg-slate-800 rounded-xl shadow-2xl border border-gray-100 dark:border-slate-700 py-2 z-[110] overflow-hidden"
                                            >
                                                <div className="px-4 py-2 border-b border-gray-100 dark:border-slate-700 text-xs font-bold text-gray-500 uppercase text-center">
                                                    أرقام الديلفري
                                                </div>
                                                {config.phones && config.phones.length > 0 ? (
                                                    config.phones.map((phoneNum: string, idx: number) => (
                                                        <a
                                                            key={idx}
                                                            href={`tel:${phoneNum}`}
                                                            className="block px-4 py-2.5 text-center text-sm font-bold text-gray-800 dark:text-gray-200 hover:bg-gray-50 dark:hover:bg-slate-700 transition-colors"
                                                        >
                                                            {phoneNum}
                                                        </a>
                                                    ))
                                                ) : (
                                                    <a
                                                        href={`tel:${config.phone}`}
                                                        className="block px-4 py-2.5 text-center text-sm font-bold text-gray-800 dark:text-gray-200 hover:bg-gray-50 dark:hover:bg-slate-700 transition-colors"
                                                    >
                                                        {config.phone}
                                                    </a>
                                                )}
                                            </motion.div>
                                        )}
                                    </AnimatePresence>
                                </div>
                            )}
                        </div>
                        <button onClick={() => setTheme(isDark ? 'light' : 'dark')} className="w-9 h-9 md:w-10 md:h-10 flex flex-col items-center justify-center rounded-full transition-colors border" style={{ backgroundColor: isDark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.05)', borderColor }}>
                            {isDark ? <Sun className="w-5 h-5 text-yellow-400" /> : <Moon className="w-5 h-5 text-slate-700" />}
                        </button>
                        <button onClick={() => setIsCartOpen(true)} className="relative w-9 h-9 md:w-10 md:h-10 flex flex-col items-center justify-center rounded-full text-white transition-transform active:scale-95 shadow-md hover:shadow-lg" style={{ backgroundColor: primaryColor }}>
                            <ShoppingCart className="w-5 h-5" />
                            {cartCount > 0 && (
                                <span className="absolute -top-1 -right-1 w-5 h-5 rounded-full bg-white text-xs font-bold flex items-center justify-center border-2" style={{ color: primaryColor, borderColor: primaryColor }}>
                                    {cartCount}
                                </span>
                            )}
                        </button>
                    </div>
                </div>
            </div>

            <main className="max-w-[1200px] mx-auto px-4 w-full">

                {/* Hero Banner Container */}
                <div className="pt-6 relative z-10">
                    <div className="rounded-2xl md:rounded-3xl overflow-hidden shadow-lg border relative aspect-[21/9] sm:aspect-[21/7] max-h-[350px] w-full" style={{ borderColor }}>
                        {config.cover_images && config.cover_images.length > 1 ? (
                            <Swiper
                                modules={[Autoplay]}
                                spaceBetween={0}
                                slidesPerView={1}
                                loop={true}
                                autoplay={{ delay: 3000, disableOnInteraction: false }}
                                className="w-full h-full"
                            >
                                {config.cover_images.map((img, idx) => (
                                    <SwiperSlide key={idx} className="w-full h-full">
                                        <div className="w-full h-full relative">
                                            <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/20 to-transparent z-10" />
                                            <img
                                                src={img}
                                                alt={`Cover ${idx}`}
                                                className="w-full h-full object-cover select-none"
                                            />
                                        </div>
                                    </SwiperSlide>
                                ))}
                            </Swiper>
                        ) : (
                            <div className="w-full h-full relative">
                                <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/20 to-transparent z-10" />
                                <img src={config.cover_url || config.cover_images?.[0] || "https://images.unsplash.com/photo-1514933651103-005eec06c04b?q=80&w=1000"} alt="Cover" className="w-full h-full object-cover" />
                            </div>
                        )}

                        {/* Info Header overlaying banner */}
                        <div className="absolute z-20 bottom-3 md:bottom-6 left-4 right-4 flex justify-between items-end">
                            <div className="text-white">
                                <h1 className="text-2xl md:text-4xl font-black mb-1 md:mb-2 drop-shadow-md">{config.name}</h1>
                                {config.time_open && (
                                    <p className="text-sm md:text-base opacity-90 font-semibold drop-shadow-sm flex items-center gap-1.5">
                                        <div className="w-2 h-2 rounded-full bg-green-400 animate-pulse" />
                                        {isAr ? 'مفتوح الآن' : 'Open Now'} • {config.time_open} - {config.time_close}
                                    </p>
                                )}
                            </div>
                        </div>
                    </div>
                </div>

                {/* Category Navigation (Swiper Based) */}
                <div className="my-6 sticky top-[65px] z-40" style={{ backgroundColor: bgBody }}>
                    {/* Shadow overlay for sticky effect */}
                    <div className="absolute -inset-x-4 inset-y-0 shadow-[0_10px_15px_-3px_rgba(0,0,0,0.05)] pointer-events-none" />
                    <div className="relative z-10 py-3">
                        <Swiper
                            modules={[FreeMode]}
                            slidesPerView="auto"
                            spaceBetween={10}
                            freeMode={true}
                            dir={isAr ? 'rtl' : 'ltr'}
                            className="w-full pb-2 px-1"
                        >
                            <SwiperSlide style={{ width: 'auto' }}>
                                <button
                                    onClick={() => setActiveCategory('all')}
                                    className={`px-5 py-2.5 rounded-full font-bold text-sm whitespace-nowrap transition-all border-2 shadow-sm ${activeCategory === 'all' ? 'text-white' : ''}`}
                                    style={{
                                        backgroundColor: activeCategory === 'all' ? primaryColor : bgCard,
                                        borderColor: activeCategory === 'all' ? primaryColor : borderColor,
                                        color: activeCategory === 'all' ? '#fff' : textMain,
                                    }}
                                >
                                    {isAr ? 'الكل' : 'All'}
                                </button>
                            </SwiperSlide>
                            {categories.map((cat) => (
                                <SwiperSlide key={cat.id} style={{ width: 'auto' }}>
                                    <button
                                        onClick={() => setActiveCategory(cat.id.toString())}
                                        className={`px-5 py-2.5 rounded-full font-bold text-sm whitespace-nowrap transition-all border-2 shadow-sm flex items-center gap-2 ${activeCategory === cat.id.toString() ? 'text-white' : ''}`}
                                        style={{
                                            backgroundColor: activeCategory === cat.id.toString() ? primaryColor : bgCard,
                                            borderColor: activeCategory === cat.id.toString() ? primaryColor : borderColor,
                                            color: activeCategory === cat.id.toString() ? '#fff' : textMain,
                                        }}
                                    >
                                        {cat.image_url && (
                                            <img src={cat.image_url} alt={catName(cat)} className="w-5 h-5 rounded-full object-cover" />
                                        )}
                                        {catName(cat)}
                                    </button>
                                </SwiperSlide>
                            ))}
                        </Swiper>
                    </div>
                </div>

                {/* Menu Items */}
                <div className="space-y-10 min-h-[50vh]">
                    {activeCatList.map((cat) => (
                        <div key={cat.id} className="scroll-mt-32">
                            <h2 className="text-2xl md:text-3xl font-black mb-6 flex items-center gap-3">
                                <span className="w-2 h-8 rounded-full" style={{ backgroundColor: primaryColor }}></span>
                                {catName(cat)}
                            </h2>
                            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-5">
                                {cat.items?.map((item: MenuItem, idx: number) => (
                                    <div key={item.id || idx} onClick={() => openModal(item, catName(cat), cat.image_url)}
                                        className="rounded-2xl overflow-hidden border transition-all hover:shadow-md cursor-pointer group flex flex-row"
                                        style={{ backgroundColor: bgCard, borderColor }}>
                                        {/* Image Box */}
                                        <div className="w-[110px] sm:w-[130px] h-[130px] shrink-0 relative overflow-hidden bg-slate-100 dark:bg-slate-800">
                                            <img
                                                src={item.image_url || cat.image_url || "https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=400"}
                                                alt={itemName(item)}
                                                className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500"
                                            />
                                            {/* Add Button Badge overlay */}
                                            <div className="absolute right-2 bottom-2 w-8 h-8 rounded-full shadow-lg flex items-center justify-center text-white backdrop-blur-sm transition-transform active:scale-90" style={{ backgroundColor: primaryColor }}>
                                                <Plus className="w-4 h-4" />
                                            </div>
                                        </div>

                                        {/* Content Box */}
                                        <div className="flex-1 p-3 flex flex-col min-w-0">
                                            <h3 className="font-bold text-sm sm:text-base leading-snug line-clamp-2 mb-1">
                                                {itemName(item)}
                                            </h3>
                                            {(item.description_ar || item.description_en) && (
                                                <p className="text-xs sm:text-sm line-clamp-2 mb-2 leading-tight" style={{ color: textMuted }}>
                                                    {isAr ? item.description_ar : (item.description_en || item.description_ar)}
                                                </p>
                                            )}

                                            <div className="mt-auto pt-2 border-t flex flex-wrap items-end gap-x-2 gap-y-1" style={{ borderColor }}>
                                                {item.prices.map((price: number, pIdx: number) => (
                                                    <div key={pIdx} className="flex flex-col items-start bg-black/5 dark:bg-white/5 rounded-md px-2 py-1 flex-1 min-w-[60px]">
                                                        {item.size_labels?.[pIdx] && (
                                                            <span className="text-[10px] opacity-70 mb-0.5 whitespace-nowrap" style={{ color: textMuted }}>
                                                                {item.size_labels[pIdx]}
                                                            </span>
                                                        )}
                                                        <div className="font-w600 text-[13px] tracking-tighter whitespace-nowrap" style={{ color: primaryColor }}>
                                                            {cur}{price?.toFixed?.(0) || price}
                                                        </div>
                                                    </div>
                                                ))}
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    ))}

                    {activeCatList.length === 0 && (
                        <div className="text-center py-20 opacity-50 font-bold text-xl">
                            {isAr ? 'لا توجد منتجات' : 'No products found'}
                        </div>
                    )}
                </div>
            </main>



            {/* Modal */}
            <AnimatePresence>
                {selectedItem && (
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}
                        className="fixed inset-0 z-[200] flex items-end sm:items-center justify-center bg-black/60 backdrop-blur-sm sm:p-4"
                        onClick={closeModal}>
                        <motion.div initial={{ y: '100%' }} animate={{ y: 0 }} exit={{ y: '100%' }}
                            transition={{ type: 'spring', damping: 25, stiffness: 200 }}
                            className="relative w-full sm:max-w-[500px] h-[90vh] sm:h-auto sm:max-h-[90vh] rounded-t-3xl sm:rounded-[24px] overflow-hidden flex flex-col shadow-2xl"
                            style={{ backgroundColor: bgCard }}
                            onClick={e => e.stopPropagation()}>

                            {/* Mobile DRAG Handle */}
                            <div className="w-full flex justify-center pt-3 pb-1 sm:hidden absolute top-0 z-30" onClick={closeModal}>
                                <div className="w-12 h-1.5 rounded-full bg-white/50 backdrop-blur-md" />
                            </div>

                            {/* Close Button UI */}
                            <button onClick={closeModal} className="absolute top-4 left-4 z-20 w-8 h-8 rounded-full bg-white/20 backdrop-blur-md text-white flex items-center justify-center hover:bg-white/40 transition-colors">
                                <X className="w-5 h-5" />
                            </button>

                            {/* Modal Header Image */}
                            <div className="w-full h-[35vh] sm:h-[250px] shrink-0 relative bg-slate-100 dark:bg-slate-800">
                                <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent z-10" />
                                <img src={selectedItem.item.image_url || selectedItem.catImg || 'https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=600'}
                                    alt={itemName(selectedItem.item)} className="w-full h-full object-cover" />

                                <div className="absolute bottom-4 left-4 right-4 z-20" dir={isAr ? 'rtl' : 'ltr'}>
                                    <h2 className="text-2xl sm:text-3xl font-black text-white drop-shadow-md mb-1">{itemName(selectedItem.item)}</h2>
                                    <p className="text-white/90 text-sm sm:text-base font-bold drop-shadow-sm line-clamp-2">
                                        {isAr ? selectedItem.item.description_ar : (selectedItem.item.description_en || selectedItem.item.description_ar)}
                                    </p>
                                </div>
                            </div>

                            {/* Modal Content */}
                            <div className="flex-1 overflow-y-auto p-5 pb-24" style={{ color: textMain }}>

                                {/* Base Price */}
                                <div className="flex justify-between items-center bg-slate-50 dark:bg-slate-900 border p-4 rounded-xl mb-6" style={{ borderColor }}>
                                    <span className="font-bold">{isAr ? 'السعر' : 'Price'}</span>
                                    <span className="text-xl font-black" style={{ color: primaryColor }}>{cur}{selectedItem.item.prices[sizeIdx]?.toFixed?.(0)}</span>
                                </div>

                                {/* Sizes */}
                                {selectedItem.item.prices.length > 1 && (
                                    <div className="mb-6">
                                        <h3 className="font-bold mb-3 flex items-center gap-2">
                                            <List className="w-4 h-4 text-slate-400" />
                                            {isAr ? 'اختر الحجم' : 'Select Size'}
                                        </h3>
                                        <div className="grid grid-cols-2 gap-3">
                                            {selectedItem.item.prices.map((p, idx) => {
                                                const label = selectedItem.item.size_labels?.[idx] || (isAr ? `حجم ${idx + 1}` : `Size ${idx + 1}`);
                                                return (
                                                    <label key={idx} className="flex flex-col items-center justify-center p-3 rounded-xl border-2 cursor-pointer transition-all text-center"
                                                        style={{ borderColor: sizeIdx === idx ? primaryColor : borderColor, backgroundColor: sizeIdx === idx ? `${primaryColor}15` : 'transparent' }}
                                                        onClick={() => setSizeIdx(idx)}>
                                                        <span className="font-bold text-sm mb-1">{label}</span>
                                                        <span className="text-xs font-semibold">{cur}{p}</span>
                                                        <input type="radio" checked={sizeIdx === idx} readOnly className="hidden" />
                                                    </label>
                                                );
                                            })}
                                        </div>
                                    </div>
                                )}

                                {/* Extras */}
                                {selectedItem.item.extras && selectedItem.item.extras.length > 0 && (
                                    <div className="mb-6">
                                        <h3 className="font-bold mb-3 flex items-center gap-2">
                                            <Plus className="w-4 h-4 text-slate-400" />
                                            {isAr ? 'إضافات (اختياري)' : 'Extras (Optional)'}
                                        </h3>
                                        <div className="space-y-3">
                                            {selectedItem.item.extras.map((ext, idx) => {
                                                const id = ext.id || idx;
                                                const isSel = selectedExtras.some(e => e.id === id);
                                                return (
                                                    <label key={idx} className="flex items-center justify-between p-3 rounded-xl border border-dashed cursor-pointer transition-colors"
                                                        style={{ borderColor: isSel ? primaryColor : borderColor, backgroundColor: isSel ? `${primaryColor}05` : 'transparent' }}>
                                                        <input type="checkbox" className="hidden" checked={isSel}
                                                            onChange={() => {
                                                                if (isSel) setSelectedExtras(p => p.filter(e => e.id !== id));
                                                                else setSelectedExtras(p => [...p, { id, name: isAr ? ext.name_ar : (ext.name_en || ext.name_ar), price: ext.price }]);
                                                            }} />
                                                        <div className="flex items-center gap-3">
                                                            <div className="w-5 h-5 rounded border flex items-center justify-center transition-colors"
                                                                style={{ borderColor: isSel ? primaryColor : borderColor, backgroundColor: isSel ? primaryColor : 'transparent' }}>
                                                                {isSel && <X className="w-3 h-3 text-white" style={{ transform: 'rotate(45deg)' }} />}
                                                            </div>
                                                            <span className="text-sm font-semibold">{isAr ? ext.name_ar : (ext.name_en || ext.name_ar)}</span>
                                                        </div>
                                                        <span className="text-sm font-bold" style={{ color: primaryColor }}>{ext.price > 0 ? `+${cur}${ext.price}` : (isAr ? 'مجاناً' : 'Free')}</span>
                                                    </label>
                                                );
                                            })}
                                        </div>
                                    </div>
                                )}

                                {/* Notes */}
                                <div className="mb-6">
                                    <h3 className="font-bold mb-3 flex items-center gap-2">
                                        <FileText className="w-4 h-4 text-slate-400" />
                                        {isAr ? 'ملاحظات' : 'Notes'}
                                    </h3>
                                    <textarea value={notes} onChange={e => setNotes(e.target.value)}
                                        placeholder={isAr ? 'أضف ملاحظاتك الخاصة هنا...' : 'Special instructions...'}
                                        className="w-full rounded-xl p-3 outline-none resize-none h-24 text-sm border focus:ring-1 transition-all"
                                        style={{ backgroundColor: bgBody, color: textMain, borderColor, '--tw-ring-color': primaryColor } as React.CSSProperties} />
                                </div>
                            </div>

                            {/* Sticky Bottom Actions */}
                            <div className="absolute bottom-0 w-full p-4 border-t shadow-[0_-10px_20px_rgba(0,0,0,0.05)]" style={{ backgroundColor: bgCard, borderColor }}>
                                <div className="flex gap-4 items-center">
                                    <div className="flex items-center bg-slate-100 dark:bg-slate-800 rounded-full h-12 px-2 border" style={{ borderColor }}>
                                        <button onClick={() => setQty(Math.max(1, qty - 1))} className="w-8 h-8 rounded-full flex flex-col items-center justify-center text-slate-500 hover:bg-slate-200 dark:hover:bg-slate-700">
                                            <Minus className="w-4 h-4" />
                                        </button>
                                        <span className="w-8 text-center font-bold text-lg">{qty}</span>
                                        <button onClick={() => setQty(qty + 1)} className="w-8 h-8 rounded-full flex flex-col items-center justify-center text-slate-500 hover:bg-slate-200 dark:hover:bg-slate-700">
                                            <Plus className="w-4 h-4" />
                                        </button>
                                    </div>
                                    <button onClick={addToCart} className="flex-1 h-12 rounded-full text-white font-bold text-sm sm:text-base flex items-center justify-center gap-2 transition-transform active:scale-95 shadow-md hover:shadow-lg"
                                        style={{ backgroundColor: primaryColor }}>
                                        <ShoppingCart className="w-5 h-5" />
                                        {isAr ? 'إضافة إلى السلة' : 'Add to Cart'} — {cur}{(((selectedItem.item.prices[sizeIdx] || 0) + selectedExtras.reduce((s, e) => s + e.price, 0)) * qty).toFixed?.(0)}
                                    </button>
                                </div>
                            </div>
                        </motion.div>
                    </motion.div>
                )}
            </AnimatePresence>

            {/* Cart Drawer */}
            <AnimatePresence>
                {isCartOpen && (
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}
                        className="fixed inset-0 z-[300] bg-black/50 backdrop-blur-sm"
                        onClick={() => setIsCartOpen(false)}>
                        <motion.div initial={{ x: isAr ? '100%' : '100%' }} animate={{ x: 0 }} exit={{ x: isAr ? '100%' : '100%' }}
                            transition={{ type: 'spring', damping: 25, stiffness: 200 }}
                            className={`absolute top-0 ${isAr ? 'right-0' : 'right-0'} w-full max-w-[400px] h-full flex flex-col shadow-2xl`}
                            style={{ backgroundColor: bgCard }}
                            onClick={e => e.stopPropagation()}>

                            {/* Cart Header */}
                            <div className="p-5 flex justify-between items-center text-white shadow-md z-10 sticky top-0" style={{ backgroundColor: primaryColor }}>
                                <div className="flex flex-col">
                                    <span className="font-bold text-lg leading-none mb-1">{isAr ? 'عربة التسوق' : 'Cart'}</span>
                                    <span className="text-xs opacity-90">{cartCount} {isAr ? 'عناصر' : 'items'}</span>
                                </div>
                                <button onClick={() => setIsCartOpen(false)} className="w-8 h-8 flex flex-col items-center justify-center bg-white/20 hover:bg-white/30 rounded-full transition-colors"><X className="w-5 h-5" /></button>
                            </div>

                            {/* Cart Body */}
                            <div className="flex-1 overflow-y-auto" style={{ backgroundColor: bgBody }}>
                                {cart.length === 0 ? (
                                    <div className="flex flex-col items-center justify-center h-[50vh] text-center px-4" style={{ color: textMuted }}>
                                        <div className="w-24 h-24 mb-4 rounded-full bg-slate-200 dark:bg-slate-800 flex items-center justify-center">
                                            <ShoppingCart className="w-10 h-10 opacity-40" />
                                        </div>
                                        <p className="font-bold text-lg mb-2">{isAr ? 'سلتك فارغة' : 'Your cart is empty'}</p>
                                        <p className="text-sm opacity-80">{isAr ? 'أضف بعض المنتجات الشهية للبدء' : 'Add some delicious items to start'}</p>
                                    </div>
                                ) : (
                                    <div className="p-4 space-y-4">
                                        {cart.map((c, i) => (
                                            <div key={i} className="flex gap-4 p-4 rounded-2xl bg-white dark:bg-slate-800 shadow-sm border" style={{ borderColor }}>
                                                <div className="w-20 h-20 shrink-0 bg-slate-100 rounded-xl overflow-hidden">
                                                    <img src={c.item.image_url || 'https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=200'} alt="" className="w-full h-full object-cover" />
                                                </div>
                                                <div className="flex-1 min-w-0 flex flex-col justify-between" dir={isAr ? 'rtl' : 'ltr'}>
                                                    <div className="flex justify-between items-start">
                                                        <div>
                                                            <h4 className="font-bold text-sm line-clamp-1">{itemName(c.item)}</h4>
                                                            {(c.sizeLabel || c.notes) && (
                                                                <p className="text-[11px] mt-1 space-y-0.5" style={{ color: textMuted }}>
                                                                    {c.sizeLabel && <span className="block">{c.sizeLabel}</span>}
                                                                    {c.notes && <span className="block italic flex gap-1"><AlertCircle className="w-3 h-3 inline" />{c.notes}</span>}
                                                                </p>
                                                            )}
                                                        </div>
                                                        <button onClick={() => updateQty(c.id, c.notes, -c.quantity)} className="text-slate-300 hover:text-red-500 transition-colors p-1">
                                                            <Trash2 className="w-4 h-4" />
                                                        </button>
                                                    </div>

                                                    <div className="flex justify-between items-center mt-3">
                                                        <span className="font-black" style={{ color: primaryColor }}>{cur}{(c.price * c.quantity).toFixed?.(0)}</span>
                                                        <div className="flex items-center bg-slate-100 dark:bg-slate-900 rounded-full h-8 px-1 border" style={{ borderColor }}>
                                                            <button onClick={() => updateQty(c.id, c.notes, -1)} className="w-6 h-6 flex items-center justify-center text-slate-500 hover:text-primary-600"><Minus className="w-3 h-3" /></button>
                                                            <span className="w-6 text-center text-sm font-bold">{c.quantity}</span>
                                                            <button onClick={() => updateQty(c.id, c.notes, 1)} className="w-6 h-6 flex items-center justify-center text-slate-500 hover:text-primary-600"><Plus className="w-3 h-3" /></button>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        ))}

                                        {/* Delivery Info */}
                                        <div className="mt-6 bg-white dark:bg-slate-800 p-5 rounded-2xl border shadow-sm" style={{ borderColor }}>
                                            <h4 className="font-bold text-sm mb-4 flex items-center gap-2" style={{ color: textMain }}>
                                                <MapIcon className="w-4 h-4" style={{ color: primaryColor }} />
                                                {isAr ? 'بيانات التوصيل' : 'Delivery Details'}
                                            </h4>
                                            <div className="space-y-3 flex flex-col items-end">
                                                <input type="text" placeholder={isAr ? "الاسم" : "Name"} value={customerInfo.name} onChange={e => setCustomerInfo({ ...customerInfo, name: e.target.value })}
                                                    className="w-full border rounded-xl p-3 text-sm outline-none focus:ring-1" style={{ backgroundColor: bgBody, color: textMain, borderColor, '--tw-ring-color': primaryColor } as React.CSSProperties} dir={isAr ? "rtl" : "ltr"} />
                                                <input type="tel" placeholder={isAr ? "رقم الجوال" : "Phone number"} value={customerInfo.phone} onChange={e => setCustomerInfo({ ...customerInfo, phone: e.target.value })}
                                                    className="w-full border rounded-xl p-3 text-sm outline-none focus:ring-1 text-left" style={{ backgroundColor: bgBody, color: textMain, borderColor, '--tw-ring-color': primaryColor } as React.CSSProperties} dir="ltr" />
                                                <textarea placeholder={isAr ? "العنوان بالتفصيل" : "Full Address"} value={customerInfo.address} onChange={e => setCustomerInfo({ ...customerInfo, address: e.target.value })}
                                                    className="w-full border rounded-xl p-3 text-sm outline-none focus:ring-1 h-20 resize-none" style={{ backgroundColor: bgBody, color: textMain, borderColor, '--tw-ring-color': primaryColor } as React.CSSProperties} dir={isAr ? "rtl" : "ltr"} />
                                            </div>
                                        </div>
                                    </div>
                                )}
                            </div>

                            {/* Cart Footer */}
                            {cart.length > 0 && (
                                <div className="p-5 shadow-[0_-5px_15px_rgba(0,0,0,0.05)] border-t z-20" style={{ backgroundColor: bgCard, borderColor }}>
                                    <div className="flex justify-between items-center mb-4 text-sm font-bold" style={{ color: textMain }}>
                                        <span>{isAr ? 'الإجمالي' : 'Total'}</span>
                                        <span className="text-xl font-black" style={{ color: primaryColor }}>{cur}{cartTotal.toFixed?.(0)}</span>
                                    </div>
                                    <button onClick={checkOutWhatsApp} className="w-full flex justify-center items-center gap-2 h-14 rounded-full text-white font-bold text-lg transition-transform active:scale-95 shadow-lg group hover:bg-[#20b858]"
                                        style={{ backgroundColor: '#25d366' }}>
                                        <FaWhatsapp className="w-6 h-6 group-hover:scale-110 transition-transform" />
                                        {isAr ? 'تأكيد الطلب' : 'Confirm Order'}
                                    </button>
                                    <p className="text-center text-[11px] mt-3 opacity-60 font-semibold">{isAr ? 'سيتم تحويلك إلى واتساب لإتمام الطلب' : 'You will be redirected to WhatsApp to complete order'}</p>
                                </div>
                            )}
                        </motion.div>
                    </motion.div>
                )}
            </AnimatePresence>
        </div>
    );
}

'use client';

import React, { useState, useEffect } from 'react';
import { useTheme } from 'next-themes';
import { motion, AnimatePresence } from 'framer-motion';
import { UtensilsCrossed, AlertCircle, Moon, Sun, ShoppingCart, Plus, Minus, Trash2, X, FileText, MapPin as MapIcon, List, Globe, PhoneCall } from 'lucide-react';
import { FaWhatsapp, FaFacebookF, FaSnapchatGhost } from 'react-icons/fa';
import SharedMarquee from './SharedMarquee';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, FreeMode } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/free-mode';

type MenuItem = {
    id: string | number;
    title_ar: string;
    title_en?: string;
    description_ar?: string;
    description_en?: string;
    image?: string;
    image_url?: string;
    prices: number[];
    size_labels?: string[];
    extras?: { id?: number | string; name_ar: string; name_en?: string; price: number }[];
    is_available?: boolean;
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    [key: string]: any;
};

// ================= THEME 11 CONSTANTS =================
const T11_PRIMARY = '#e54750'; // Luxe red

interface CategoryWithItemsType {
    id: string | number;
    name_ar: string;
    name_en?: string;
    items?: MenuItem[];
    image_url?: string;
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    [key: string]: any;
}

interface RestaurantType {
    name: string;
    theme?: string;
    theme_colors?: {
        main_color?: string;
        bg_color?: string;
        text_color?: string;
    };
    cover_images?: string[];
    marquee_enabled?: boolean;
    marquee_text_ar?: string;
    marquee_text_en?: string;
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    [key: string]: any;
}

interface Theme11MenuProps {
    config: RestaurantType;
    categories: CategoryWithItemsType[];
}

export default function Theme11Menu({ config, categories }: Theme11MenuProps) {
    const { theme, setTheme } = useTheme();
    const [mounted, setMounted] = useState(false);
    const [isPhoneMenuOpen, setIsPhoneMenuOpen] = useState(false);
    useEffect(() => setMounted(true), []);

    const isAr = config.default_language === 'ar' || true; // Defaulting to true for now since design is RTL primary
    const isDark = mounted && theme === 'dark';
    const cur = '';

    // Theme Variables - matching HTML
    const bgBody = isDark ? '#0f172a' : '#f8fafc'; // slate-900 / slate-50
    const bgCard = isDark ? '#1e293b' : '#ffffff'; // slate-800 / white
    const textMain = isDark ? '#f1f5f9' : '#0f172a'; // slate-100 / slate-900
    const textMuted = isDark ? '#94a3b8' : '#64748b'; // slate-400 / slate-500
    const borderColor = isDark ? '#334155' : '#e2e8f0'; // slate-700 / slate-200
    const primaryColor = config.theme_colors?.main_color || T11_PRIMARY;

    // State
    const [activeCategory, setActiveCategory] = useState<string>('all');

    // Drawers & Modals
    const [isCartOpen, setIsCartOpen] = useState(false);
    const [selectedItem, setSelectedItem] = useState<{ item: MenuItem; catName: string; catImg?: string } | null>(null);

    // Item Detail Modal
    const [qty, setQty] = useState(1);
    const [sizeIdx, setSizeIdx] = useState(0);
    const [notes, setNotes] = useState('');
    const [selectedExtras, setSelectedExtras] = useState<{ id: number | string, name: string, price: number }[]>([]);

    // Checkout
    const [customerInfo, setCustomerInfo] = useState({ name: '', phone: '', address: '' });
    const [cart, setCart] = useState<{ id: string, item: MenuItem, catName: string, price: number, sizeLabel: string, quantity: number, notes: string }[]>([]);

    const itemName = (item: MenuItem) => isAr ? item.title_ar : (item.title_en || item.title_ar);
    const catName = (cat: CategoryWithItemsType) => isAr ? cat.name_ar : (cat.name_en || cat.name_ar);

    // Derived cart values
    const cartCount = cart.reduce((acc, curr) => acc + curr.quantity, 0);
    const cartTotal = cart.reduce((acc, curr) => acc + (curr.price * curr.quantity), 0);

    // Modals
    const openModal = (item: MenuItem, cName: string, cImg?: string) => {
        setSelectedItem({ item, catName: cName, catImg: cImg });
        setQty(1);
        setSizeIdx(0);
        setSelectedExtras([]);
        setNotes('');
        document.body.style.overflow = 'hidden';
    };

    const closeModal = () => {
        setSelectedItem(null);
        document.body.style.overflow = 'auto';
    };

    // Cart Handlers
    const addToCart = () => {
        if (!selectedItem) return;
        const itemP = selectedItem.item.prices[sizeIdx] || 0;
        const extP = selectedExtras.reduce((sum, e) => sum + e.price, 0);
        const finalPrice = itemP + extP;

        let sizeLbl = selectedItem.item.size_labels?.[sizeIdx] || '';
        if (selectedExtras.length > 0) {
            const extNames = selectedExtras.map(e => e.name).join(' + ');
            sizeLbl = sizeLbl ? `${sizeLbl} | +${extNames}` : `+${extNames}`;
        }

        const cId = `${selectedItem.item.id}-${sizeIdx}-${selectedExtras.map(e => e.id).sort().join('-')}`;

        setCart(prev => {
            const ex = prev.find(c => c.id === cId && c.notes === notes);
            if (ex) return prev.map(c => c.id === cId && c.notes === notes ? { ...c, quantity: c.quantity + qty } : c);
            return [...prev, {
                id: cId,
                item: selectedItem.item,
                catName: selectedItem.catName,
                price: finalPrice,
                sizeLabel: sizeLbl,
                quantity: qty,
                notes
            }];
        });
        closeModal();
        setIsCartOpen(true);
    };

    const updateQty = (id: string, itemNotes: string, delta: number) => {
        setCart(prev => prev.map(c => {
            if (c.id === id && c.notes === itemNotes) {
                const n = c.quantity + delta;
                return n > 0 ? { ...c, quantity: n } : c;
            }
            return c;
        }).filter(c => c.quantity > 0));
    };

    const checkOutWhatsApp = () => {
        if (!config.phone) {
            alert(isAr ? 'رقم المطعم غير متوفر' : 'Restaurant phone unavailable');
            return;
        }
        if (!customerInfo.name || !customerInfo.phone || !customerInfo.address) {
            alert(isAr ? 'يرجى إكمال بيانات التوصيل' : 'Please complete delivery details');
            return;
        }

        let txt = isAr ? `*طلب جديد من ${customerInfo.name}*%0A` : `*New Order from ${customerInfo.name}*%0A`;
        txt += isAr ? `رقم الهاتف: ${customerInfo.phone}%0A` : `Phone: ${customerInfo.phone}%0A`;
        txt += isAr ? `العنوان: ${customerInfo.address}%0A%0A` : `Address: ${customerInfo.address}%0A%0A`;

        cart.forEach(c => {
            txt += `▪ ${itemName(c.item)} (${c.quantity}x)\n`;
            if (c.sizeLabel) txt += `   ${c.sizeLabel}\n`;
            if (c.notes) txt += `   📝 ${c.notes}\n`;
            txt += `   ${cur}${(c.price * c.quantity).toFixed?.(0)}\n\n`;
        });

        txt += isAr ? `*الإجمالي: ${cur}${cartTotal.toFixed?.(0)}*` : `*Total: ${cur}${cartTotal.toFixed?.(0)}*`;

        const tel = config.phone.replace(/\D/g, '');
        window.open(`https://wa.me/${tel}?text=${encodeURIComponent(txt)}`, '_blank');
    };

    // Filter Logic
    const activeCatList = activeCategory === 'all'
        ? categories
        : categories.filter(c => c.id === activeCategory);

    if (!mounted) return <div className="min-h-screen bg-slate-50 dark:bg-slate-900" />;

    return (
        <div className={`min-h-screen font-poppins pb-24`} style={{ backgroundColor: bgBody, color: textMain }} dir={isAr ? 'rtl' : 'ltr'}>

            {/* Header Marquee */}
            {config.marquee_enabled && (
                <div className="font-poppins text-sm text-white" style={{ backgroundColor: primaryColor }}>
                    <SharedMarquee
                        text={isAr ? (config.marquee_text_ar || '') : (config.marquee_text_en || config.marquee_text_ar || '')}
                    />
                </div>
            )}

            {/* Top Navigation Bar */}
            <div className="sticky top-0 z-[100] shadow-sm backdrop-blur-md border-b transition-colors duration-300" style={{ backgroundColor: isDark ? 'rgba(30, 41, 59, 0.85)' : 'rgba(255, 255, 255, 0.85)', borderColor }}>
                <div className="max-w-[1200px] mx-auto px-4 py-3 flex items-center justify-between">
                    <div className="flex items-center gap-3">
                        <img src={config.logo_url} alt="Logo" className="w-10 h-10 md:w-12 md:h-12 rounded-xl object-cover border-2 shadow-sm" style={{ borderColor: primaryColor }} />
                        <div>
                            <h1 className="font-extrabold text-base md:text-xl line-clamp-1">{config.name}</h1>
                            <p className="text-[10px] md:text-sm font-semibold flex items-center gap-1" style={{ color: textMuted }}>
                                <UtensilsCrossed className="w-3 h-3" />
                                {isAr ? 'القائمة الرقمية' : 'Digital Menu'}
                            </p>
                        </div>
                    </div>
                    <div className="flex items-center gap-2 md:gap-4">
                        <div className="flex items-center gap-1.5 md:gap-2 mr-2 md:mr-4 border-r pr-2 md:pr-4 border-gray-200 dark:border-slate-700">
                            {config.map_link ? (
                                <a href={config.map_link} target="_blank" rel="noreferrer" className="w-8 h-8 md:w-9 md:h-9 rounded-full flex items-center justify-center bg-gray-100 dark:bg-slate-800 text-red-500 hover:bg-red-50 dark:hover:bg-slate-700 transition-colors border border-gray-200 dark:border-slate-700">
                                    <MapIcon className="w-4 h-4 md:w-4.5 md:h-4.5" />
                                </a>
                            ) : (config.latitude && config.longitude && (
                                <a href={`https://www.google.com/maps/search/?api=1&query=${config.latitude},${config.longitude}`} target="_blank" rel="noreferrer" className="w-8 h-8 md:w-9 md:h-9 rounded-full flex items-center justify-center bg-gray-100 dark:bg-slate-800 text-red-500 hover:bg-red-50 dark:hover:bg-slate-700 transition-colors border border-gray-200 dark:border-slate-700">
                                    <MapIcon className="w-4 h-4 md:w-4.5 md:h-4.5" />
                                </a>
                            ))}

                            {(config.social_links?.facebook || config.facebook_url) && (
                                <a href={config.social_links?.facebook || config.facebook_url} target="_blank" rel="noreferrer" className="w-8 h-8 md:w-9 md:h-9 rounded-full flex items-center justify-center bg-gray-100 dark:bg-slate-800 text-[#1877f2] hover:bg-blue-50 dark:hover:bg-slate-700 transition-colors border border-gray-200 dark:border-slate-700">
                                    <FaFacebookF className="text-sm md:text-base" />
                                </a>
                            )}

                            {(config.social_links?.whatsapp || config.whatsapp_number) && (
                                <a href={`https://wa.me/${(config.social_links?.whatsapp || config.whatsapp_number).replace(/\+/g, '')}`} target="_blank" rel="noreferrer" className="w-8 h-8 md:w-9 md:h-9 rounded-full flex items-center justify-center bg-gray-100 dark:bg-slate-800 text-[#25D366] hover:bg-green-50 dark:hover:bg-slate-700 transition-colors border border-gray-200 dark:border-slate-700">
                                    <FaWhatsapp className="text-sm md:text-base md:text-lg" />
                                </a>
                            )}

                            {config.social_links?.snapchat && (
                                <a href={config.social_links.snapchat} target="_blank" rel="noreferrer" className="w-8 h-8 md:w-9 md:h-9 rounded-full flex items-center justify-center bg-gray-100 dark:bg-slate-800 text-[#fffc00] hover:bg-yellow-50 dark:hover:bg-slate-700 transition-colors border border-gray-200 dark:border-slate-700">
                                    <FaSnapchatGhost className="text-sm md:text-base" />
                                </a>
                            )}

                            {config.website_url && (
                                <a href={config.website_url} target="_blank" rel="noreferrer" className="w-8 h-8 md:w-9 md:h-9 rounded-full flex items-center justify-center bg-gray-100 dark:bg-slate-800 text-gray-600 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-slate-700 transition-colors border border-gray-200 dark:border-slate-700">
                                    <Globe className="w-4 h-4 md:w-4.5 md:h-4.5" />
                                </a>
                            )}

                            {(config.phone || (config.phones && config.phones.length > 0)) && (
                                <div className="relative">
                                    <button
                                        onClick={() => setIsPhoneMenuOpen(!isPhoneMenuOpen)}
                                        className="w-8 h-8 md:w-9 md:h-9 rounded-full flex items-center justify-center bg-gray-100 dark:bg-slate-800 text-blue-500 hover:bg-blue-50 dark:hover:bg-slate-700 transition-colors border border-gray-200 dark:border-slate-700"
                                    >
                                        <PhoneCall className="w-4 h-4 md:w-4.5 md:h-4.5" />
                                    </button>

                                    <AnimatePresence>
                                        {isPhoneMenuOpen && (
                                            <motion.div
                                                initial={{ opacity: 0, y: 10 }}
                                                animate={{ opacity: 1, y: 0 }}
                                                exit={{ opacity: 0, y: 10 }}
                                                className="absolute top-full mt-2 left-1/2 -translate-x-1/2 w-48 bg-white dark:bg-slate-800 rounded-xl shadow-2xl border border-gray-100 dark:border-slate-700 py-2 z-[110] overflow-hidden"
                                            >
                                                <div className="px-4 py-2 border-b border-gray-100 dark:border-slate-700 text-xs font-bold text-gray-500 uppercase text-center">
                                                    أرقام الديلفري
                                                </div>
                                                {config.phones && config.phones.length > 0 ? (
                                                    config.phones.map((phoneNum: string, idx: number) => (
                                                        <a
                                                            key={idx}
                                                            href={`tel:${phoneNum}`}
                                                            className="block px-4 py-2.5 text-center text-sm font-bold text-gray-800 dark:text-gray-200 hover:bg-gray-50 dark:hover:bg-slate-700 transition-colors"
                                                        >
                                                            {phoneNum}
                                                        </a>
                                                    ))
                                                ) : (
                                                    <a
                                                        href={`tel:${config.phone}`}
                                                        className="block px-4 py-2.5 text-center text-sm font-bold text-gray-800 dark:text-gray-200 hover:bg-gray-50 dark:hover:bg-slate-700 transition-colors"
                                                    >
                                                        {config.phone}
                                                    </a>
                                                )}
                                            </motion.div>
                                        )}
                                    </AnimatePresence>
                                </div>
                            )}
                        </div>
                        <button onClick={() => setTheme(isDark ? 'light' : 'dark')} className="w-9 h-9 md:w-10 md:h-10 flex flex-col items-center justify-center rounded-full transition-colors border" style={{ backgroundColor: isDark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.05)', borderColor }}>
                            {isDark ? <Sun className="w-5 h-5 text-yellow-400" /> : <Moon className="w-5 h-5 text-slate-700" />}
                        </button>
                        <button onClick={() => setIsCartOpen(true)} className="relative w-9 h-9 md:w-10 md:h-10 flex flex-col items-center justify-center rounded-full text-white transition-transform active:scale-95 shadow-md hover:shadow-lg" style={{ backgroundColor: primaryColor }}>
                            <ShoppingCart className="w-5 h-5" />
                            {cartCount > 0 && (
                                <span className="absolute -top-1 -right-1 w-5 h-5 rounded-full bg-white text-xs font-bold flex items-center justify-center border-2" style={{ color: primaryColor, borderColor: primaryColor }}>
                                    {cartCount}
                                </span>
                            )}
                        </button>
                    </div>
                </div>
            </div>

            <main className="max-w-[1200px] mx-auto px-4 w-full">

                {/* Hero Banner Container */}
                <div className="pt-6 relative z-10">
                    <div className="rounded-2xl md:rounded-3xl overflow-hidden shadow-lg border relative aspect-[21/9] sm:aspect-[21/7] max-h-[350px] w-full" style={{ borderColor }}>
                        {config.cover_images && config.cover_images.length > 1 ? (
                            <Swiper
                                modules={[Autoplay]}
                                spaceBetween={0}
                                slidesPerView={1}
                                loop={true}
                                autoplay={{ delay: 3000, disableOnInteraction: false }}
                                className="w-full h-full"
                            >
                                {config.cover_images.map((img, idx) => (
                                    <SwiperSlide key={idx} className="w-full h-full">
                                        <div className="w-full h-full relative">
                                            <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/20 to-transparent z-10" />
                                            <img
                                                src={img}
                                                alt={`Cover ${idx}`}
                                                className="w-full h-full object-cover select-none"
                                            />
                                        </div>
                                    </SwiperSlide>
                                ))}
                            </Swiper>
                        ) : (
                            <div className="w-full h-full relative">
                                <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/20 to-transparent z-10" />
                                <img src={config.cover_url || config.cover_images?.[0] || "https://images.unsplash.com/photo-1514933651103-005eec06c04b?q=80&w=1000"} alt="Cover" className="w-full h-full object-cover" />
                            </div>
                        )}

                        {/* Info Header overlaying banner */}
                        <div className="absolute z-20 bottom-3 md:bottom-6 left-4 right-4 flex justify-between items-end">
                            <div className="text-white">
                                <h1 className="text-2xl md:text-4xl font-black mb-1 md:mb-2 drop-shadow-md">{config.name}</h1>
                                {config.time_open && (
                                    <p className="text-sm md:text-base opacity-90 font-semibold drop-shadow-sm flex items-center gap-1.5">
                                        <div className="w-2 h-2 rounded-full bg-green-400 animate-pulse" />
                                        {isAr ? 'مفتوح الآن' : 'Open Now'} • {config.time_open} - {config.time_close}
                                    </p>
                                )}
                            </div>
                        </div>
                    </div>
                </div>

                {/* Category Navigation (Swiper Based) */}
                <div className="my-6 sticky top-[65px] z-40" style={{ backgroundColor: bgBody }}>
                    {/* Shadow overlay for sticky effect */}
                    <div className="absolute -inset-x-4 inset-y-0 shadow-[0_10px_15px_-3px_rgba(0,0,0,0.05)] pointer-events-none" />
                    <div className="relative z-10 py-3">
                        <Swiper
                            modules={[FreeMode]}
                            slidesPerView="auto"
                            spaceBetween={10}
                            freeMode={true}
                            dir={isAr ? 'rtl' : 'ltr'}
                            className="w-full pb-2 px-1"
                        >
                            <SwiperSlide style={{ width: 'auto' }}>
                                <button
                                    onClick={() => setActiveCategory('all')}
                                    className={`px-5 py-2.5 rounded-full font-bold text-sm whitespace-nowrap transition-all border-2 shadow-sm ${activeCategory === 'all' ? 'text-white' : ''}`}
                                    style={{
                                        backgroundColor: activeCategory === 'all' ? primaryColor : bgCard,
                                        borderColor: activeCategory === 'all' ? primaryColor : borderColor,
                                        color: activeCategory === 'all' ? '#fff' : textMain,
                                    }}
                                >
                                    {isAr ? 'الكل' : 'All'}
                                </button>
                            </SwiperSlide>
                            {categories.map((cat) => (
                                <SwiperSlide key={cat.id} style={{ width: 'auto' }}>
                                    <button
                                        onClick={() => setActiveCategory(cat.id.toString())}
                                        className={`px-5 py-2.5 rounded-full font-bold text-sm whitespace-nowrap transition-all border-2 shadow-sm flex items-center gap-2 ${activeCategory === cat.id.toString() ? 'text-white' : ''}`}
                                        style={{
                                            backgroundColor: activeCategory === cat.id.toString() ? primaryColor : bgCard,
                                            borderColor: activeCategory === cat.id.toString() ? primaryColor : borderColor,
                                            color: activeCategory === cat.id.toString() ? '#fff' : textMain,
                                        }}
                                    >
                                        {cat.image_url && (
                                            <img src={cat.image_url} alt={catName(cat)} className="w-5 h-5 rounded-full object-cover" />
                                        )}
                                        {catName(cat)}
                                    </button>
                                </SwiperSlide>
                            ))}
                        </Swiper>
                    </div>
                </div>

                {/* Menu Items */}
                <div className="space-y-10 min-h-[50vh]">
                    {activeCatList.map((cat) => (
                        <div key={cat.id} className="scroll-mt-32">
                            <h2 className="text-2xl md:text-3xl font-black mb-6 flex items-center gap-3">
                                <span className="w-2 h-8 rounded-full" style={{ backgroundColor: primaryColor }}></span>
                                {catName(cat)}
                            </h2>
                            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-5">
                                {cat.items?.map((item: MenuItem, idx: number) => (
                                    <div key={item.id || idx} onClick={() => openModal(item, catName(cat), cat.image_url)}
                                        className="rounded-2xl overflow-hidden border transition-all hover:shadow-md cursor-pointer group flex flex-row p-3 gap-3"
                                        style={{ backgroundColor: bgCard, borderColor }}>

                                        {/* Content Box (Left Side in LTR, Right Side in RTL) */}
                                        <div className="flex-1 flex flex-col min-w-0 justify-between">
                                            <div>
                                                <h3 className="font-w600 text-[15px] sm:text-[17px] leading-snug line-clamp-2 mb-1.5" style={{ color: textMain }}>
                                                    {itemName(item)}
                                                </h3>
                                                {(item.description_ar || item.description_en) && (
                                                    <p className="text-xs sm:text-[13px] line-clamp-2 mb-2 leading-relaxed" style={{ color: textMuted }}>
                                                        {isAr ? item.description_ar : (item.description_en || item.description_ar)}
                                                    </p>
                                                )}
                                            </div>

                                            <div className="mt-auto pt-2 flex flex-wrap items-end gap-x-3 gap-y-1">
                                                {item.prices.map((price, pIdx) => (
                                                    <div key={pIdx} className="flex flex-col items-start">
                                                        {item.size_labels?.[pIdx] && (
                                                            <span className="text-[11px] opacity-70 mb-0.5" style={{ color: textMuted }}>
                                                                {item.size_labels[pIdx]}
                                                            </span>
                                                        )}
                                                        <div className="font-w600 text-[14px] sm:text-[15px] font-bold tracking-tight" style={{ color: primaryColor }}>
                                                            {cur}{price?.toFixed?.(0) || price}
                                                        </div>
                                                    </div>
                                                ))}
                                            </div>
                                        </div>

                                        {/* Image Box (Right Side in LTR, Left Side in RTL) */}
                                        <div className="w-[90px] h-[90px] sm:w-[110px] sm:h-[110px] shrink-0 relative overflow-hidden rounded-xl bg-slate-100 dark:bg-slate-800">
                                            <img
                                                src={item.image_url || cat.image_url || "https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=400"}
                                                alt={itemName(item)}
                                                className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500"
                                            />
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    ))}

                    {activeCatList.length === 0 && (
                        <div className="text-center py-20 opacity-50 font-bold text-xl">
                            {isAr ? 'لا توجد منتجات' : 'No products found'}
                        </div>
                    )}
                </div>
            </main>



            {/* Modal */}
            <AnimatePresence>
                {selectedItem && (
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}
                        className="fixed inset-0 z-[200] flex items-end sm:items-center justify-center bg-black/60 backdrop-blur-sm sm:p-4"
                        onClick={closeModal}>
                        <motion.div initial={{ y: '100%' }} animate={{ y: 0 }} exit={{ y: '100%' }}
                            transition={{ type: 'spring', damping: 25, stiffness: 200 }}
                            className="relative w-full sm:max-w-[500px] h-[90vh] sm:h-auto sm:max-h-[90vh] rounded-t-3xl sm:rounded-[24px] overflow-hidden flex flex-col shadow-2xl"
                            style={{ backgroundColor: bgCard }}
                            onClick={e => e.stopPropagation()}>

                            {/* Mobile DRAG Handle */}
                            <div className="w-full flex justify-center pt-3 pb-1 sm:hidden absolute top-0 z-30" onClick={closeModal}>
                                <div className="w-12 h-1.5 rounded-full bg-white/50 backdrop-blur-md" />
                            </div>

                            {/* Close Button UI */}
                            <button onClick={closeModal} className="absolute top-4 left-4 z-20 w-8 h-8 rounded-full bg-white/20 backdrop-blur-md text-white flex items-center justify-center hover:bg-white/40 transition-colors">
                                <X className="w-5 h-5" />
                            </button>

                            {/* Modal Header Image */}
                            <div className="w-full h-[35vh] sm:h-[250px] shrink-0 relative bg-slate-100 dark:bg-slate-800">
                                <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent z-10" />
                                <img src={selectedItem.item.image_url || selectedItem.catImg || 'https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=600'}
                                    alt={itemName(selectedItem.item)} className="w-full h-full object-cover" />

                                <div className="absolute bottom-4 left-4 right-4 z-20" dir={isAr ? 'rtl' : 'ltr'}>
                                    <h2 className="text-2xl sm:text-3xl font-black text-white drop-shadow-md mb-1">{itemName(selectedItem.item)}</h2>
                                    <p className="text-white/90 text-sm sm:text-base font-bold drop-shadow-sm line-clamp-2">
                                        {isAr ? selectedItem.item.description_ar : (selectedItem.item.description_en || selectedItem.item.description_ar)}
                                    </p>
                                </div>
                            </div>

                            {/* Modal Content */}
                            <div className="flex-1 overflow-y-auto p-5 pb-24" style={{ color: textMain }}>

                                {/* Base Price */}
                                <div className="flex justify-between items-center bg-slate-50 dark:bg-slate-900 border p-4 rounded-xl mb-6" style={{ borderColor }}>
                                    <span className="font-bold">{isAr ? 'السعر' : 'Price'}</span>
                                    <span className="text-xl font-black" style={{ color: primaryColor }}>{cur}{selectedItem.item.prices[sizeIdx]?.toFixed?.(0)}</span>
                                </div>

                                {/* Sizes */}
                                {selectedItem.item.prices.length > 1 && (
                                    <div className="mb-6">
                                        <h3 className="font-bold mb-3 flex items-center gap-2">
                                            <List className="w-4 h-4 text-slate-400" />
                                            {isAr ? 'اختر الحجم' : 'Select Size'}
                                        </h3>
                                        <div className="grid grid-cols-2 gap-3">
                                            {selectedItem.item.prices.map((p, idx) => {
                                                const label = selectedItem.item.size_labels?.[idx] || (isAr ? `حجم ${idx + 1}` : `Size ${idx + 1}`);
                                                return (
                                                    <label key={idx} className="flex flex-col items-center justify-center p-3 rounded-xl border-2 cursor-pointer transition-all text-center"
                                                        style={{ borderColor: sizeIdx === idx ? primaryColor : borderColor, backgroundColor: sizeIdx === idx ? `${primaryColor}15` : 'transparent' }}
                                                        onClick={() => setSizeIdx(idx)}>
                                                        <span className="font-bold text-sm mb-1">{label}</span>
                                                        <span className="text-xs font-semibold">{cur}{p}</span>
                                                        <input type="radio" checked={sizeIdx === idx} readOnly className="hidden" />
                                                    </label>
                                                );
                                            })}
                                        </div>
                                    </div>
                                )}

                                {/* Extras */}
                                {selectedItem.item.extras && selectedItem.item.extras.length > 0 && (
                                    <div className="mb-6">
                                        <h3 className="font-bold mb-3 flex items-center gap-2">
                                            <Plus className="w-4 h-4 text-slate-400" />
                                            {isAr ? 'إضافات (اختياري)' : 'Extras (Optional)'}
                                        </h3>
                                        <div className="space-y-3">
                                            {selectedItem.item.extras.map((ext, idx) => {
                                                const id = ext.id || idx;
                                                const isSel = selectedExtras.some(e => e.id === id);
                                                return (
                                                    <label key={idx} className="flex items-center justify-between p-3 rounded-xl border border-dashed cursor-pointer transition-colors"
                                                        style={{ borderColor: isSel ? primaryColor : borderColor, backgroundColor: isSel ? `${primaryColor}05` : 'transparent' }}>
                                                        <input type="checkbox" className="hidden" checked={isSel}
                                                            onChange={() => {
                                                                if (isSel) setSelectedExtras(p => p.filter(e => e.id !== id));
                                                                else setSelectedExtras(p => [...p, { id, name: isAr ? ext.name_ar : (ext.name_en || ext.name_ar), price: ext.price }]);
                                                            }} />
                                                        <div className="flex items-center gap-3">
                                                            <div className="w-5 h-5 rounded border flex items-center justify-center transition-colors"
                                                                style={{ borderColor: isSel ? primaryColor : borderColor, backgroundColor: isSel ? primaryColor : 'transparent' }}>
                                                                {isSel && <X className="w-3 h-3 text-white" style={{ transform: 'rotate(45deg)' }} />}
                                                            </div>
                                                            <span className="text-sm font-semibold">{isAr ? ext.name_ar : (ext.name_en || ext.name_ar)}</span>
                                                        </div>
                                                        <span className="text-sm font-bold" style={{ color: primaryColor }}>{ext.price > 0 ? `+${cur}${ext.price}` : (isAr ? 'مجاناً' : 'Free')}</span>
                                                    </label>
                                                );
                                            })}
                                        </div>
                                    </div>
                                )}

                                {/* Notes */}
                                <div className="mb-6">
                                    <h3 className="font-bold mb-3 flex items-center gap-2">
                                        <FileText className="w-4 h-4 text-slate-400" />
                                        {isAr ? 'ملاحظات' : 'Notes'}
                                    </h3>
                                    <textarea value={notes} onChange={e => setNotes(e.target.value)}
                                        placeholder={isAr ? 'أضف ملاحظاتك الخاصة هنا...' : 'Special instructions...'}
                                        className="w-full rounded-xl p-3 outline-none resize-none h-24 text-sm border focus:ring-1 transition-all"
                                        style={{ backgroundColor: bgBody, color: textMain, borderColor, '--tw-ring-color': primaryColor } as React.CSSProperties} />
                                </div>
                            </div>

                            {/* Sticky Bottom Actions */}
                            <div className="absolute bottom-0 w-full p-4 border-t shadow-[0_-10px_20px_rgba(0,0,0,0.05)]" style={{ backgroundColor: bgCard, borderColor }}>
                                <div className="flex gap-4 items-center">
                                    <div className="flex items-center bg-slate-100 dark:bg-slate-800 rounded-full h-12 px-2 border" style={{ borderColor }}>
                                        <button onClick={() => setQty(Math.max(1, qty - 1))} className="w-8 h-8 rounded-full flex flex-col items-center justify-center text-slate-500 hover:bg-slate-200 dark:hover:bg-slate-700">
                                            <Minus className="w-4 h-4" />
                                        </button>
                                        <span className="w-8 text-center font-bold text-lg">{qty}</span>
                                        <button onClick={() => setQty(qty + 1)} className="w-8 h-8 rounded-full flex flex-col items-center justify-center text-slate-500 hover:bg-slate-200 dark:hover:bg-slate-700">
                                            <Plus className="w-4 h-4" />
                                        </button>
                                    </div>
                                    <button onClick={addToCart} className="flex-1 h-12 rounded-full text-white font-bold text-sm sm:text-base flex items-center justify-center gap-2 transition-transform active:scale-95 shadow-md hover:shadow-lg"
                                        style={{ backgroundColor: primaryColor }}>
                                        <ShoppingCart className="w-5 h-5" />
                                        {isAr ? 'إضافة إلى السلة' : 'Add to Cart'} — {cur}{(((selectedItem.item.prices[sizeIdx] || 0) + selectedExtras.reduce((s, e) => s + e.price, 0)) * qty).toFixed?.(0)}
                                    </button>
                                </div>
                            </div>
                        </motion.div>
                    </motion.div>
                )}
            </AnimatePresence>

            {/* Cart Drawer */}
            <AnimatePresence>
                {isCartOpen && (
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}
                        className="fixed inset-0 z-[300] bg-black/50 backdrop-blur-sm"
                        onClick={() => setIsCartOpen(false)}>
                        <motion.div initial={{ x: isAr ? '100%' : '100%' }} animate={{ x: 0 }} exit={{ x: isAr ? '100%' : '100%' }}
                            transition={{ type: 'spring', damping: 25, stiffness: 200 }}
                            className={`absolute top-0 ${isAr ? 'right-0' : 'right-0'} w-full max-w-[400px] h-full flex flex-col shadow-2xl`}
                            style={{ backgroundColor: bgCard }}
                            onClick={e => e.stopPropagation()}>

                            {/* Cart Header */}
                            <div className="p-5 flex justify-between items-center text-white shadow-md z-10 sticky top-0" style={{ backgroundColor: primaryColor }}>
                                <div className="flex flex-col">
                                    <span className="font-bold text-lg leading-none mb-1">{isAr ? 'عربة التسوق' : 'Cart'}</span>
                                    <span className="text-xs opacity-90">{cartCount} {isAr ? 'عناصر' : 'items'}</span>
                                </div>
                                <button onClick={() => setIsCartOpen(false)} className="w-8 h-8 flex flex-col items-center justify-center bg-white/20 hover:bg-white/30 rounded-full transition-colors"><X className="w-5 h-5" /></button>
                            </div>

                            {/* Cart Body */}
                            <div className="flex-1 overflow-y-auto" style={{ backgroundColor: bgBody }}>
                                {cart.length === 0 ? (
                                    <div className="flex flex-col items-center justify-center h-[50vh] text-center px-4" style={{ color: textMuted }}>
                                        <div className="w-24 h-24 mb-4 rounded-full bg-slate-200 dark:bg-slate-800 flex items-center justify-center">
                                            <ShoppingCart className="w-10 h-10 opacity-40" />
                                        </div>
                                        <p className="font-bold text-lg mb-2">{isAr ? 'سلتك فارغة' : 'Your cart is empty'}</p>
                                        <p className="text-sm opacity-80">{isAr ? 'أضف بعض المنتجات الشهية للبدء' : 'Add some delicious items to start'}</p>
                                    </div>
                                ) : (
                                    <div className="p-4 space-y-4">
                                        {cart.map((c, i) => (
                                            <div key={i} className="flex gap-4 p-4 rounded-2xl bg-white dark:bg-slate-800 shadow-sm border" style={{ borderColor }}>
                                                <div className="w-20 h-20 shrink-0 bg-slate-100 rounded-xl overflow-hidden">
                                                    <img src={c.item.image_url || 'https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=200'} alt="" className="w-full h-full object-cover" />
                                                </div>
                                                <div className="flex-1 min-w-0 flex flex-col justify-between" dir={isAr ? 'rtl' : 'ltr'}>
                                                    <div className="flex justify-between items-start">
                                                        <div>
                                                            <h4 className="font-bold text-sm line-clamp-1">{itemName(c.item)}</h4>
                                                            {(c.sizeLabel || c.notes) && (
                                                                <p className="text-[11px] mt-1 space-y-0.5" style={{ color: textMuted }}>
                                                                    {c.sizeLabel && <span className="block">{c.sizeLabel}</span>}
                                                                    {c.notes && <span className="block italic flex gap-1"><AlertCircle className="w-3 h-3 inline" />{c.notes}</span>}
                                                                </p>
                                                            )}
                                                        </div>
                                                        <button onClick={() => updateQty(c.id, c.notes, -c.quantity)} className="text-slate-300 hover:text-red-500 transition-colors p-1">
                                                            <Trash2 className="w-4 h-4" />
                                                        </button>
                                                    </div>

                                                    <div className="flex justify-between items-center mt-3">
                                                        <span className="font-black" style={{ color: primaryColor }}>{cur}{(c.price * c.quantity).toFixed?.(0)}</span>
                                                        <div className="flex items-center bg-slate-100 dark:bg-slate-900 rounded-full h-8 px-1 border" style={{ borderColor }}>
                                                            <button onClick={() => updateQty(c.id, c.notes, -1)} className="w-6 h-6 flex items-center justify-center text-slate-500 hover:text-primary-600"><Minus className="w-3 h-3" /></button>
                                                            <span className="w-6 text-center text-sm font-bold">{c.quantity}</span>
                                                            <button onClick={() => updateQty(c.id, c.notes, 1)} className="w-6 h-6 flex items-center justify-center text-slate-500 hover:text-primary-600"><Plus className="w-3 h-3" /></button>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        ))}

                                        {/* Delivery Info */}
                                        <div className="mt-6 bg-white dark:bg-slate-800 p-5 rounded-2xl border shadow-sm" style={{ borderColor }}>
                                            <h4 className="font-bold text-sm mb-4 flex items-center gap-2" style={{ color: textMain }}>
                                                <MapIcon className="w-4 h-4" style={{ color: primaryColor }} />
                                                {isAr ? 'بيانات التوصيل' : 'Delivery Details'}
                                            </h4>
                                            <div className="space-y-3 flex flex-col items-end">
                                                <input type="text" placeholder={isAr ? "الاسم" : "Name"} value={customerInfo.name} onChange={e => setCustomerInfo({ ...customerInfo, name: e.target.value })}
                                                    className="w-full border rounded-xl p-3 text-sm outline-none focus:ring-1" style={{ backgroundColor: bgBody, color: textMain, borderColor, '--tw-ring-color': primaryColor } as React.CSSProperties} dir={isAr ? "rtl" : "ltr"} />
                                                <input type="tel" placeholder={isAr ? "رقم الجوال" : "Phone number"} value={customerInfo.phone} onChange={e => setCustomerInfo({ ...customerInfo, phone: e.target.value })}
                                                    className="w-full border rounded-xl p-3 text-sm outline-none focus:ring-1 text-left" style={{ backgroundColor: bgBody, color: textMain, borderColor, '--tw-ring-color': primaryColor } as React.CSSProperties} dir="ltr" />
                                                <textarea placeholder={isAr ? "العنوان بالتفصيل" : "Full Address"} value={customerInfo.address} onChange={e => setCustomerInfo({ ...customerInfo, address: e.target.value })}
                                                    className="w-full border rounded-xl p-3 text-sm outline-none focus:ring-1 h-20 resize-none" style={{ backgroundColor: bgBody, color: textMain, borderColor, '--tw-ring-color': primaryColor } as React.CSSProperties} dir={isAr ? "rtl" : "ltr"} />
                                            </div>
                                        </div>
                                    </div>
                                )}
                            </div>

                            {/* Cart Footer */}
                            {cart.length > 0 && (
                                <div className="p-5 shadow-[0_-5px_15px_rgba(0,0,0,0.05)] border-t z-20" style={{ backgroundColor: bgCard, borderColor }}>
                                    <div className="flex justify-between items-center mb-4 text-sm font-bold" style={{ color: textMain }}>
                                        <span>{isAr ? 'الإجمالي' : 'Total'}</span>
                                        <span className="text-xl font-black" style={{ color: primaryColor }}>{cur}{cartTotal.toFixed?.(0)}</span>
                                    </div>
                                    <button onClick={checkOutWhatsApp} className="w-full flex justify-center items-center gap-2 h-14 rounded-full text-white font-bold text-lg transition-transform active:scale-95 shadow-lg group hover:bg-[#20b858]"
                                        style={{ backgroundColor: '#25d366' }}>
                                        <FaWhatsapp className="w-6 h-6 group-hover:scale-110 transition-transform" />
                                        {isAr ? 'تأكيد الطلب' : 'Confirm Order'}
                                    </button>
                                    <p className="text-center text-[11px] mt-3 opacity-60 font-semibold">{isAr ? 'سيتم تحويلك إلى واتساب لإتمام الطلب' : 'You will be redirected to WhatsApp to complete order'}</p>
                                </div>
                            )}
                        </motion.div>
                    </motion.div>
                )}
            </AnimatePresence>
        </div>
    );
}

'use client';

import React, { useState, useEffect } from 'react';
import { useTheme } from 'next-themes';
import { motion, AnimatePresence } from 'framer-motion';
import { UtensilsCrossed, AlertCircle, ShoppingCart, Plus, Minus, Trash2, X, FileText, MapPin as MapIcon, PhoneCall, Link as LinkIcon, Star, Share2 } from 'lucide-react';
import { FaWhatsapp, } from 'react-icons/fa';
import SharedMarquee from './SharedMarquee';
import { Swiper, SwiperSlide } from 'swiper/react';
import { FreeMode } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/free-mode';

type MenuItem = {
    id: string | number;
    title_ar: string;
    title_en?: string;
    description_ar?: string;
    description_en?: string;
    image?: string;
    image_url?: string;
    prices: number[];
    size_labels?: string[];
    extras?: { id?: number | string; name_ar: string; name_en?: string; price: number }[];
    is_available?: boolean;
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    [key: string]: any;
};

// ================= THEME 13 CONSTANTS =================
// The template uses #86ac70 as the primary color.
const T13_PRIMARY = '#86ac70';
const T13_PRIMARY_LIGHT = '#9ebd8d';
const T13_ACCENT = '#FF6B6B';

interface CategoryWithItemsType {
    id: string | number;
    name_ar: string;
    name_en?: string;
    items?: MenuItem[];
    image_url?: string;
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    [key: string]: any;
}

interface RestaurantType {
    name: string;
    theme?: string;
    theme_colors?: {
        main_color?: string;
        bg_color?: string;
        text_color?: string;
    };
    cover_images?: string[];
    logo_url?: string;
    cover_url?: string;
    marquee_enabled?: boolean;
    marquee_text_ar?: string;
    marquee_text_en?: string;
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    [key: string]: any;
}

interface Theme13MenuProps {
    config: RestaurantType;
    categories: CategoryWithItemsType[];
}

export default function Theme13Menu({ config, categories }: Theme13MenuProps) {
    const { theme, setTheme } = useTheme();
    const [mounted, setMounted] = useState(false);
    useEffect(() => setMounted(true), []);

    const isAr = config.default_language === 'ar' || true; // Defaulting to true for now since design is RTL primary
    const isDark = mounted && theme === 'dark';
    const cur = config.currency || 'ج.م';

    // Theme Variables - matching Theme 13 CSS
    const primaryColor = config.theme_colors?.main_color || T13_PRIMARY;
    const bgBody = isDark ? '#121212' : '#f8f9fa';
    const bgCard = isDark ? '#1e1e1e' : '#ffffff';
    const textMain = isDark ? '#f1f1f1' : '#333333';
    const textMuted = isDark ? '#aaaaaa' : '#666666';
    const borderColor = isDark ? '#333333' : '#e0e0e0';

    // State
    const [activeCategory, setActiveCategory] = useState<string>('all');
    const [isCartOpen, setIsCartOpen] = useState(false);
    const [selectedItem, setSelectedItem] = useState<{ item: MenuItem; catName: string; catImg?: string } | null>(null);

    // Item Detail Modal
    const [qty, setQty] = useState(1);
    const [sizeIdx, setSizeIdx] = useState(0);
    const [notes, setNotes] = useState('');
    const [selectedExtras, setSelectedExtras] = useState<{ id: number | string, name: string, price: number }[]>([]);

    // Checkout
    const [customerInfo, setCustomerInfo] = useState({ name: '', phone: '', address: '' });
    const [cart, setCart] = useState<{ id: string, item: MenuItem, catName: string, price: number, sizeLabel: string, quantity: number, notes: string }[]>([]);

    // Notification toast
    const [toastMsg, setToastMsg] = useState('');

    const itemName = (item: MenuItem) => isAr ? item.title_ar : (item.title_en || item.title_ar);
    const catName = (cat: CategoryWithItemsType) => isAr ? cat.name_ar : (cat.name_en || cat.name_ar);

    const showToast = (msg: string) => {
        setToastMsg(msg);
        setTimeout(() => setToastMsg(''), 2500);
    };

    // Derived cart values
    const cartCount = cart.reduce((acc, curr) => acc + curr.quantity, 0);
    const cartTotal = cart.reduce((acc, curr) => acc + (curr.price * curr.quantity), 0);

    // Modals
    const openModal = (item: MenuItem, cName: string, cImg?: string) => {
        setSelectedItem({ item, catName: cName, catImg: cImg });
        setQty(1);
        setSizeIdx(0);
        setSelectedExtras([]);
        setNotes('');
        document.body.style.overflow = 'hidden';
    };

    const closeModal = () => {
        setSelectedItem(null);
        document.body.style.overflow = 'auto';
    };

    // Cart Handlers
    const addToCart = () => {
        if (!selectedItem) return;
        const itemP = selectedItem.item.prices[sizeIdx] || 0;
        const extP = selectedExtras.reduce((sum, e) => sum + e.price, 0);
        const finalPrice = itemP + extP;

        let sizeLbl = selectedItem.item.size_labels?.[sizeIdx] || '';
        if (selectedExtras.length > 0) {
            const extNames = selectedExtras.map(e => e.name).join(' + ');
            sizeLbl = sizeLbl ? `${sizeLbl} | +${extNames}` : `+${extNames}`;
        }

        const cId = `${selectedItem.item.id}-${sizeIdx}-${selectedExtras.map(e => e.id).sort().join('-')}`;

        setCart(prev => {
            const ex = prev.find(c => c.id === cId && c.notes === notes);
            if (ex) return prev.map(c => c.id === cId && c.notes === notes ? { ...c, quantity: c.quantity + qty } : c);
            return [...prev, {
                id: cId,
                item: selectedItem.item,
                catName: selectedItem.catName,
                price: finalPrice,
                sizeLabel: sizeLbl,
                quantity: qty,
                notes
            }];
        });
        closeModal();
        showToast(isAr ? 'تمت الإضافة إلى السلة بنجاح' : 'Added to cart successfully');
    };

    const updateQty = (id: string, itemNotes: string, delta: number) => {
        setCart(prev => prev.map(c => {
            if (c.id === id && c.notes === itemNotes) {
                const n = c.quantity + delta;
                return n > 0 ? { ...c, quantity: n } : c;
            }
            return c;
        }).filter(c => c.quantity > 0));
    };

    const checkOutWhatsApp = () => {
        if (!config.phone) {
            alert(isAr ? 'رقم المطعم غير متوفر' : 'Restaurant phone unavailable');
            return;
        }
        if (!customerInfo.name || !customerInfo.phone || !customerInfo.address) {
            alert(isAr ? 'يرجى إكمال بيانات التوصيل' : 'Please complete delivery details');
            return;
        }

        let txt = isAr ? `*طلب جديد من ${customerInfo.name}*%0A` : `*New Order from ${customerInfo.name}*%0A`;
        txt += isAr ? `رقم الهاتف: ${customerInfo.phone}%0A` : `Phone: ${customerInfo.phone}%0A`;
        txt += isAr ? `العنوان: ${customerInfo.address}%0A%0A` : `Address: ${customerInfo.address}%0A%0A`;

        cart.forEach(c => {
            txt += `▪ ${itemName(c.item)} (${c.quantity}x)\n`;
            if (c.sizeLabel) txt += `   ${c.sizeLabel}\n`;
            if (c.notes) txt += `   📝 ${c.notes}\n`;
            txt += `   ${cur}${(c.price * c.quantity).toFixed?.(0)}\n\n`;
        });

        txt += isAr ? `*الإجمالي: ${cur}${cartTotal.toFixed?.(0)}*` : `*Total: ${cur}${cartTotal.toFixed?.(0)}*`;

        const tel = config.phone.replace(/\D/g, '');
        window.open(`https://wa.me/${tel}?text=${encodeURIComponent(txt)}`, '_blank');
    };

    // Filter Logic
    const activeCatList = activeCategory === 'all'
        ? categories
        : categories.filter(c => c.id.toString() === activeCategory);

    // Get all featured items (assuming items with is_featured flag or just taking the first 4 from all categories)
    const featuredItems = categories.flatMap(c => c.items || []).slice(0, 6);

    if (!mounted) return <div className="min-h-screen bg-slate-50 dark:bg-slate-900" />;

    return (
        <div className="min-h-screen font-cairo pb-24 overflow-x-hidden" style={{ backgroundColor: bgBody, color: textMain }} dir={isAr ? 'rtl' : 'ltr'}>

            {/* Header Marquee */}
            {config.marquee_enabled && (
                <div className="font-cairo text-sm text-white" style={{ backgroundColor: primaryColor }}>
                    <SharedMarquee
                        text={isAr ? (config.marquee_text_ar || '') : (config.marquee_text_en || config.marquee_text_ar || '')}
                    />
                </div>
            )}

            {/* Modern Header - Theme 13 specific */}
            <div className="relative h-[160px] md:h-[200px] mb-[15px] rounded-b-2xl md:rounded-b-3xl shadow-[0_4px_15px_rgba(0,0,0,0.1)] overflow-hidden">
                <div
                    className="absolute inset-0 bg-cover bg-center transition-transform duration-500 hover:scale-105"
                    style={{ backgroundImage: `url(${config.cover_url || config.cover_images?.[0] || 'https://images.unsplash.com/photo-1514933651103-005eec06c04b?q=80&w=1000'})` }}
                />
                <div className="absolute bottom-0 left-0 w-full h-full bg-gradient-to-t from-black/80 via-black/40 to-transparent flex flex-col justify-end p-4">
                    <div className="absolute bottom-4 right-4 flex items-center gap-3">
                        <div className="w-[80px] h-[80px] md:w-[100px] md:h-[100px] rounded-full border-[3px] border-white shadow-[0_3px_12px_rgba(0,0,0,0.2)] overflow-hidden shrink-0 bg-white hover:scale-110 hover:rotate-[5deg] transition-all duration-300">
                            <img
                                src={config.logo_url || 'https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=200'}
                                alt={config.name}
                                className="w-full h-full object-cover"
                            />
                        </div>
                        <h2 className="text-white text-2xl md:text-3xl font-bold drop-shadow-[0_2px_5px_rgba(0,0,0,0.6)] m-0 leading-tight">
                            {config.name}
                        </h2>
                    </div>
                </div>
            </div>

            <main className="max-w-[1200px] mx-auto w-full">

                {/* Category Filter - Circular Design */}
                <div className="px-4 mb-6">
                    <Swiper
                        modules={[FreeMode]}
                        slidesPerView="auto"
                        spaceBetween={15}
                        freeMode={true}
                        dir={isAr ? 'rtl' : 'ltr'}
                        className="w-full py-2"
                    >
                        <SwiperSlide style={{ width: 'auto' }}>
                            <div
                                onClick={() => setActiveCategory('all')}
                                className={`flex flex-col items-center cursor-pointer transition-all duration-300 w-[80px] md:w-[100px] ${activeCategory === 'all' ? '-translate-y-1' : ''}`}
                            >
                                <div className="relative w-[65px] h-[65px] md:w-[80px] md:h-[80px] rounded-full overflow-hidden mb-2 mx-auto">
                                    <div className="w-full h-full bg-slate-200 dark:bg-slate-700 flex items-center justify-center text-slate-500">
                                        <UtensilsCrossed className="w-8 h-8 md:w-10 md:h-10 opacity-50" />
                                    </div>
                                    <div
                                        className={`absolute inset-0 border-[3px] rounded-full transition-opacity duration-300 ${activeCategory === 'all' ? 'opacity-100 shadow-[0_5px_15px_rgba(0,0,0,0.15)]' : 'opacity-0'}`}
                                        style={{ borderColor: primaryColor }}
                                    />
                                </div>
                                <span className={`text-xs md:text-sm font-semibold truncate w-full text-center ${activeCategory === 'all' ? 'text-primary' : ''}`} style={{ color: activeCategory === 'all' ? primaryColor : textMain }}>
                                    {isAr ? 'الكل' : 'All'}
                                </span>
                            </div>
                        </SwiperSlide>

                        {categories.map((cat) => (
                            <SwiperSlide key={cat.id} style={{ width: 'auto' }}>
                                <div
                                    onClick={() => setActiveCategory(cat.id.toString())}
                                    className={`flex flex-col items-center cursor-pointer transition-all duration-300 w-[80px] md:w-[100px] ${activeCategory === cat.id.toString() ? '-translate-y-1' : ''}`}
                                >
                                    <div className="relative w-[65px] h-[65px] md:w-[80px] md:h-[80px] rounded-full overflow-hidden mb-2 mx-auto">
                                        <img
                                            src={cat.image_url || 'https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=200'}
                                            alt={catName(cat)}
                                            className="w-full h-full object-cover"
                                        />
                                        <div
                                            className={`absolute inset-0 border-[3px] rounded-full transition-opacity duration-300 ${activeCategory === cat.id.toString() ? 'opacity-100 shadow-[0_5px_15px_rgba(0,0,0,0.15)]' : 'opacity-0'}`}
                                            style={{ borderColor: primaryColor }}
                                        />
                                    </div>
                                    <span className="text-xs md:text-sm font-semibold truncate w-full text-center" style={{ color: activeCategory === cat.id.toString() ? primaryColor : textMain }}>
                                        {catName(cat)}
                                    </span>
                                </div>
                            </SwiperSlide>
                        ))}
                    </Swiper>
                </div>

                {/* Featured Items Section - Only show when "All" is selected */}
                {activeCategory === 'all' && featuredItems.length > 0 && (
                    <div className="px-4 mb-8">
                        <div className="flex items-center gap-2 mb-4 relative pb-2 border-b-[3px] border-b-transparent after:content-[''] after:absolute after:bottom-0 after:right-0 after:w-[50px] after:h-[3px] after:rounded-full after:bg-[--section-color]" style={{ '--section-color': primaryColor } as React.CSSProperties}>
                            <Star className="w-5 h-5" style={{ color: primaryColor }} />
                            <span className="text-lg md:text-xl font-bold" style={{ color: textMain }}>
                                {isAr ? 'منتجات مميزة' : 'Featured Items'}
                            </span>
                        </div>

                        <Swiper
                            modules={[FreeMode]}
                            slidesPerView={1.5}
                            breakpoints={{ 640: { slidesPerView: 2.5 }, 1024: { slidesPerView: 3.5 } }}
                            spaceBetween={15}
                            freeMode={true}
                            dir={isAr ? 'rtl' : 'ltr'}
                            className="w-full pb-4"
                        >
                            {featuredItems.map((item, idx) => (
                                <SwiperSlide key={`featured-${item.id || idx}`}>
                                    <div
                                        onClick={() => openModal(item, "Featured")}
                                        className="bg-white dark:bg-[#1e1e1e] rounded-xl overflow-hidden shadow-[0_5px_15px_rgba(0,0,0,0.08)] transition-all duration-300 border hover:-translate-y-1 hover:shadow-[0_8px_25px_rgba(0,0,0,0.12)] cursor-pointer flex flex-col h-[250px] md:h-[280px]"
                                        style={{ borderColor }}
                                    >
                                        <div className="w-full h-[140px] md:h-[160px] relative overflow-hidden shrink-0">
                                            <img
                                                src={item.image_url || "https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=400"}
                                                alt={itemName(item)}
                                                className="w-full h-full object-cover transition-transform duration-500 hover:scale-105"
                                            />
                                        </div>
                                        <div className="p-3 flex flex-col justify-between flex-1">
                                            <div>
                                                <h3 className="font-semibold text-sm md:text-base line-clamp-1 mb-1" style={{ color: textMain }}>
                                                    {itemName(item)}
                                                </h3>
                                                <p className="text-xs md:text-sm line-clamp-2 leading-tight" style={{ color: textMuted }}>
                                                    {isAr ? item.description_ar : (item.description_en || item.description_ar)}
                                                </p>
                                            </div>
                                            <div className="flex justify-between items-center mt-2">
                                                <span className="font-bold text-sm md:text-base" style={{ color: primaryColor }}>
                                                    {cur} {item.prices[0]?.toFixed?.(0) || item.prices[0]}
                                                </span>
                                                <button
                                                    className="rounded-full px-3 py-1.5 text-xs font-semibold text-white transition-all hover:-translate-y-0.5 flex items-center gap-1.5"
                                                    style={{ backgroundColor: primaryColor }}
                                                    onClick={(e) => { e.stopPropagation(); openModal(item, "Featured"); }}
                                                >
                                                    <Plus className="w-3.5 h-3.5" />
                                                    {isAr ? 'إضافة' : 'Add'}
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                </SwiperSlide>
                            ))}
                        </Swiper>
                    </div>
                )}

                {/* Main Menu Grid */}
                <div className="px-4 space-y-10 min-h-[50vh]">
                    {activeCatList.map((cat) => (
                        <div key={cat.id}>
                            <div className="flex items-center gap-2 mb-4 relative pb-2 border-b-[3px] border-b-transparent after:content-[''] after:absolute after:bottom-0 after:right-0 after:w-[50px] after:h-[3px] after:rounded-full after:bg-[--section-color]" style={{ '--section-color': primaryColor } as React.CSSProperties}>
                                <span className="text-lg md:text-xl font-bold" style={{ color: textMain }}>
                                    {catName(cat)}
                                </span>
                            </div>

                            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-3 md:gap-4">
                                {cat.items?.map((item: MenuItem, idx: number) => (
                                    <div
                                        key={item.id || idx}
                                        onClick={() => openModal(item, catName(cat), cat.image_url)}
                                        className="bg-white dark:bg-[#1e1e1e] rounded-xl overflow-hidden shadow-[0_5px_15px_rgba(0,0,0,0.08)] transition-all duration-300 border hover:-translate-y-1 hover:shadow-[0_8px_25px_rgba(0,0,0,0.12)] cursor-pointer flex flex-col h-[220px] md:h-[260px] animate-fade-in-up"
                                        style={{ borderColor }}
                                    >
                                        <div className="w-full h-[130px] md:h-[150px] relative overflow-hidden shrink-0">
                                            <div className="absolute inset-0 bg-gradient-to-b from-black/20 to-transparent z-10 opacity-0 hover:opacity-100 transition-opacity duration-300" />
                                            <img
                                                src={item.image_url || cat.image_url || "https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=400"}
                                                alt={itemName(item)}
                                                className="w-full h-full object-cover transition-transform duration-500 hover:scale-110"
                                            />
                                            {/* Action Buttons Overlay */}
                                            <div className="absolute top-2 left-2 z-20 flex gap-2">
                                                <button
                                                    className="w-7 h-7 rounded-full bg-black/50 backdrop-blur-sm text-white flex items-center justify-center text-xs hover:scale-110 transition-transform shadow-sm"
                                                    onClick={(e) => {
                                                        e.stopPropagation();
                                                        navigator.clipboard.writeText(window.location.href);
                                                        showToast(isAr ? 'تم نسخ الرابط' : 'Link copied');
                                                    }}
                                                >
                                                    <LinkIcon className="w-3.5 h-3.5" />
                                                </button>
                                            </div>
                                        </div>

                                        <div className="p-3 flex flex-col justify-between flex-1">
                                            <div>
                                                <h3 className="font-semibold text-xs md:text-sm line-clamp-2 mb-1" style={{ color: textMain }}>
                                                    {itemName(item)}
                                                </h3>
                                                {(item.description_ar || item.description_en) && (
                                                    <p className="text-[10px] md:text-xs line-clamp-1 md:line-clamp-2 leading-tight" style={{ color: textMuted }}>
                                                        {isAr ? item.description_ar : (item.description_en || item.description_ar)}
                                                    </p>
                                                )}
                                            </div>

                                            <div className="flex justify-between items-center mt-2 pt-2 border-t" style={{ borderColor }}>
                                                <span className="font-bold text-sm md:text-base whitespace-nowrap" style={{ color: primaryColor }}>
                                                    {cur} {item.prices[0]?.toFixed?.(0) || item.prices[0]}
                                                </span>
                                                <button
                                                    className="rounded-full px-2.5 py-1 text-[10px] md:text-xs font-semibold text-white transition-all hover:-translate-y-0.5 flex items-center gap-1 shrink-0"
                                                    style={{ backgroundColor: primaryColor }}
                                                    onClick={(e) => { e.stopPropagation(); openModal(item, catName(cat), cat.image_url); }}
                                                >
                                                    <Plus className="w-3 h-3" />
                                                    <span className="hidden sm:inline">{isAr ? 'أضف' : 'Add'}</span>
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>

                            {(!cat.items || cat.items.length === 0) && (
                                <div className="text-center py-10 opacity-50 font-bold text-lg bg-white/50 dark:bg-black/10 rounded-xl border border-dashed" style={{ borderColor }}>
                                    <AlertCircle className="w-8 h-8 mx-auto mb-2 opacity-50" />
                                    {isAr ? 'لا توجد منتجات في هذا القسم' : 'No products in this category'}
                                </div>
                            )}
                        </div>
                    ))}

                    {activeCatList.length === 0 && (
                        <div className="text-center py-20 opacity-50 font-bold text-xl">
                            {isAr ? 'لا توجد منتجات' : 'No products found'}
                        </div>
                    )}
                </div>
            </main>

            {/* Floating WhatsApp Share FAB */}
            {(config.social_links?.whatsapp || config.whatsapp_number || config.phone) && (
                <a
                    href={`https://wa.me/${(config.social_links?.whatsapp || config.whatsapp_number || config.phone).replace(/\+/g, '')}?text=${encodeURIComponent(isAr ? 'شاهد قائمة الطعام من ' + config.name + ' - ' + window.location.href : 'Check out the menu from ' + config.name + ' - ' + window.location.href)}`}
                    target="_blank"
                    rel="noreferrer"
                    className="fixed bottom-[90px] right-4 w-12 h-12 md:w-14 md:h-14 rounded-full flex items-center justify-center text-white shadow-[0_4px_15px_rgba(0,0,0,0.2)] hover:scale-110 transition-transform z-[90]"
                    style={{ background: 'linear-gradient(135deg, #25D366, #128C7E)' }}
                >
                    <Share2 className="w-5 h-5 md:w-6 md:h-6" />
                </a>
            )}

            {/* Bottom Dock Navigation */}
            <div className="fixed bottom-0 left-0 w-full bg-white dark:bg-[#1e1e1e] shadow-[0_-2px_15px_rgba(0,0,0,0.1)] border-t z-[100] flex justify-around py-2.5 md:py-3 px-2 transition-transform duration-500" style={{ borderColor }}>
                <a href="#" className="flex flex-col items-center gap-1 px-4 py-1 rounded-lg hover:bg-black/5 dark:hover:bg-white/5 transition-colors" style={{ color: primaryColor }}>
                    <UtensilsCrossed className="w-5 h-5 md:w-6 md:h-6" />
                    <span className="text-[10px] md:text-xs font-semibold">{isAr ? 'القائمة' : 'Menu'}</span>
                </a>
                <button
                    onClick={() => setIsCartOpen(true)}
                    className="flex flex-col items-center gap-1 px-4 py-1 rounded-lg relative hover:bg-black/5 dark:hover:bg-white/5 transition-colors"
                    style={{ color: textMuted }}
                >
                    <div className="relative">
                        <ShoppingCart className="w-5 h-5 md:w-6 md:h-6" />
                        {cartCount > 0 && (
                            <span
                                className="absolute -top-1.5 -right-2 text-white rounded-full w-4 h-4 md:w-5 md:h-5 text-[9px] md:text-[10px] font-bold flex items-center justify-center shadow-sm"
                                style={{ backgroundColor: T13_ACCENT }}
                            >
                                {cartCount}
                            </span>
                        )}
                    </div>
                    <span className="text-[10px] md:text-xs font-semibold">{isAr ? 'السلة' : 'Cart'}</span>
                </button>
                <a
                    href={`tel:${config.phone}`}
                    className="flex flex-col items-center gap-1 px-4 py-1 rounded-lg hover:bg-black/5 dark:hover:bg-white/5 transition-colors"
                    style={{ color: textMuted }}
                >
                    <PhoneCall className="w-5 h-5 md:w-6 md:h-6" />
                    <span className="text-[10px] md:text-xs font-semibold">{isAr ? 'اتصل' : 'Call'}</span>
                </a>
            </div>

            {/* Item Details Modal */}
            <AnimatePresence>
                {selectedItem && (
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}
                        className="fixed inset-0 z-[200] flex items-end md:items-center justify-center bg-black/60 backdrop-blur-sm md:p-4"
                        onClick={closeModal}>
                        <motion.div initial={{ y: '100%' }} animate={{ y: 0 }} exit={{ y: '100%' }}
                            transition={{ type: 'spring', damping: 25, stiffness: 200 }}
                            className="relative w-full md:max-w-[500px] h-[90vh] md:h-auto md:max-h-[90vh] rounded-t-2xl md:rounded-[24px] overflow-hidden flex flex-col shadow-2xl"
                            style={{ backgroundColor: bgCard }}
                            onClick={e => e.stopPropagation()}>

                            {/* Mobile DRAG Handle */}
                            <div className="w-full flex justify-center pt-3 pb-1 md:hidden absolute top-0 z-30" onClick={closeModal}>
                                <div className="w-12 h-1.5 rounded-full bg-white/50 backdrop-blur-md" />
                            </div>

                            <button onClick={closeModal} className="absolute top-4 left-4 z-20 w-8 h-8 rounded-full bg-white/20 backdrop-blur-md text-white flex items-center justify-center hover:bg-white/40 transition-colors">
                                <X className="w-4 h-4" />
                            </button>

                            <div className="w-full h-[35vh] md:h-[250px] shrink-0 relative bg-slate-100 dark:bg-slate-800">
                                <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/20 to-transparent z-10" />
                                <img src={selectedItem.item.image_url || selectedItem.catImg || 'https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=600'}
                                    alt={itemName(selectedItem.item)} className="w-full h-full object-cover" />

                                <div className="absolute bottom-4 left-4 right-4 z-20" dir={isAr ? 'rtl' : 'ltr'}>
                                    <h2 className="text-xl md:text-2xl font-bold text-white drop-shadow-md mb-1 leading-tight">{itemName(selectedItem.item)}</h2>
                                    <p className="text-white/80 text-xs md:text-sm font-medium drop-shadow-sm line-clamp-2">
                                        {isAr ? selectedItem.item.description_ar : (selectedItem.item.description_en || selectedItem.item.description_ar)}
                                    </p>
                                </div>
                            </div>

                            <div className="flex-1 overflow-y-auto p-4 md:p-5 pb-24 space-y-5" style={{ color: textMain }}>

                                {/* Header / Price */}
                                <div className="flex justify-between items-center pb-4 border-b border-dashed" style={{ borderColor }}>
                                    <span className="font-semibold text-sm md:text-base">{isAr ? 'السعر الأساسي' : 'Base Price'}</span>
                                    <span className="text-lg md:text-xl font-bold" style={{ color: primaryColor }}>{cur} {selectedItem.item.prices[sizeIdx]?.toFixed?.(0)}</span>
                                </div>

                                {/* Sizes */}
                                {selectedItem.item.prices.length > 1 && (
                                    <div>
                                        <h3 className="font-bold text-sm mb-3 text-[--text-muted]" style={{ '--text-muted': textMuted } as React.CSSProperties}>{isAr ? 'اختر الحجم' : 'Select Size'}</h3>
                                        <div className="grid grid-cols-2 gap-3">
                                            {selectedItem.item.prices.map((p, idx) => {
                                                const label = selectedItem.item.size_labels?.[idx] || (isAr ? `حجم ${idx + 1}` : `Size ${idx + 1}`);
                                                return (
                                                    <label key={idx} className="flex flex-col items-center justify-center p-3 rounded-xl border-2 cursor-pointer transition-all text-center relative overflow-hidden"
                                                        style={{ borderColor: sizeIdx === idx ? primaryColor : borderColor, backgroundColor: sizeIdx === idx ? `${primaryColor}10` : 'transparent' }}
                                                        onClick={() => setSizeIdx(idx)}>
                                                        {sizeIdx === idx && (
                                                            <div className="absolute top-0 right-0 w-0 h-0 border-[15px] border-r-[--primary] border-t-[--primary] border-l-transparent border-b-transparent" style={{ '--primary': primaryColor } as React.CSSProperties} />
                                                        )}
                                                        <span className="font-bold text-sm mb-1">{label}</span>
                                                        <span className="text-xs font-medium" style={{ color: primaryColor }}>{cur} {p}</span>
                                                        <input type="radio" checked={sizeIdx === idx} readOnly className="hidden" />
                                                    </label>
                                                );
                                            })}
                                        </div>
                                    </div>
                                )}

                                {/* Extras */}
                                {selectedItem.item.extras && selectedItem.item.extras.length > 0 && (
                                    <div>
                                        <h3 className="font-bold text-sm mb-3 text-[--text-muted]" style={{ '--text-muted': textMuted } as React.CSSProperties}>{isAr ? 'إضافات (اختياري)' : 'Extras (Optional)'}</h3>
                                        <div className="space-y-2">
                                            {selectedItem.item.extras.map((ext, idx) => {
                                                const id = ext.id || idx;
                                                const isSel = selectedExtras.some(e => e.id === id);
                                                return (
                                                    <label key={idx} className="flex items-center justify-between p-3 rounded-xl border cursor-pointer transition-colors"
                                                        style={{ borderColor, backgroundColor: isSel ? `${primaryColor}05` : 'transparent' }}>
                                                        <input type="checkbox" className="hidden" checked={isSel}
                                                            onChange={() => {
                                                                if (isSel) setSelectedExtras(p => p.filter(e => e.id !== id));
                                                                else setSelectedExtras(p => [...p, { id, name: isAr ? ext.name_ar : (ext.name_en || ext.name_ar), price: ext.price }]);
                                                            }} />
                                                        <div className="flex items-center gap-3">
                                                            <div className="w-5 h-5 rounded border flex items-center justify-center transition-colors"
                                                                style={{ borderColor: isSel ? primaryColor : borderColor, backgroundColor: isSel ? primaryColor : 'transparent' }}>
                                                                {isSel && <Plus className="w-3 h-3 text-white" style={{ transform: 'rotate(45deg)' }} />}
                                                            </div>
                                                            <span className="text-sm font-semibold">{isAr ? ext.name_ar : (ext.name_en || ext.name_ar)}</span>
                                                        </div>
                                                        <span className="text-xs font-bold" style={{ color: primaryColor }}>{ext.price > 0 ? `+${cur} ${ext.price}` : (isAr ? 'مجاناً' : 'Free')}</span>
                                                    </label>
                                                );
                                            })}
                                        </div>
                                    </div>
                                )}

                                {/* Notes */}
                                <div>
                                    <h3 className="font-bold text-sm mb-3 flex items-center gap-2 text-[--text-muted]" style={{ '--text-muted': textMuted } as React.CSSProperties}>
                                        <FileText className="w-4 h-4" />
                                        {isAr ? 'ملاحظات إضافية' : 'Special Instructions'}
                                    </h3>
                                    <textarea value={notes} onChange={e => setNotes(e.target.value)}
                                        placeholder={isAr ? 'مثال: بدون بصل، زيادة صوص...' : 'e.g., no onions, extra sauce...'}
                                        className="w-full rounded-xl p-3 outline-none resize-none h-24 text-sm border focus:border-[--primary] transition-all"
                                        style={{ backgroundColor: bgBody, color: textMain, borderColor, '--primary': primaryColor } as React.CSSProperties} />
                                </div>
                            </div>

                            {/* Sticky Modal Footer */}
                            <div className="absolute bottom-0 w-full p-4 border-t bg-white dark:bg-[#1e1e1e] shadow-[0_-5px_15px_rgba(0,0,0,0.05)]" style={{ borderColor }}>
                                <div className="flex gap-3 md:gap-4 items-center">
                                    <div className="flex items-center bg-slate-100 dark:bg-slate-800 rounded-lg h-12 px-2 border" style={{ borderColor }}>
                                        <button onClick={() => setQty(Math.max(1, qty - 1))} className="w-8 h-8 rounded shrink-0 flex items-center justify-center text-slate-500 hover:bg-white dark:hover:bg-slate-700 shadow-sm transition-colors">
                                            <Minus className="w-3.5 h-3.5" />
                                        </button>
                                        <span className="w-8 text-center font-bold text-sm md:text-base">{qty}</span>
                                        <button onClick={() => setQty(qty + 1)} className="w-8 h-8 rounded shrink-0 flex items-center justify-center text-slate-500 hover:bg-white dark:hover:bg-slate-700 shadow-sm transition-colors">
                                            <Plus className="w-3.5 h-3.5" />
                                        </button>
                                    </div>
                                    <button onClick={addToCart} className="flex-1 h-12 rounded-lg text-white font-bold text-sm flex items-center justify-center gap-2 transition-transform active:scale-95 shadow-md hover:shadow-lg"
                                        style={{ backgroundColor: primaryColor }}>
                                        <ShoppingCart className="w-4 h-4" />
                                        <span>{isAr ? 'أضف' : 'Add'}</span>
                                        <span className="opacity-50">|</span>
                                        <span>{cur} {(((selectedItem.item.prices[sizeIdx] || 0) + selectedExtras.reduce((s, e) => s + e.price, 0)) * qty).toFixed?.(0)}</span>
                                    </button>
                                </div>
                            </div>
                        </motion.div>
                    </motion.div>
                )}
            </AnimatePresence>

            {/* Cart Drawer */}
            <AnimatePresence>
                {isCartOpen && (
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}
                        className="fixed inset-0 z-[300] bg-black/60 backdrop-blur-sm flex justify-end"
                        onClick={() => setIsCartOpen(false)}>
                        <motion.div initial={{ x: isAr ? '100%' : '100%' }} animate={{ x: 0 }} exit={{ x: isAr ? '100%' : '100%' }}
                            transition={{ type: 'spring', damping: 25, stiffness: 200 }}
                            className={`w-full max-w-[420px] h-full flex flex-col shadow-2xl relative bg-white dark:bg-[#1e1e1e]`}
                            onClick={e => e.stopPropagation()}>

                            {/* Cart Header */}
                            <div className="p-4 md:p-5 flex justify-between items-center text-white shadow-md z-10" style={{ backgroundColor: primaryColor }}>
                                <div className="flex items-center gap-3">
                                    <div className="w-10 h-10 rounded-full bg-white/20 flex items-center justify-center">
                                        <ShoppingCart className="w-5 h-5" />
                                    </div>
                                    <div>
                                        <h2 className="font-bold text-lg leading-tight m-0">{isAr ? 'عربة التسوق' : 'Shopping Cart'}</h2>
                                        <p className="text-xs opacity-90 m-0">{cartCount} {isAr ? 'منتجات' : 'items'}</p>
                                    </div>
                                </div>
                                <button onClick={() => setIsCartOpen(false)} className="w-8 h-8 flex items-center justify-center bg-white/20 hover:bg-white/30 rounded-full transition-colors">
                                    <X className="w-4 h-4" />
                                </button>
                            </div>

                            {/* Cart Body */}
                            <div className="flex-1 overflow-y-auto" style={{ backgroundColor: bgBody }}>
                                {cart.length === 0 ? (
                                    <div className="flex flex-col items-center justify-center h-full text-center px-6" style={{ color: textMuted }}>
                                        <div className="w-24 h-24 mb-4 rounded-full bg-slate-200 dark:bg-slate-800 flex items-center justify-center">
                                            <ShoppingCart className="w-10 h-10 opacity-40" />
                                        </div>
                                        <h3 className="font-bold text-xl mb-2 text-[--text-main]" style={{ '--text-main': textMain } as React.CSSProperties}>{isAr ? 'السلة فارغة!' : 'Cart is Empty!'}</h3>
                                        <p className="text-sm opacity-80 mb-6">{isAr ? 'تبدو سلتك فارغة، استكشف قائمتنا وأضف بعض المنتجات الشهية.' : 'Looks like your cart is empty, explore our menu and add some delicious items.'}</p>
                                        <button
                                            onClick={() => setIsCartOpen(false)}
                                            className="px-6 py-3 rounded-full font-bold text-white text-sm"
                                            style={{ backgroundColor: primaryColor }}
                                        >
                                            {isAr ? 'تصفح القائمة' : 'Browse Menu'}
                                        </button>
                                    </div>
                                ) : (
                                    <div className="p-4 space-y-4">
                                        {/* Items */}
                                        <div className="space-y-3">
                                            {cart.map((c, i) => (
                                                <div key={i} className="flex gap-3 p-3 rounded-xl bg-white dark:bg-[#1e1e1e] border shadow-sm" style={{ borderColor }}>
                                                    <div className="w-[70px] h-[70px] shrink-0 bg-slate-100 dark:bg-slate-800 rounded-lg overflow-hidden">
                                                        <img src={c.item.image_url || 'https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=200'} alt="" className="w-full h-full object-cover" />
                                                    </div>
                                                    <div className="flex-1 min-w-0 flex flex-col justify-between pt-1">
                                                        <div className="flex justify-between items-start gap-2">
                                                            <div>
                                                                <h4 className="font-semibold text-sm line-clamp-1" style={{ color: textMain }}>{itemName(c.item)}</h4>
                                                                {(c.sizeLabel || c.notes) && (
                                                                    <p className="text-[10px] mt-1 space-y-0.5 leading-tight" style={{ color: textMuted }}>
                                                                        {c.sizeLabel && <span className="block">{c.sizeLabel}</span>}
                                                                        {c.notes && <span className="block italic">📝 {c.notes}</span>}
                                                                    </p>
                                                                )}
                                                            </div>
                                                            <button onClick={() => updateQty(c.id, c.notes, -c.quantity)} className="text-red-400 hover:text-red-600 transition-colors p-1 shrink-0">
                                                                <Trash2 className="w-3.5 h-3.5" />
                                                            </button>
                                                        </div>

                                                        <div className="flex justify-between items-center mt-2">
                                                            <span className="font-bold text-sm" style={{ color: primaryColor }}>{cur} {(c.price * c.quantity).toFixed?.(0)}</span>
                                                            <div className="flex items-center bg-slate-100 dark:bg-slate-800 rounded px-1 py-0.5 border" style={{ borderColor }}>
                                                                <button onClick={() => updateQty(c.id, c.notes, -1)} className="w-6 h-6 flex items-center justify-center text-slate-500 hover:text-red-500 rounded hover:bg-white dark:hover:bg-slate-700 transition-colors"><Minus className="w-3 h-3" /></button>
                                                                <span className="w-6 text-center text-xs font-bold" style={{ color: textMain }}>{c.quantity}</span>
                                                                <button onClick={() => updateQty(c.id, c.notes, 1)} className="w-6 h-6 flex items-center justify-center text-slate-500 hover:text-[--primary] rounded hover:bg-white dark:hover:bg-slate-700 transition-colors" style={{ '--primary': primaryColor } as React.CSSProperties}><Plus className="w-3 h-3" /></button>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            ))}
                                        </div>

                                        {/* Order Summary Form */}
                                        <div className="bg-white dark:bg-[#1e1e1e] p-4 rounded-xl border shadow-sm" style={{ borderColor }}>
                                            <h4 className="font-bold text-sm mb-4 border-b pb-2 flex items-center gap-2" style={{ color: textMain, borderColor }}>
                                                <MapIcon className="w-4 h-4" style={{ color: primaryColor }} />
                                                {isAr ? 'بيانات التوصيل' : 'Delivery Details'}
                                            </h4>
                                            <div className="space-y-3">
                                                <input type="text" placeholder={isAr ? "الاسم" : "Full Name"} value={customerInfo.name} onChange={e => setCustomerInfo({ ...customerInfo, name: e.target.value })}
                                                    className="w-full border rounded-lg p-3 text-sm outline-none transition-colors border-[--border] focus:border-[--primary]"
                                                    style={{ backgroundColor: bgBody, color: textMain, '--border': borderColor, '--primary': primaryColor } as React.CSSProperties} />
                                                <input type="tel" placeholder={isAr ? "رقم الجوال" : "Phone Number"} value={customerInfo.phone} onChange={e => setCustomerInfo({ ...customerInfo, phone: e.target.value })}
                                                    className="w-full border rounded-lg p-3 text-sm outline-none transition-colors border-[--border] focus:border-[--primary]" dir="ltr"
                                                    style={{ backgroundColor: bgBody, color: textMain, '--border': borderColor, '--primary': primaryColor } as React.CSSProperties} />
                                                <textarea placeholder={isAr ? "العنوان بالتفصيل" : "Full Address"} value={customerInfo.address} onChange={e => setCustomerInfo({ ...customerInfo, address: e.target.value })}
                                                    className="w-full border rounded-lg p-3 text-sm outline-none h-20 resize-none transition-colors border-[--border] focus:border-[--primary]"
                                                    style={{ backgroundColor: bgBody, color: textMain, '--border': borderColor, '--primary': primaryColor } as React.CSSProperties} />
                                            </div>
                                        </div>
                                    </div>
                                )}
                            </div>

                            {/* Cart Footer Total/Checkout */}
                            {cart.length > 0 && (
                                <div className="p-4 md:p-5 shadow-[0_-5px_15px_rgba(0,0,0,0.05)] border-t z-20 bg-white dark:bg-[#1e1e1e]" style={{ borderColor }}>
                                    <div className="flex justify-between items-center mb-4 text-sm font-bold" style={{ color: textMain }}>
                                        <span>{isAr ? 'المجموع الكلي' : 'Total Amount'}</span>
                                        <span className="text-xl md:text-2xl font-black" style={{ color: primaryColor }}>{cur} {cartTotal.toFixed?.(0)}</span>
                                    </div>
                                    <button onClick={checkOutWhatsApp} className="w-full flex justify-center items-center gap-2 h-12 md:h-14 rounded-xl text-white font-bold text-base transition-transform active:scale-95 shadow-md hover:shadow-lg"
                                        style={{ backgroundColor: '#25d366' }}>
                                        <FaWhatsapp className="w-5 h-5 md:w-6 md:h-6" />
                                        {isAr ? 'تأكيد الطلب عبر واتساب' : 'Confirm via WhatsApp'}
                                    </button>
                                </div>
                            )}
                        </motion.div>
                    </motion.div>
                )}
            </AnimatePresence>

            {/* Toast Notification Layer */}
            <AnimatePresence>
                {toastMsg && (
                    <motion.div
                        initial={{ opacity: 0, y: 50, x: '-50%' }}
                        animate={{ opacity: 1, y: 0, x: '-50%' }}
                        exit={{ opacity: 0, y: 20, x: '-50%' }}
                        className="fixed bottom-[100px] left-1/2 z-[400] bg-black/80 backdrop-blur-sm text-white px-5 py-2.5 rounded-full text-xs md:text-sm font-bold shadow-xl whitespace-nowrap border border-white/10"
                    >
                        {toastMsg}
                    </motion.div>
                )}
            </AnimatePresence>

        </div>
    );
}

/* eslint-disable @typescript-eslint/no-explicit-any */
"use client";

import React, { useState, useEffect } from "react";
import Image from "next/image";
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, FreeMode, Pagination } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/free-mode';
import 'swiper/css/pagination';

import {
    ShoppingCart,
    Share2,
    MapPin,
    Clipboard,
    Plus,
    Minus,
    Info,
    X,
    FileText
} from "lucide-react";
import { FaWhatsapp } from 'react-icons/fa';

// --- Local Types ---
type MenuItem = {
    id: string | number;
    title_ar: string;
    title_en?: string;
    name?: string; // some themes use name instead of title_ar
    description_ar?: string;
    description_en?: string;
    description?: string;
    image?: string;
    image_url?: string;
    prices: number[];
    price?: number;
    size_labels?: string[];
    extras?: { id?: number | string; name_ar: string; name_en?: string; price: number }[];
    is_available?: boolean;
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    [key: string]: any;
};

interface CategoryWithItemsType {
    id: string | number;
    name_ar: string;
    name_en?: string;
    name?: string;
    items?: MenuItem[];
    image_url?: string;
    icon?: string;
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    [key: string]: any;
}

interface RestaurantType {
    name: string;
    theme?: string;
    theme_colors?: {
        main_color?: string;
        bg_color?: string;
        text_color?: string;
    };
    cover_images?: string[];
    logo_url?: string;
    cover_url?: string;
    phone?: string;
    currency?: string;
    default_language?: string;
    location_text?: string;
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    [key: string]: any;
}

interface Theme14MenuProps {
    config: RestaurantType;
    categories: CategoryWithItemsType[];
}

export default function Theme14Menu({ config, categories }: Theme14MenuProps) {
    const [mounted, setMounted] = useState(false);
    useEffect(() => setMounted(true), []);

    const language = config?.default_language || 'ar';
    const isRTL = language === "ar" || true; // Force RTL for Arabic themes
    const cur = config?.currency || 'SAR';

    // Custom states
    const [activeCategory, setActiveCategory] = useState<string>("all");
    const [selectedItem, setSelectedItem] = useState<{ item: MenuItem; catName: string; catImg?: string } | null>(null);
    const [toastMessage, setToastMessage] = useState<string | null>(null);
    const [isScrolled, setIsScrolled] = useState(false);
    const [isCartOpen, setIsCartOpen] = useState(false);

    // Checkout & Cart State
    const [customerInfo, setCustomerInfo] = useState({ name: '', phone: '', address: '' });
    const [cart, setCart] = useState<{ id: string, item: MenuItem, catName: string, price: number, sizeLabel: string, quantity: number, notes: string }[]>([]);

    // Item detail state
    const [qty, setQty] = useState(1);
    const [sizeIdx, setSizeIdx] = useState(0);
    const [notes, setNotes] = useState('');
    const [selectedExtras, setSelectedExtras] = useState<{ id: number | string, name: string, price: number }[]>([]);

    useEffect(() => {
        const handleScroll = () => { setIsScrolled(window.scrollY > 100); };
        window.addEventListener("scroll", handleScroll);
        return () => window.removeEventListener("scroll", handleScroll);
    }, []);

    // Theme 14 colors (fallback to typical defaults if not provided in theme_colors)
    const primaryColor = config?.theme_colors?.main_color || "#e67e22";
    const secondaryColor = config?.theme_colors?.text_color || "#2c3e50";
    const backgroundColor = config?.theme_colors?.bg_color || "#f8f9fa";

    const extendedCategories = [{ id: "all", name_ar: "الكل", name_en: "All", icon: "🏠" }, ...categories];
    const allItems = categories.flatMap((c: CategoryWithItemsType) => c.items || []);
    const featuredItems = allItems.slice(0, 5);

    // Helpers
    const itemName = (item: MenuItem) => isRTL ? (item.title_ar || item.name || '') : (item.title_en || item.title_ar || item.name || '');
    const catName = (cat: CategoryWithItemsType) => isRTL ? (cat.name_ar || cat.name || '') : (cat.name_en || cat.name_ar || cat.name || '');
    const itemPrice = (item: MenuItem, idx: number = 0) => item.prices?.[idx] || item.price || 0;

    const showToast = (msg: string) => {
        setToastMessage(msg);
        setTimeout(() => setToastMessage(null), 2500);
    };

    const copyToClipboard = (text: string, e: React.MouseEvent) => {
        e.stopPropagation();
        navigator.clipboard.writeText(text);
        showToast(isRTL ? "تم نسخ محتوى القائمة إلى الحافظة!" : "Copied to clipboard!");
    };

    const handleShareWhatsApp = () => {
        const text = isRTL
            ? `🔖 ${config?.name || "المطعم"}\n\nتصفح القائمة:\n${window.location.href}`
            : `🔖 ${config?.name || "Restaurant"}\n\nBrowse menu:\n${window.location.href}`;
        window.open(`https://wa.me/?text=${encodeURIComponent(text)}`, '_blank');
    };

    const openModal = (item: MenuItem, cName: string, cImg?: string) => {
        setSelectedItem({ item, catName: cName, catImg: cImg });
        setQty(1);
        setSizeIdx(0);
        setSelectedExtras([]);
        setNotes('');
        document.body.style.overflow = 'hidden';
    };

    const closeModal = () => {
        setSelectedItem(null);
        document.body.style.overflow = 'auto';
    };

    const addToCart = () => {
        if (!selectedItem) return;
        const iP = itemPrice(selectedItem.item, sizeIdx);
        const extP = selectedExtras.reduce((sum, e) => sum + e.price, 0);
        const finalPrice = iP + extP;

        let sizeLbl = selectedItem.item.size_labels?.[sizeIdx] || '';
        if (selectedExtras.length > 0) {
            const extNames = selectedExtras.map(e => e.name).join(' + ');
            sizeLbl = sizeLbl ? `${sizeLbl} | +${extNames}` : `+${extNames}`;
        }

        const cId = `${selectedItem.item.id}-${sizeIdx}-${selectedExtras.map(e => e.id).sort().join('-')}`;

        setCart(prev => {
            const ex = prev.find(c => c.id === cId && c.notes === notes);
            if (ex) return prev.map(c => c.id === cId && c.notes === notes ? { ...c, quantity: c.quantity + qty } : c);
            return [...prev, {
                id: cId,
                item: selectedItem.item,
                catName: selectedItem.catName,
                price: finalPrice,
                sizeLabel: sizeLbl,
                quantity: qty,
                notes
            }];
        });
        closeModal();
        showToast(isRTL ? 'تمت الإضافة إلى السلة بنجاح' : 'Added to cart successfully');
    };

    const updateQty = (id: string, itemNotes: string, delta: number) => {
        setCart(prev => prev.map(c => {
            if (c.id === id && c.notes === itemNotes) {
                const n = c.quantity + delta;
                return n > 0 ? { ...c, quantity: n } : c;
            }
            return c;
        }).filter(c => c.quantity > 0));
    };

    const cartCount = cart.reduce((acc, curr) => acc + curr.quantity, 0);
    const cartTotal = cart.reduce((acc, curr) => acc + (curr.price * curr.quantity), 0);

    const checkOutWhatsApp = () => {
        if (!config.phone) {
            alert(isRTL ? 'رقم المطعم غير متوفر' : 'Restaurant phone unavailable');
            return;
        }
        if (!customerInfo.name || !customerInfo.phone || !customerInfo.address) {
            alert(isRTL ? 'يرجى إكمال بيانات التوصيل' : 'Please complete delivery details');
            return;
        }

        let txt = isRTL ? `*طلب جديد من ${customerInfo.name}*%0A` : `*New Order from ${customerInfo.name}*%0A`;
        txt += isRTL ? `رقم الهاتف: ${customerInfo.phone}%0A` : `Phone: ${customerInfo.phone}%0A`;
        txt += isRTL ? `العنوان: ${customerInfo.address}%0A%0A` : `Address: ${customerInfo.address}%0A%0A`;

        cart.forEach(c => {
            txt += `▪ ${itemName(c.item)} (${c.quantity}x)\n`;
            if (c.sizeLabel) txt += `   ${c.sizeLabel}\n`;
            if (c.notes) txt += `   📝 ${c.notes}\n`;
            txt += `   ${cur}${(c.price * c.quantity).toFixed?.(0) || c.price * c.quantity}\n\n`;
        });

        txt += isRTL ? `*الإجمالي: ${cur}${cartTotal.toFixed?.(0) || cartTotal}*` : `*Total: ${cur}${cartTotal.toFixed?.(0) || cartTotal}*`;

        const tel = config.phone.replace(/\D/g, '');
        window.open(`https://wa.me/${tel}?text=${encodeURIComponent(txt)}`, '_blank');
    };

    const filteredItems = activeCategory === "all"
        ? allItems
        : categories.find((c: CategoryWithItemsType) => c.id.toString() === activeCategory)?.items || [];

    if (!mounted) return <div className="min-h-screen bg-slate-50" />;

    return (
        <div dir={isRTL ? "rtl" : "ltr"} className="theme14-container relative">
            <style jsx>{`
        .theme14-container {
            --primary-color: ${primaryColor};
            --secondary-color: ${secondaryColor};
            --background-color: ${backgroundColor};
            background-color: var(--background-color);
            min-height: 100vh;
            font-family: inherit;
            padding-bottom: 80px;
        }

        .theme14-container * {
            box-sizing: border-box;
        }

        .modern-header {
            position: relative;
            height: 250px;
            background-size: cover;
            background-position: center;
            border-bottom-left-radius: 30px;
            border-bottom-right-radius: 30px;
            overflow: hidden;
            box-shadow: 0 4px 15px rgba(0,0,0,0.1);
        }

        .header-overlay {
            position: absolute;
            top: 0; left: 0; right: 0; bottom: 0;
            background: linear-gradient(to bottom, rgba(0,0,0,0.2) 0%, rgba(0,0,0,0.8) 100%);
        }

        .header-content {
            position: absolute;
            bottom: 20px;
            left: 20px;
            right: 20px;
            display: flex;
            align-items: flex-end;
            gap: 15px;
            z-index: 2;
        }

        .store-logo-wrapper {
            width: 80px;
            height: 80px;
            border-radius: 20px;
            background: white;
            padding: 5px;
            box-shadow: 0 4px 15px rgba(0,0,0,0.2);
            flex-shrink: 0;
            position: relative;
            overflow: hidden;
        }

        .store-logo-wrapper img {
            width: 100%;
            height: 100%;
            object-fit: contain;
            border-radius: 15px;
        }

        .store-info {
            color: white;
            flex-grow: 1;
        }

        .store-title {
            font-size: 24px;
            font-weight: 800;
            margin: 0 0 5px 0;
            text-shadow: 0 2px 4px rgba(0,0,0,0.3);
        }

        .badge {
            background: rgba(255,255,255,0.2);
            backdrop-filter: blur(5px);
            padding: 4px 10px;
            border-radius: 15px;
            font-size: 12px;
            display: flex;
            align-items: center;
            gap: 5px;
            width: fit-content;
        }

        .categories-filter {
            padding: 20px;
            position: sticky;
            top: 0;
            z-index: 100;
            background: rgba(248, 249, 250, 0.95);
            backdrop-filter: blur(10px);
            transition: all 0.3s ease;
            margin-top: -10px;
        }

        .categories-filter.floating {
            box-shadow: 0 4px 15px rgba(0,0,0,0.05);
            padding: 10px 20px;
        }

        .category-pill {
            display: flex;
            flex-direction: column;
            align-items: center;
            gap: 8px;
            cursor: pointer;
            transition: all 0.3s ease;
            opacity: 0.7;
            border: none;
            background: transparent;
            width: 100%;
        }

        .category-pill.active {
            opacity: 1;
            transform: translateY(-2px);
        }

        .category-icon-wrapper {
            width: 60px;
            height: 60px;
            border-radius: 50%;
            background: white;
            display: flex;
            align-items: center;
            justify-content: center;
            box-shadow: 0 4px 10px rgba(0,0,0,0.05);
            font-size: 24px;
            color: var(--primary-color);
            transition: all 0.3s ease;
            border: 2px solid transparent;
        }

        .category-pill.active .category-icon-wrapper {
            border-color: var(--primary-color);
            background: var(--primary-color);
            color: white;
            box-shadow: 0 4px 15px rgba(var(--primary-color-rgb), 0.3);
        }

        .category-name {
            font-size: 13px;
            font-weight: 600;
            color: var(--secondary-color);
            text-align: center;
            white-space: nowrap;
        }

        .section-title {
            padding: 0 20px;
            margin: 20px 0 15px;
            font-size: 20px;
            font-weight: 800;
            color: var(--secondary-color);
            display: flex;
            align-items: center;
            gap: 10px;
        }

        .featured-item-card {
            background: white;
            border-radius: 20px;
            overflow: hidden;
            box-shadow: 0 4px 15px rgba(0,0,0,0.05);
            height: 100%;
            display: flex;
            flex-direction: column;
            cursor: pointer;
        }

        .featured-image-container {
            position: relative;
            height: 150px;
            width: 100%;
        }
        
        .featured-image-container img {
            width: 100%;
            height: 100%;
            object-fit: cover;
        }

        .featured-badge {
            position: absolute;
            top: 10px;
            right: 10px;
            background: var(--primary-color);
            color: white;
            padding: 4px 12px;
            border-radius: 15px;
            font-size: 12px;
            font-weight: bold;
            display: flex;
            align-items: center;
            gap: 5px;
            z-index: 10;
        }

        .featured-content {
            padding: 15px;
            flex-grow: 1;
            display: flex;
            flex-direction: column;
            justify-content: space-between;
        }

        .featured-title {
            font-size: 16px;
            font-weight: 700;
            margin: 0 0 5px;
            color: var(--secondary-color);
        }

        .featured-desc {
            font-size: 13px;
            color: #666;
            margin: 0 0 10px;
            display: -webkit-box;
            -webkit-line-clamp: 2;
            -webkit-box-orient: vertical;
            overflow: hidden;
        }

        .price-tag {
            font-size: 18px;
            font-weight: 800;
            color: var(--primary-color);
        }

        .add-btn {
            background: var(--primary-color);
            color: white;
            border: none;
            width: 35px;
            height: 35px;
            border-radius: 10px;
            display: flex;
            align-items: center;
            justify-content: center;
            cursor: pointer;
            transition: all 0.2s ease;
        }

        .menu-container {
            padding: 0 15px 15px;
        }

        .category-title {
            font-size: 18px;
            font-weight: 700;
            color: var(--secondary-color);
            margin: 20px 0 15px;
            display: flex;
            align-items: center;
            gap: 8px;
        }

        .menu-item {
            background: white;
            border-radius: 20px;
            margin-bottom: 15px;
            display: flex;
            overflow: hidden;
            box-shadow: 0 4px 10px rgba(0,0,0,0.03);
            border: 1px solid rgba(0,0,0,0.02);
            transition: all 0.3s ease;
            cursor: pointer;
        }

        .menu-item-content {
            flex-grow: 1;
            padding: 15px;
            display: flex;
            align-items: center;
            gap: 15px;
        }

        .item-actions {
            display: flex;
            flex-direction: column;
            gap: 8px;
        }

        .action-btn {
            width: 35px;
            height: 35px;
            border-radius: 10px;
            border: none;
            display: flex;
            align-items: center;
            justify-content: center;
            cursor: pointer;
        }

        .cart-btn {
            background: rgba(230, 126, 34, 0.1);
            color: var(--primary-color);
        }

        .copy-btn {
            background: rgba(44, 62, 80, 0.05);
            color: var(--secondary-color);
        }

        .item-text {
            flex-grow: 1;
        }

        .item-name {
            font-size: 16px;
            font-weight: 700;
            margin: 0 0 5px;
            color: var(--secondary-color);
        }

        .item-desc {
            font-size: 13px;
            color: #666;
            margin: 0;
            display: -webkit-box;
            -webkit-line-clamp: 2;
            -webkit-box-orient: vertical;
            overflow: hidden;
        }

        .item-price {
            font-weight: 800;
            color: var(--primary-color);
            font-size: 16px;
        }

        .menu-item-image-container {
            width: 120px;
            height: 120px;
            flex-shrink: 0;
            padding: 10px;
        }

        .menu-item-image {
            width: 100%;
            height: 100%;
            object-fit: cover;
            border-radius: 15px;
        }

        .bottom-nav {
            position: fixed;
            bottom: 20px;
            left: 20px;
            right: 20px;
            background: white;
            border-radius: 20px;
            display: flex;
            justify-content: space-around;
            padding: 10px 5px;
            box-shadow: 0 10px 30px rgba(0,0,0,0.1);
            z-index: 100;
        }

        .nav-item {
            display: flex;
            flex-direction: column;
            align-items: center;
            gap: 5px;
            color: var(--secondary-color);
            font-size: 12px;
            font-weight: 600;
            opacity: 0.7;
            cursor: pointer;
            position: relative;
        }
        
        .nav-item.active {
            opacity: 1;
            color: var(--primary-color);
        }

        .cart-badge {
            position: absolute;
            top: -5px;
            right: 5px;
            background: #e74c3c;
            color: white;
            width: 18px;
            height: 18px;
            border-radius: 50%;
            font-size: 10px;
            display: flex;
            align-items: center;
            justify-content: center;
            font-weight: bold;
        }

        .toast {
            position: fixed;
            bottom: 100px;
            left: 50%;
            transform: translateX(-50%) translateY(20px);
            background: rgba(44, 62, 80, 0.9);
            color: white;
            padding: 10px 20px;
            border-radius: 30px;
            font-size: 14px;
            opacity: 0;
            visibility: hidden;
            transition: all 0.3s ease;
            z-index: 2000;
            backdrop-filter: blur(5px);
            white-space: nowrap;
        }

        .toast.show {
            opacity: 1;
            visibility: visible;
            transform: translateX(-50%) translateY(0);
        }

        .fab-whatsapp {
            position: fixed;
            bottom: 100px;
            z-index: 90;
            width: 50px;
            height: 50px;
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
            color: white;
            background: linear-gradient(135deg, #25D366, #128C7E);
            box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
            cursor: pointer;
        }

        /* Cart overlay styling */
        .cart-overlay {
            position: fixed;
            top: 0; left: 0; right: 0; bottom: 0;
            background: rgba(0,0,0,0.6);
            backdrop-filter: blur(5px);
            z-index: 1050;
            display: flex;
            justify-content: ${isRTL ? "flex-start" : "flex-end"};
        }
      `}</style>

            <div className={`toast ${toastMessage ? 'show' : ''}`}>
                {toastMessage}
            </div>

            <div
                className="fab-whatsapp"
                onClick={handleShareWhatsApp}
                style={{ [isRTL ? "left" : "right"]: "15px" }}
            >
                <Share2 size={24} />
            </div>

            {/* Header */}
            <div
                className="modern-header"
                style={{ backgroundImage: `url(${config?.cover_url || config?.cover_images?.[0] || '/placeholder-cover.jpg'})` }}
            >
                <div className="header-overlay"></div>
                <div className="header-content">
                    <div className="store-logo-wrapper">
                        <Image
                            src={config?.logo_url || '/placeholder-logo.png'}
                            alt={config?.name || "Store Logo"}
                            fill
                        />
                    </div>
                    <div className="store-info">
                        <h1 className="store-title">{config?.name || "Restaurant Name"}</h1>
                        {config?.location_text && (
                            <div className="badge">
                                <MapPin size={12} />
                                <span>{config.location_text}</span>
                            </div>
                        )}
                    </div>
                </div>
            </div>

            {/* Categories Filter */}
            <div className={`categories-filter ${isScrolled ? 'floating' : ''}`}>
                <Swiper
                    slidesPerView="auto"
                    spaceBetween={10}
                    freeMode={true}
                    modules={[FreeMode]}
                    className="categories-swiper"
                    dir={isRTL ? "rtl" : "ltr"}
                >
                    {extendedCategories.map((cat: any) => (
                        <SwiperSlide key={cat.id || cat.name_en || cat.name_ar} style={{ width: '80px' }}>
                            <button
                                className={`category-pill ${activeCategory === cat.id.toString() ? 'active' : ''}`}
                                onClick={() => setActiveCategory(cat.id.toString())}
                            >
                                <div className="category-icon-wrapper">
                                    {cat.image_url ? (
                                        <img src={cat.image_url} alt={catName(cat)} style={{ width: '100%', height: '100%', objectFit: 'cover', borderRadius: '50%' }} />
                                    ) : (cat.icon || "🍽️")}
                                </div>
                                <span className="category-name">{catName(cat)}</span>
                            </button>
                        </SwiperSlide>
                    ))}
                </Swiper>
            </div>

            {/* Featured Swiper */}
            {activeCategory === "all" && featuredItems.length > 0 && (
                <div className="featured-section">
                    <h2 className="section-title">
                        <span style={{ fontSize: '24px' }}>🔥</span>
                        {isRTL ? "الأكثر مبيعاً" : "Featured Items"}
                    </h2>
                    <div style={{ padding: '0 0 20px 0' }}>
                        <Swiper
                            slidesPerView={1.2}
                            spaceBetween={20}
                            centeredSlides={true}
                            loop={featuredItems.length > 1}
                            autoplay={{ delay: 3000, disableOnInteraction: false }}
                            pagination={{ clickable: true }}
                            modules={[Autoplay, Pagination]}
                            className="featured-swiper"
                            breakpoints={{ 768: { slidesPerView: 2, spaceBetween: 30 } }}
                        >
                            {featuredItems.map((item: MenuItem, idx: number) => (
                                <SwiperSlide key={`featured-${item.id || idx}`}>
                                    <div className="featured-item-card" onClick={() => openModal(item, "Featured", item.image_url)}>
                                        <div className="featured-image-container">
                                            <Image
                                                src={item.image_url || '/placeholder-item.jpg'}
                                                alt={itemName(item)}
                                                fill
                                            />
                                            <div className="featured-badge">
                                                <span>★</span> Top
                                            </div>
                                        </div>
                                        <div className="featured-content">
                                            <div>
                                                <h3 className="featured-title">{itemName(item)}</h3>
                                                <p className="featured-desc">
                                                    {isRTL ? item.description_ar : (item.description_en || item.description_ar || item.description)}
                                                </p>
                                            </div>
                                            <div className="flex justify-between items-center mt-auto pt-2">
                                                <span className="price-tag">{cur} {itemPrice(item).toFixed?.(0) || itemPrice(item)}</span>
                                                <button className="add-btn" onClick={(e) => { e.stopPropagation(); openModal(item, "Featured", item.image_url); }}>
                                                    <Plus size={20} />
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                </SwiperSlide>
                            ))}
                        </Swiper>
                    </div>
                </div>
            )}

            {/* Main Menu Grid / List */}
            <div className="menu-container">
                {activeCategory === "all" ? (
                    categories.map((cat: CategoryWithItemsType, i: number) => (
                        <div key={cat.id || i}>
                            <h3 className="category-title">
                                <span>{cat.icon || "📁"}</span>
                                {catName(cat)}
                            </h3>
                            {cat.items?.map((item: MenuItem, idx: number) => (
                                <MenuItemCard
                                    key={item.id || idx}
                                    item={item}
                                    currency={cur}
                                    itemName={itemName(item)}
                                    itemDesc={isRTL ? (item.description_ar || '') : (item.description_en || item.description_ar || item.description || '')}
                                    itemPrice={itemPrice(item)}
                                    onAdd={(e: any) => { e.stopPropagation(); openModal(item, catName(cat), cat.image_url); }}
                                    onCopy={(e: any) => copyToClipboard(`${itemName(item)}\n${itemPrice(item)} ${cur}`, e)}
                                    onClick={() => openModal(item, catName(cat), cat.image_url)}
                                />
                            ))}
                        </div>
                    ))
                ) : (
                    <div>
                        <h3 className="category-title">
                            {categories.find((c: CategoryWithItemsType) => c.id.toString() === activeCategory) && catName(categories.find((c: CategoryWithItemsType) => c.id.toString() === activeCategory)!)}
                        </h3>
                        {filteredItems.map((item: MenuItem, idx: number) => (
                            <MenuItemCard
                                key={item.id || idx}
                                item={item}
                                currency={cur}
                                itemName={itemName(item)}
                                itemDesc={isRTL ? (item.description_ar || '') : (item.description_en || item.description_ar || item.description || '')}
                                itemPrice={itemPrice(item)}
                                onAdd={(e: any) => { e.stopPropagation(); openModal(item, "Category", ""); }}
                                onCopy={(e: any) => copyToClipboard(`${itemName(item)}\n${itemPrice(item)} ${cur}`, e)}
                                onClick={() => openModal(item, "Category", "")}
                            />
                        ))}
                    </div>
                )}
            </div>

            {/* Bottom Nav Dock */}
            <div className="bottom-nav" dir={isRTL ? "rtl" : "ltr"}>
                <div className="nav-item">
                    <Info size={24} />
                    <span>{isRTL ? "معلومات" : "Info"}</span>
                </div>
                <div className="nav-item active">
                    <ShoppingCart size={24} />
                    <span>{isRTL ? "القائمة" : "Menu"}</span>
                </div>
                <div className="nav-item " onClick={() => setIsCartOpen(true)}>
                    <div style={{ position: 'relative' }}>
                        <ShoppingCart size={24} />
                        {cartCount > 0 && (
                            <span className="cart-badge">
                                {cartCount}
                            </span>
                        )}
                    </div>
                    <span>{isRTL ? "عربة التسوق" : "Cart"}</span>
                </div>
            </div>

            {/* Item Details Modal */}
            {selectedItem && (
                <div className="fixed inset-0 z-[1050] flex items-end justify-center bg-black/50 backdrop-blur-sm" onClick={closeModal} style={{ animation: "fadeIn 0.2s" }}>
                    <div className="bg-white w-full max-w-[600px] max-h-[90vh] overflow-y-auto relative pb-8 rounded-t-[30px]" onClick={e => e.stopPropagation()} style={{ animation: "slideUp 0.3s ease-out" }}>
                        <style jsx>{`
                    @keyframes slideUp { from { transform: translateY(100%); } to { transform: translateY(0); } }
                    @keyframes fadeIn { from { opacity: 0; } to { opacity: 1; } }
                `}</style>
                        <div className="w-[40px] h-[5px] bg-gray-200 rounded-[5px] mx-auto my-[15px]"></div>
                        <button className="absolute top-[20px] right-[20px] bg-white/90 text-[var(--secondary-color)] w-[40px] h-[40px] rounded-full flex items-center justify-center shadow-md z-10" onClick={closeModal}>
                            <X size={20} />
                        </button>
                        <div className="w-full h-[250px] relative">
                            <img src={selectedItem.item.image_url || selectedItem.catImg || '/placeholder-item.jpg'} alt={itemName(selectedItem.item)} className="w-full h-full object-cover" />
                        </div>
                        <div className="p-5">
                            <h2 className="text-[24px] font-[800] text-[var(--secondary-color)] mb-2">{itemName(selectedItem.item)}</h2>
                            <p className="text-[15px] mb-5 text-gray-500">
                                {isRTL ? selectedItem.item.description_ar : (selectedItem.item.description_en || selectedItem.item.description_ar || selectedItem.item.description)}
                            </p>
                            <div className="text-[22px] font-[800] text-[var(--primary-color)] mb-5">{cur} {itemPrice(selectedItem.item, sizeIdx).toFixed?.(0) || itemPrice(selectedItem.item, sizeIdx)}</div>

                            {/* Sizes */}
                            {selectedItem.item.prices?.length > 1 && (
                                <div className="mb-5">
                                    <h3 className="font-bold text-sm mb-3 text-gray-600">{isRTL ? 'اختر الحجم' : 'Select Size'}</h3>
                                    <div className="grid grid-cols-2 gap-3">
                                        {selectedItem.item.prices.map((p, idx) => {
                                            const label = selectedItem.item.size_labels?.[idx] || (isRTL ? `حجم ${idx + 1}` : `Size ${idx + 1}`);
                                            return (
                                                <label key={idx} className="flex flex-col items-center justify-center p-3 rounded-xl border-2 cursor-pointer transition-all text-center relative overflow-hidden"
                                                    style={{ borderColor: sizeIdx === idx ? primaryColor : "#eee", backgroundColor: sizeIdx === idx ? `${primaryColor}10` : 'transparent' }}
                                                    onClick={() => setSizeIdx(idx)}>
                                                    <span className="font-bold text-sm mb-1">{label}</span>
                                                    <span className="text-xs font-medium" style={{ color: primaryColor }}>{cur} {p}</span>
                                                </label>
                                            );
                                        })}
                                    </div>
                                </div>
                            )}

                            {/* Extras */}
                            {selectedItem.item.extras && selectedItem.item.extras.length > 0 && (
                                <div className="mb-5">
                                    <h3 className="font-bold text-sm mb-3 text-gray-600">{isRTL ? 'إضافات (اختياري)' : 'Extras (Optional)'}</h3>
                                    <div className="space-y-2">
                                        {selectedItem.item.extras.map((ext, idx) => {
                                            const id = ext.id || idx;
                                            const isSel = selectedExtras.some(e => e.id === id);
                                            return (
                                                <label key={idx} className="flex items-center justify-between p-3 rounded-xl border cursor-pointer transition-colors"
                                                    style={{ borderColor: "#eee", backgroundColor: isSel ? `${primaryColor}05` : 'transparent' }}>
                                                    <input type="checkbox" className="hidden" checked={isSel}
                                                        onChange={() => {
                                                            if (isSel) setSelectedExtras(p => p.filter(e => e.id !== id));
                                                            else setSelectedExtras(p => [...p, { id: id as any, name: isRTL ? ext.name_ar : (ext.name_en || ext.name_ar), price: ext.price }]);
                                                        }} />
                                                    <div className="flex items-center gap-3">
                                                        <div className="w-5 h-5 rounded border flex items-center justify-center transition-colors"
                                                            style={{ borderColor: isSel ? primaryColor : "#ddd", backgroundColor: isSel ? primaryColor : 'transparent' }}>
                                                            {isSel && <Plus className="w-3 h-3 text-white" style={{ transform: 'rotate(45deg)' }} />}
                                                        </div>
                                                        <span className="text-sm font-semibold">{isRTL ? ext.name_ar : (ext.name_en || ext.name_ar)}</span>
                                                    </div>
                                                    <span className="text-xs font-bold" style={{ color: primaryColor }}>{ext.price > 0 ? `+${cur} ${ext.price}` : (isRTL ? 'مجاناً' : 'Free')}</span>
                                                </label>
                                            );
                                        })}
                                    </div>
                                </div>
                            )}

                            {/* Notes */}
                            <div className="mb-6">
                                <h3 className="font-bold text-sm mb-2 flex items-center gap-2 text-gray-600">
                                    <FileText className="w-4 h-4" />
                                    {isRTL ? 'ملاحظات إضافية' : 'Special Instructions'}
                                </h3>
                                <textarea value={notes} onChange={e => setNotes(e.target.value)}
                                    placeholder={isRTL ? 'مثال: بدون بصل، زيادة صوص...' : 'e.g., no onions, extra sauce...'}
                                    className="w-full rounded-xl p-3 outline-none resize-none h-24 text-sm border focus:border-[var(--primary-color)] transition-all bg-gray-50 border-gray-200" />
                            </div>

                            <div className="flex gap-4 items-center mt-6">
                                <div className="flex items-center bg-gray-100 rounded-lg h-14 px-2">
                                    <button onClick={() => setQty(Math.max(1, qty - 1))} className="w-10 h-10 rounded shrink-0 flex items-center justify-center text-gray-500 hover:bg-white shadow-sm transition-colors">
                                        <Minus className="w-4 h-4" />
                                    </button>
                                    <span className="w-10 text-center font-bold text-lg">{qty}</span>
                                    <button onClick={() => setQty(qty + 1)} className="w-10 h-10 rounded shrink-0 flex items-center justify-center text-gray-500 hover:bg-white shadow-sm transition-colors">
                                        <Plus className="w-4 h-4" />
                                    </button>
                                </div>
                                <button
                                    className="flex-1 rounded-[15px] h-14 text-white font-[bold] text-[18px] flex justify-center items-center gap-[10px] cursor-pointer"
                                    style={{ background: primaryColor }}
                                    onClick={addToCart}
                                >
                                    <ShoppingCart size={20} />
                                    {isRTL ? "أضف بـ" : "Add for"} {cur} {(((itemPrice(selectedItem.item, sizeIdx)) + selectedExtras.reduce((s, e) => s + e.price, 0)) * qty).toFixed?.(0)}
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Cart Drawer */}
            {isCartOpen && (
                <div className="cart-overlay" onClick={() => setIsCartOpen(false)}>
                    <div
                        className="w-full max-w-[420px] h-full flex flex-col shadow-2xl relative bg-white overflow-hidden"
                        style={{ animation: isRTL ? "slideInFromRight 0.3s ease-out" : "slideInFromLeft 0.3s ease-out" }}
                        onClick={e => e.stopPropagation()}
                    >
                        <style jsx>{`
                    @keyframes slideInFromRight { from { transform: translateX(100%); } to { transform: translateX(0); } }
                    @keyframes slideInFromLeft { from { transform: translateX(-100%); } to { transform: translateX(0); } }
                  `}</style>

                        <div className="p-5 flex justify-between items-center text-white shadow-md z-10" style={{ backgroundColor: primaryColor }}>
                            <div className="flex items-center gap-3">
                                <ShoppingCart className="w-6 h-6" />
                                <div>
                                    <h2 className="font-bold text-lg leading-tight m-0">{isRTL ? 'عربة التسوق' : 'Shopping Cart'}</h2>
                                    <p className="text-xs opacity-90 m-0">{cartCount} {isRTL ? 'منتجات' : 'items'}</p>
                                </div>
                            </div>
                            <button onClick={() => setIsCartOpen(false)} className="bg-white/20 p-2 rounded-full"><X className="w-5 h-5" /></button>
                        </div>

                        <div className="flex-1 overflow-y-auto bg-gray-50 p-4 space-y-4">
                            {cart.length === 0 ? (
                                <div className="text-center py-20 text-gray-500">
                                    <h3 className="font-bold text-xl mb-2">{isRTL ? 'السلة فارغة!' : 'Cart is Empty!'}</h3>
                                </div>
                            ) : (
                                <>
                                    <div className="space-y-3">
                                        {cart.map((c, i) => (
                                            <div key={i} className="flex gap-3 p-3 rounded-xl bg-white border border-gray-100 shadow-sm">
                                                <div className="w-[70px] h-[70px] shrink-0 bg-gray-100 rounded-lg overflow-hidden">
                                                    <img src={c.item.image_url || '/placeholder-item.jpg'} alt="" className="w-full h-full object-cover" />
                                                </div>
                                                <div className="flex-1 min-w-0 flex flex-col justify-between pt-1">
                                                    <div className="flex justify-between items-start gap-2">
                                                        <div>
                                                            <h4 className="font-semibold text-sm line-clamp-1">{itemName(c.item)}</h4>
                                                            {(c.sizeLabel || c.notes) && (
                                                                <p className="text-[10px] mt-1 space-y-0.5 leading-tight text-gray-500">
                                                                    {c.sizeLabel && <span className="block">{c.sizeLabel}</span>}
                                                                    {c.notes && <span className="block italic">📝 {c.notes}</span>}
                                                                </p>
                                                            )}
                                                        </div>
                                                        <button onClick={() => updateQty(c.id, c.notes, -c.quantity)} className="text-red-400 p-1 shrink-0"><X className="w-4 h-4" /></button>
                                                    </div>
                                                    <div className="flex justify-between items-center mt-2">
                                                        <span className="font-bold text-sm" style={{ color: primaryColor }}>{cur} {(c.price * c.quantity).toFixed?.(0)}</span>
                                                        <div className="flex items-center bg-gray-100 rounded px-1 py-0.5 border">
                                                            <button onClick={() => updateQty(c.id, c.notes, -1)} className="w-6 h-6 flex justify-center items-center"><Minus className="w-3 h-3" /></button>
                                                            <span className="w-6 text-center text-xs font-bold">{c.quantity}</span>
                                                            <button onClick={() => updateQty(c.id, c.notes, 1)} className="w-6 h-6 flex justify-center items-center"><Plus className="w-3 h-3" /></button>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        ))}
                                    </div>

                                    <div className="bg-white p-4 rounded-xl border border-gray-100 shadow-sm">
                                        <h4 className="font-bold text-sm mb-4 border-b pb-2 text-gray-800">{isRTL ? 'بيانات التوصيل' : 'Delivery Details'}</h4>
                                        <div className="space-y-3">
                                            <input type="text" placeholder={isRTL ? "الاسم" : "Full Name"} value={customerInfo.name} onChange={e => setCustomerInfo({ ...customerInfo, name: e.target.value })} className="w-full border rounded-lg p-3 text-sm outline-none bg-gray-50 border-gray-200" />
                                            <input type="tel" placeholder={isRTL ? "رقم الجوال" : "Phone Number"} value={customerInfo.phone} onChange={e => setCustomerInfo({ ...customerInfo, phone: e.target.value })} className="w-full border rounded-lg p-3 text-sm outline-none bg-gray-50 border-gray-200" dir="ltr" />
                                            <textarea placeholder={isRTL ? "العنوان بالتفصيل" : "Full Address"} value={customerInfo.address} onChange={e => setCustomerInfo({ ...customerInfo, address: e.target.value })} className="w-full border rounded-lg p-3 text-sm outline-none h-20 resize-none bg-gray-50 border-gray-200" />
                                        </div>
                                    </div>
                                </>
                            )}
                        </div>

                        {cart.length > 0 && (
                            <div className="p-5 shadow-[0_-5px_15px_rgba(0,0,0,0.05)] border-t z-20 bg-white">
                                <div className="flex justify-between items-center mb-4 text-sm font-bold text-gray-800">
                                    <span>{isRTL ? 'المجموع الكلي' : 'Total Amount'}</span>
                                    <span className="text-2xl font-black" style={{ color: primaryColor }}>{cur} {cartTotal.toFixed?.(0)}</span>
                                </div>
                                <button onClick={checkOutWhatsApp} className="w-full flex justify-center items-center gap-2 h-14 rounded-xl text-white font-bold text-base shadow-md" style={{ backgroundColor: '#25D366' }}>
                                    <FaWhatsapp className="w-6 h-6" />
                                    {isRTL ? 'تأكيد الطلب عبر واتساب' : 'Confirm via WhatsApp'}
                                </button>
                            </div>
                        )}
                    </div>
                </div>
            )}

        </div>
    );
}

function MenuItemCard({ item, currency, itemName, itemDesc, itemPrice, onAdd, onCopy, onClick }: { item: any, currency: string, itemName: string, itemDesc: string, itemPrice: number, onAdd: any, onCopy: any, onClick: any }) {
    return (
        <div className="menu-item" onClick={onClick}>
            <div className="menu-item-content">
                <div className="item-actions">
                    <button className="action-btn copy-btn" onClick={onCopy}>
                        <Clipboard size={16} />
                    </button>
                    <button className="action-btn cart-btn" onClick={onAdd}>
                        <Plus size={16} />
                    </button>
                </div>
                <div className="item-text">
                    <h3 className="item-name">{itemName}</h3>
                    <p className="item-desc">{itemDesc}</p>
                    <span className="item-price">{currency} {itemPrice.toFixed?.(0) || itemPrice}</span>
                </div>
            </div>
            <div className="menu-item-image-container">
                <Image src={item.image_url || '/placeholder-item.jpg'} alt={itemName} width={120} height={120} className="menu-item-image" />
            </div>
        </div>
    );
}

"use client";
import React, { useState, useEffect, useRef } from 'react';
import { useTheme } from 'next-themes';
import { motion, AnimatePresence } from 'framer-motion';
import { Search, Share2, Bike, X, Moon, Sun, ShoppingBag, Plus as PlusIcon, Minus, LayoutGrid } from 'lucide-react';
import { FaWhatsapp, FaFacebook } from 'react-icons/fa';
import SharedMarquee from './SharedMarquee';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectFade } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/effect-fade';

type Item = {
    id: string;
    title_ar: string;
    title_en?: string;
    desc_ar?: string;
    desc_en?: string;
    prices: number[];
    size_labels: string[];
    is_popular: boolean;
    is_spicy: boolean;
    image_url?: string;
    is_available: boolean;
};

type Category = {
    id: string;
    name_ar: string;
    name_en?: string;
    emoji?: string;
    image_url?: string;
    items: Item[];
};

type RestaurantConfig = {
    name: string;
    theme: string;
    phone?: string;
    whatsapp_number?: string;
    facebook_url?: string;
    instagram_url?: string;
    tiktok_url?: string;
    map_link?: string;
    logo_url?: string;
    cover_url?: string;
    cover_images?: string[];
    marquee_enabled?: boolean;
    marquee_text_ar?: string;
    marquee_text_en?: string;
    phone_numbers?: { label: string; number: string }[];
    payment_methods?: {
        id: string;
        name_ar: string;
        name_en: string;
        desc_ar: string;
        desc_en: string;
        number?: string;
        link?: string;
    }[];
};

interface CartItem {
    id: string;
    item: Item;
    price: number;
    size_label: string;
    quantity: number;
    notes?: string;
}

type Props = {
    config: RestaurantConfig;
    categories: Category[];
    language: string;
};

// Colors from F:\\ASN\\theme5
const THEME5_PRIMARY = '#4ba3e3'; // Light blue
const THEME5_LIGHT = '#e6f2fa';
const THEME5_GREEN = '#a7f3d0';
const THEME5_GREEN_TEXT = '#059669';

export default function Theme5Menu({ config, categories, language }: Props) {
    const isAr = language === "ar";
    const { theme, setTheme } = useTheme();
    const isDark = theme === 'dark';

    const [searchQuery, setSearchQuery] = useState('');
    const [isSearchOpen, setIsSearchOpen] = useState(false);
    const [activeSection, setActiveSection] = useState<string>('');
    const [showCallMenu, setShowCallMenu] = useState(false);
    const [showPaymentMenu, setShowPaymentMenu] = useState(false);

    // Modals
    const [selectedItem, setSelectedItem] = useState<{ item: Item; cName: string; catImg?: string } | null>(null);
    const [tempSizeIdx, setTempSizeIdx] = useState(0);
    const [showCart, setShowCart] = useState(false);
    const [showCategoriesModal, setShowCategoriesModal] = useState(false);

    // Cart State
    const [cart, setCart] = useState<CartItem[]>([]);
    const [customerInfo, setCustomerInfo] = useState({ name: "", phone: "", address: "", notes: "" });

    const categoryNavRef = useRef<HTMLDivElement>(null);
    const isManualScroll = useRef(false);

    const currency = isAr ? "ج.م" : "EGP";

    const filteredCategories = categories.map(cat => ({
        ...cat,
        items: cat.items.filter(item =>
            searchQuery === "" ||
            (item.title_ar && item.title_ar.includes(searchQuery)) ||
            (item.title_en && item.title_en.toLowerCase().includes(searchQuery.toLowerCase())) ||
            (item.desc_ar && item.desc_ar.includes(searchQuery))
        )
    })).filter(cat => cat.items.length > 0);

    useEffect(() => {
        const observer = new IntersectionObserver(
            (entries) => {
                if (isManualScroll.current) return;
                entries.forEach((e) => {
                    if (e.isIntersecting) {
                        const id = e.target.id.replace("t5-s-", "");
                        setActiveSection(id);
                        const btn = categoryNavRef.current?.querySelector(`[data-cat-id="${id}"]`);
                        btn?.scrollIntoView({ behavior: "smooth", inline: "center", block: "nearest" });
                    }
                });
            },
            { rootMargin: "-140px 0px -75% 0px", threshold: 0 }
        );
        categories.forEach((cat) => {
            const el = document.getElementById(`t5-s-${cat.id}`);
            if (el) observer.observe(el);
        });
        return () => observer.disconnect();
    }, [categories]);

    const haptic = (v = 10) => {
        if (typeof window !== "undefined" && window.navigator && window.navigator.vibrate) {
            window.navigator.vibrate(v);
        }
    };

    const scrollToTop = () => {
        window.scrollTo({ top: 0, behavior: 'smooth' });
        setActiveSection('');
        haptic(5);
    };

    const scrollToSection = (id: string) => {
        haptic(10);
        const el = document.getElementById(`t5-s-${id}`);
        if (el) {
            isManualScroll.current = true;
            setActiveSection(id);
            el.scrollIntoView({ behavior: "smooth" });
            setTimeout(() => { isManualScroll.current = false; }, 800);
        }
    };

    const handleArrowScroll = (direction: 'left' | 'right') => {
        if (categoryNavRef.current) {
            const scrollAmount = 150;
            categoryNavRef.current.scrollBy({
                left: direction === 'left' ? -scrollAmount : scrollAmount,
                behavior: 'smooth'
            });
            haptic(5);
        }
    };

    const handleShare = async () => {
        if (navigator.share) {
            try {
                await navigator.share({
                    title: config.name,
                    text: isAr ? `أستمتع بأحلى مأكولات ${config.name} 😋` : `Enjoy the best from ${config.name} 😋`,
                    url: window.location.href,
                });
            } catch (error) {
                console.error('Error sharing:', error);
            }
        } else {
            navigator.clipboard.writeText(window.location.href).then(() => {
                alert(isAr ? 'تم نسخ الرابط بنجاح!' : 'Link copied successfully!');
            });
        }
    };

    const openItemSelect = (item: Item, cName: string, catImg?: string) => {
        setSelectedItem({ item, cName, catImg });
        setTempSizeIdx(0);
        haptic(10);
    };

    const addToCart = () => {
        if (!selectedItem) return;
        const { item } = selectedItem;
        const price = item.prices ? parseFloat(item.prices[tempSizeIdx]?.toString()) : 0;
        const sizeLabel = item.size_labels?.[tempSizeIdx] || (isAr ? "عادي" : "Regular");
        const cartId = `${item.id}-${sizeLabel}`;
        setCart((prev) => {
            const ex = prev.find((c) => c.id === cartId);
            if (ex) return prev.map((c) => (c.id === cartId ? { ...c, quantity: c.quantity + 1 } : c));
            return [...prev, { id: cartId, item, price, size_label: sizeLabel, quantity: 1 }];
        });
        setSelectedItem(null);
        haptic(20);
    };

    const updateCartQty = (id: string, delta: number) => {
        setCart((prev) =>
            prev.map((c) => (c.id === id ? { ...c, quantity: Math.max(0, c.quantity + delta) } : c)).filter((c) => c.quantity > 0)
        );
        haptic(5);
    };

    const cartTotal = cart.reduce((s, c) => s + c.price * c.quantity, 0);
    const cartCount = cart.reduce((s, c) => s + c.quantity, 0);

    const checkOutWhatsApp = () => {
        if (!config.whatsapp_number) { alert(isAr ? "عذراً، لم يتم توفير رقم واتساب." : "No WhatsApp number available."); return; }
        if (!customerInfo.name || !customerInfo.phone || !customerInfo.address) {
            alert(isAr ? "⚠️ يرجى إدخال جميع البيانات" : "⚠️ Please fill all fields");
            return;
        }

        if (config.payment_methods && config.payment_methods.length > 0) {
            setShowPaymentMenu(true);
        } else {
            finalizeOrder();
        }
    };

    const finalizeOrder = (selectedPaymentName?: string) => {
        let msg = `*🧾 فاتورة طلب جديدة - ${config.name}*\n------------------------------\n`;
        msg += `👤 *الاسم:* ${customerInfo.name}\n📞 *الموبايل:* ${customerInfo.phone}\n📍 *العنوان:* ${customerInfo.address}\n`;

        if (customerInfo.notes) msg += `📝 *ملاحظات:* ${customerInfo.notes}\n`;
        if (selectedPaymentName) msg += `💳 *طريقة الدفع:* ${selectedPaymentName}\n`;

        msg += `------------------------------\n*📋 الأصناف:*\n\n`;

        cart.forEach((c) => {
            msg += `▪️ ${isAr ? c.item.title_ar : c.item.title_en || c.item.title_ar} (${c.size_label})\n`;
            msg += `   الكمية: ${c.quantity} × ${c.price} ${currency} = ${c.quantity * c.price} ${currency}\n`;
        });

        msg += `\n------------------------------\n*💰 الإجمالي:* ${cartTotal} ${currency}\n`;
        msg += `*🛵 يضاف مصاريف التوصيل*\n------------------------------\n`;

        const url = `https://wa.me/${config.whatsapp_number?.replace(/\+/g, "")}?text=${encodeURIComponent(msg)}`;
        window.open(url, "_blank");

        haptic(50);
        setShowCart(false);
        setCart([]);
        setShowPaymentMenu(false);
    };

    return (
        <div className="min-h-screen pb-40 bg-[#f4f6f8] dark:bg-[#121212] transition-colors duration-500 font-sans" dir={isAr ? "rtl" : "ltr"}>

            {/* Header Area */}
            <div className="flex flex-col bg-white dark:bg-[#18181b]">
                {/* Top Navigation Bar */}
                <div className="flex items-center justify-between px-4 py-3 border-b border-zinc-100 dark:border-zinc-800 h-14">
                    <div className="flex items-center gap-4">
                        {isSearchOpen ? (
                            <div className="flex items-center bg-zinc-100 dark:bg-zinc-800 rounded-full px-3 py-1.5 animate-fade-in">
                                <input
                                    type="text"
                                    placeholder={isAr ? "بحث..." : "Search..."}
                                    value={searchQuery}
                                    onChange={(e) => setSearchQuery(e.target.value)}
                                    className="bg-transparent border-none outline-none text-sm w-32 md:w-48 dark:text-white text-right"
                                    autoFocus
                                    dir={isAr ? "rtl" : "ltr"}
                                />
                                <button onClick={() => { setIsSearchOpen(false); setSearchQuery(''); }} className="text-zinc-500 mr-2">
                                    <X className="w-4 h-4" />
                                </button>
                            </div>
                        ) : (
                            <button onClick={() => setIsSearchOpen(true)} className="text-zinc-600 dark:text-zinc-300 transition-colors hover:text-[#4ba3e3]">
                                <Search className="w-5 h-5" />
                            </button>
                        )}
                        {!isSearchOpen && (
                            <button onClick={handleShare} className="text-zinc-600 dark:text-zinc-300 transition-colors hover:text-[#4ba3e3]">
                                <Share2 className="w-5 h-5" />
                            </button>
                        )}
                    </div>

                    {!isSearchOpen && (
                        <button onClick={() => setTheme(isDark ? 'light' : 'dark')} className="flex items-center justify-center w-10 h-10 rounded-full bg-zinc-100 dark:bg-zinc-800 text-zinc-600 dark:text-zinc-300 transition-colors">
                            {isDark ? <Sun className="w-5 h-5" /> : <Moon className="w-5 h-5" />}
                        </button>
                    )}

                    <div className="flex items-center gap-4">
                        <div className="flex items-center gap-2">
                            <span className="text-sm font-bold text-zinc-800 dark:text-zinc-200">{isAr ? "توصيل" : "Delivery"}</span>
                            <div className="w-8 h-8 rounded-full flex items-center justify-center text-white" style={{ backgroundColor: THEME5_PRIMARY }}>
                                <Bike className="w-4 h-4" />
                            </div>
                        </div>
                    </div>
                </div>

                {/* Shared Marquee Banner */}
                {config.marquee_enabled && (config.marquee_text_ar || config.marquee_text_en) && (
                    <SharedMarquee
                        text={isAr ? (config.marquee_text_ar || config.marquee_text_en || '') : (config.marquee_text_en || config.marquee_text_ar || '')}
                        bgColor={THEME5_PRIMARY}
                    />
                )}

                {/* Hero Section */}
                <div className="relative">
                    {/* Banner Image */}
                    <div className="h-48 md:h-64 w-full relative">
                        {config.cover_images && config.cover_images.length > 0 ? (
                            <Swiper
                                modules={[Autoplay, EffectFade]}
                                effect="fade"
                                autoplay={{ delay: 3000, disableOnInteraction: false }}
                                loop={config.cover_images.length > 1}
                                className="w-full h-full absolute inset-0 z-0"
                            >
                                {config.cover_images.map((img: string, idx: number) => (
                                    <SwiperSlide key={idx}>
                                        <img src={img || 'https://images.unsplash.com/photo-1544025162-d76694265947?q=80&w=1000&auto=format&fit=crop'}
                                            alt={`Cover ${idx}`} className="w-full h-full object-cover" />
                                    </SwiperSlide>
                                ))}
                            </Swiper>
                        ) : config.cover_url ? (
                            <img src={config.cover_url} alt="Restaurant Banner" className="w-full h-full object-cover z-0" />
                        ) : (
                            <div className="w-full h-full bg-zinc-200 dark:bg-zinc-800 z-0"></div>
                        )}
                    </div>

                    {/* Floating Info Card */}
                    <div className="absolute -bottom-12 left-4 right-4 bg-white dark:bg-zinc-800 rounded-xl t5-shadow-floating p-4 flex items-center justify-between border border-zinc-100 dark:border-zinc-700">
                        <div className="flex flex-col gap-2">
                            <div className="px-3 py-1 rounded-md text-xs font-bold w-fit" style={{ backgroundColor: `${THEME5_GREEN}33`, color: THEME5_GREEN_TEXT }}>
                                {isAr ? "مفتوح" : "Open"}
                            </div>
                        </div>

                        <div className="flex flex-col items-end mr-16" dir={isAr ? "rtl" : "ltr"}>
                            <h1 className="text-lg font-bold text-zinc-900 dark:text-white line-clamp-1">{config.name}</h1>
                            <p className="text-sm text-zinc-500 dark:text-zinc-400">{isAr ? "الفرع الرئيسي" : "Main Branch"}</p>
                        </div>

                        {/* Overlapping Logo */}
                        <div className="absolute -top-6 -right-2 w-24 h-24 rounded-full border-4 border-white dark:border-zinc-800 overflow-hidden bg-white shadow-sm">
                            {config.logo_url ? (
                                <img src={config.logo_url} alt="Logo" className="w-full h-full object-cover" />
                            ) : (
                                <div className="w-full h-full flex items-center justify-center bg-zinc-100 dark:bg-zinc-800 text-2xl">🍽️</div>
                            )}
                        </div>
                    </div>
                </div>

                {/* Spacing for floating card */}
                <div className="h-16"></div>

                {/* Announcement Banner */}
                <div className="mx-4 mb-4 rounded-lg p-3 text-center font-bold text-sm border flex items-center justify-center gap-2" style={{ backgroundColor: THEME5_LIGHT, color: THEME5_PRIMARY, borderColor: `${THEME5_PRIMARY}33` }}>
                    <span className="w-1.5 h-1.5 rounded-full" style={{ backgroundColor: THEME5_PRIMARY }}></span>
                    {isAr ? `أستمتع بأحلى مأكولات ${config.name} 😋` : `Enjoy the best from ${config.name} 😋`}
                    <span className="w-1.5 h-1.5 rounded-full" style={{ backgroundColor: THEME5_PRIMARY }}></span>
                </div>
            </div>

            {/* Category Nav */}
            <div className="sticky top-0 z-[100] bg-white/95 dark:bg-[#18181b]/95 backdrop-blur-md py-3 border-b border-zinc-100 dark:border-white/5 shadow-sm flex items-center justify-between flex-row-reverse px-2">
                <button
                    onClick={() => setShowCategoriesModal(true)}
                    className="shrink-0 w-11 h-11 flex items-center justify-center rounded-full ml-3 bg-zinc-100 dark:bg-zinc-800 text-zinc-600 dark:text-zinc-300 active:scale-95 transition-transform"
                >
                    <LayoutGrid className="w-5 h-5" />
                </button>
                <div className="flex-1 relative overflow-hidden h-full flex items-center">
                    <button
                        onClick={() => handleArrowScroll('right')}
                        className="t5-nav-arrow right-1.5 flex items-center justify-center text-zinc-400 dark:text-zinc-500"
                    >
                        <span className="text-sm font-bold leading-none">›</span>
                    </button>
                    <button
                        onClick={() => handleArrowScroll('left')}
                        className="t5-nav-arrow left-1.5 flex items-center justify-center text-zinc-400 dark:text-zinc-500"
                    >
                        <span className="text-sm font-bold leading-none">‹</span>
                    </button>

                    <div ref={categoryNavRef} className="flex items-center overflow-x-auto t5-no-scrollbar px-6 py-1 gap-2 scroll-smooth" dir="rtl">
                        <button
                            onClick={scrollToTop}
                            data-cat-id="top"
                            className={`shrink-0 px-5 py-2 rounded-full text-sm font-bold transition-colors border ${activeSection === '' ? 'text-white' : 'bg-white dark:bg-zinc-900'}`}
                            style={activeSection === '' ? { backgroundColor: THEME5_PRIMARY, borderColor: THEME5_PRIMARY } : { color: THEME5_PRIMARY, borderColor: `${THEME5_PRIMARY}4d` }}
                        >
                            {isAr ? "الكل" : "All"}
                        </button>
                        {categories.map((c) => (
                            <button
                                key={c.id}
                                data-cat-id={c.id}
                                onClick={() => scrollToSection(c.id)}
                                className={`shrink-0 px-5 py-2 rounded-full text-sm font-bold flex items-center gap-2 transition-colors border ${activeSection === c.id ? 'text-white' : 'bg-white dark:bg-zinc-900'}`}
                                style={activeSection === c.id ? { backgroundColor: THEME5_PRIMARY, borderColor: THEME5_PRIMARY } : { color: THEME5_PRIMARY, borderColor: `${THEME5_PRIMARY}4d` }}
                            >
                                <span>{isAr ? c.name_ar : c.name_en || c.name_ar}</span>
                                {c.emoji && <span>{c.emoji}</span>}
                            </button>
                        ))}
                    </div>
                </div>
            </div>

            {/* Content Lists */}
            <main className="mt-8 space-y-12 max-w-4xl mx-auto px-4 md:px-6">
                {filteredCategories.map((cat) => (
                    <section key={cat.id} id={`t5-s-${cat.id}`}>
                        <div className="flex flex-col gap-3 mb-6">
                            <div className="flex items-center gap-2.5">
                                <div className="w-9 h-9 rounded-full flex items-center justify-center text-lg shadow-sm" style={{ backgroundColor: `${THEME5_PRIMARY}1a` }}>
                                    {cat.emoji}
                                </div>
                                <h3 className="text-lg font-black text-zinc-800 dark:text-zinc-100 tracking-tight">{isAr ? cat.name_ar : cat.name_en || cat.name_ar}</h3>
                            </div>
                        </div>

                        <div className="flex flex-col gap-4">
                            {cat.items.map((item, idx) => {
                                const title = isAr ? item.title_ar : item.title_en || item.title_ar;
                                const desc = isAr ? item.desc_ar : item.desc_en;
                                return (
                                    <div
                                        key={idx}
                                        onClick={() => openItemSelect(item, title, cat.image_url)}
                                        className="t5-item-card overflow-hidden flex items-center p-3 gap-3 transition-all duration-300 bg-white dark:bg-[#18181b] border border-[#edf2f7] dark:border-[#27272a] cursor-pointer"
                                        dir="ltr" // forces price left, image right when inside flex
                                    >
                                        {/* Price (Left) */}
                                        <div className="flex-shrink-0 flex flex-col gap-1.5 min-w-[3.5rem] items-center">
                                            {item.prices.map((price, pIdx) => {
                                                const sizeLabel = item.size_labels?.[pIdx] || (isAr ? "عادي" : "Regular");
                                                return (
                                                    <div key={pIdx} className="px-2 py-1 rounded-lg flex flex-col items-center justify-center min-w-[3.5rem]" style={{ backgroundColor: THEME5_LIGHT, color: THEME5_PRIMARY }}>
                                                        <div className="flex items-center gap-0.5">
                                                            <span className="text-sm font-bold">{price}</span>
                                                            <span className="text-[9px]">{currency}</span>
                                                        </div>
                                                        {item.prices.length > 1 && sizeLabel && <span className="text-[8px] font-bold opacity-80">{sizeLabel}</span>}
                                                    </div>
                                                );
                                            })}
                                        </div>

                                        {/* Content (Middle) */}
                                        <div className="flex-1 flex flex-col items-end text-right" dir="rtl">
                                            <h3 className="text-sm md:text-base font-bold text-zinc-900 dark:text-white line-clamp-1 mb-1">
                                                {title}
                                            </h3>
                                            <p className="text-xs text-zinc-500 dark:text-zinc-400 line-clamp-2 leading-tight">
                                                {desc || title}
                                            </p>
                                        </div>

                                        {/* Image (Right) */}
                                        <div className="relative w-24 h-24 flex-shrink-0 rounded-xl overflow-hidden bg-zinc-100 dark:bg-zinc-800">
                                            {item.image_url ? (
                                                <img src={item.image_url} alt={title} className="w-full h-full object-cover" loading="lazy" />
                                            ) : (
                                                <div className="w-full h-full flex items-center justify-center"><ShoppingBag className="w-8 h-8 opacity-20" /></div>
                                            )}
                                            {item.is_popular && (
                                                <div className="absolute top-1 right-1 text-white text-[8px] font-bold px-1.5 py-0.5 rounded shadow-sm z-10" style={{ backgroundColor: THEME5_PRIMARY }}>
                                                    {isAr ? "شائع" : "Popular"}
                                                </div>
                                            )}
                                        </div>
                                    </div>
                                );
                            })}
                        </div>
                    </section>
                ))}
            </main>

            {/* Bottom Nav */}
            <nav className="fixed bottom-0 left-0 right-0 z-[130] t5-bottom-nav px-2 py-4 flex items-center justify-around rounded-t-[2rem]">
                {config.facebook_url && (
                    <a href={config.facebook_url} target="_blank" className="flex flex-col items-center gap-1 flex-1">
                        <div className="w-10 h-10 flex items-center justify-center bg-[#1877F2]/10 text-[#1877F2] rounded-xl active:scale-90 transition-transform">
                            <FaFacebook className="w-5 h-5" />
                        </div>
                        <span className="text-[9px] font-black text-zinc-500">{isAr ? "فيسبوك" : "Facebook"}</span>
                    </a>
                )}

                <div onClick={() => setShowCallMenu(true)} className="flex flex-col items-center gap-1 flex-1 cursor-pointer">
                    <div className="w-10 h-10 flex items-center justify-center bg-zinc-100 dark:bg-zinc-800 text-zinc-600 rounded-xl active:scale-90 transition-transform">
                        <span className="text-lg">📞</span>
                    </div>
                    <span className="text-[9px] font-black text-zinc-500">{isAr ? "دليفري" : "Call"}</span>
                </div>

                <div onClick={scrollToTop} className="flex flex-col items-center gap-1 flex-1 -mt-8 cursor-pointer">
                    <div className="w-14 h-14 flex items-center justify-center text-white rounded-full shadow-lg border-[4px] border-[#f8f9fa] dark:border-[#121212] active:scale-90 transition-transform" style={{ backgroundColor: THEME5_PRIMARY }}>
                        <span className="text-xl font-black">↑</span>
                    </div>
                    <span className="text-[9px] font-black mt-1" style={{ color: THEME5_PRIMARY }}>{isAr ? "للأعلى" : "Top"}</span>
                </div>

                <div onClick={() => setShowPaymentMenu(true)} className="flex flex-col items-center gap-1 flex-1 cursor-pointer">
                    <div className="w-10 h-10 flex items-center justify-center bg-zinc-100 dark:bg-zinc-800 text-zinc-600 rounded-xl active:scale-90 transition-transform">
                        <span className="text-lg">💳</span>
                    </div>
                    <span className="text-[9px] font-black text-zinc-500">{isAr ? "الدفع" : "Pay"}</span>
                </div>

                <div onClick={() => setShowCart(true)} className="flex flex-col items-center gap-1 flex-1 cursor-pointer relative">
                    <div className="w-10 h-10 flex items-center justify-center bg-zinc-100 dark:bg-zinc-800 text-zinc-600 rounded-xl active:scale-90 transition-transform">
                        <ShoppingBag className="w-5 h-5" />
                    </div>
                    {cartCount > 0 && (
                        <div className="absolute -top-1 right-2 bg-red-500 text-white text-[10px] font-bold w-4 h-4 rounded-full flex items-center justify-center">{cartCount}</div>
                    )}
                    <span className="text-[9px] font-black text-zinc-500">{isAr ? "السلة" : "Cart"}</span>
                </div>
            </nav>

            {/* Floating WhatsApp */}
            {config.whatsapp_number && (
                <a href={`https://wa.me/${config.whatsapp_number.replace(/\+/g, "")}`} target="_blank" className="fixed bottom-28 right-5 bg-[#25D366] w-12 h-12 rounded-full flex items-center justify-center text-white shadow-lg active:scale-90 z-[100]">
                    <FaWhatsapp className="w-7 h-7" />
                </a>
            )}

            {/* Modals */}
            <AnimatePresence>
                {/* Item Select Modal */}
                {selectedItem && (
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} className="fixed inset-0 z-[200] flex items-center justify-center bg-black/60 p-4 backdrop-blur-sm" onClick={() => setSelectedItem(null)}>
                        <motion.div initial={{ scale: 0.95 }} animate={{ scale: 1 }} exit={{ scale: 0.95 }} className="w-full max-w-lg bg-white dark:bg-[#1E1E1E] rounded-3xl overflow-hidden flex flex-col max-h-[90vh] shadow-2xl" onClick={e => e.stopPropagation()}>
                            <div className="relative">
                                {selectedItem.item.image_url || selectedItem.catImg ? (
                                    <img src={selectedItem.item.image_url || selectedItem.catImg} alt={selectedItem.cName} className="w-full h-64 object-cover" />
                                ) : (
                                    <div className="w-full h-40 bg-zinc-100 dark:bg-zinc-800 flex items-center justify-center"><ShoppingBag className="w-12 h-12 opacity-20" /></div>
                                )}
                                <button onClick={() => setSelectedItem(null)} className="absolute top-4 right-4 w-10 h-10 bg-black/50 text-white rounded-full flex items-center justify-center"><X className="w-5 h-5" /></button>
                            </div>
                            <div className="p-6 pb-2">
                                <h3 className="text-2xl font-bold mb-1 text-right">{selectedItem.cName}</h3>
                                <p className="text-zinc-500 text-sm text-right line-clamp-2">{isAr ? selectedItem.item.desc_ar : selectedItem.item.desc_en}</p>
                            </div>
                            <div className="p-6 overflow-y-auto">
                                <div className="space-y-3">
                                    {selectedItem.item.prices.map((price, idx) => {
                                        const label = selectedItem.item.size_labels?.[idx] || (isAr ? "عادي" : "Regular");
                                        const isSelected = tempSizeIdx === idx;
                                        return (
                                            <div key={idx} onClick={() => setTempSizeIdx(idx)} className={`flex justify-between items-center p-4 rounded-xl border-2 cursor-pointer flex-row-reverse ${isSelected ? 'border-[#4ba3e3] bg-[#e6f2fa] dark:bg-[#4ba3e3]/10' : 'border-zinc-200 dark:border-zinc-800'}`}>
                                                <div className="flex items-center gap-3 flex-row-reverse">
                                                    <div className="w-5 h-5 rounded-full border-2 flex items-center justify-center" style={{ borderColor: isSelected ? THEME5_PRIMARY : '#cbd5e1' }}>
                                                        {isSelected && <div className="w-3 h-3 rounded-full" style={{ backgroundColor: THEME5_PRIMARY }}></div>}
                                                    </div>
                                                    <span className={`font-bold ${isSelected ? 'text-[#4ba3e3]' : ''}`}>{label}</span>
                                                </div>
                                                <span className={`font-bold ${isSelected ? 'text-[#4ba3e3]' : ''}`}>{price} {currency}</span>
                                            </div>
                                        );
                                    })}
                                </div>
                            </div>
                            <div className="p-6 border-t border-zinc-100 dark:border-zinc-800">
                                <button onClick={addToCart} className="w-full text-white py-4 rounded-xl font-bold text-lg flex justify-center items-center gap-2" style={{ backgroundColor: THEME5_PRIMARY }}>
                                    <ShoppingBag className="w-5 h-5" />
                                    {isAr ? "إضافة للسلة" : "Add to Cart"} — {selectedItem.item.prices[tempSizeIdx]} {currency}
                                </button>
                            </div>
                        </motion.div>
                    </motion.div>
                )}

                {/* Cart Modal */}
                {showCart && (
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} className="fixed inset-0 z-[150] bg-black/60 backdrop-blur-sm flex items-center justify-center p-4" onClick={() => setShowCart(false)}>
                        <motion.div initial={{ scale: 0.95 }} animate={{ scale: 1 }} exit={{ scale: 0.95 }} className="w-full max-w-lg bg-white dark:bg-[#1E1E1E] rounded-3xl overflow-hidden flex flex-col max-h-[95vh] shadow-2xl" onClick={e => e.stopPropagation()}>
                            <div className="p-5 border-b border-zinc-100 dark:border-zinc-800 flex justify-between items-center flex-row-reverse bg-gray-50 dark:bg-black/20">
                                <h3 className="text-xl font-bold flex items-center gap-2">
                                    {isAr ? "سلة الطلبات" : "Your Cart"} <ShoppingBag className="w-5 h-5" style={{ color: THEME5_PRIMARY }} />
                                </h3>
                                <button onClick={() => setShowCart(false)} className="w-8 h-8 rounded-full bg-zinc-200 dark:bg-zinc-800 flex items-center justify-center text-zinc-600 dark:text-zinc-300"><X className="w-5 h-5" /></button>
                            </div>
                            <div className="p-5 overflow-y-auto flex-1">
                                {cart.length === 0 ? (
                                    <div className="text-center py-10 opacity-50 font-bold">{isAr ? "السلة فارغة" : "Cart is empty"}</div>
                                ) : (
                                    <div className="space-y-4">
                                        {cart.map((c) => (
                                            <div key={c.id} className="flex gap-4 p-3 border rounded-2xl flex-row-reverse border-zinc-100 dark:border-zinc-800 bg-gray-50 dark:bg-black/10">
                                                <div className="flex flex-col items-center justify-between bg-white dark:bg-[#1E1E1E] border rounded-full p-1 border-zinc-100 dark:border-zinc-800">
                                                    <button onClick={() => updateCartQty(c.id, 1)} className="w-7 h-7 rounded-full bg-zinc-100 dark:bg-[#333] flex items-center justify-center hover:bg-[#4ba3e3] hover:text-white transition-colors"><PlusIcon className="w-4 h-4" /></button>
                                                    <span className="font-bold tabular-nums my-1">{c.quantity}</span>
                                                    <button onClick={() => updateCartQty(c.id, -1)} className="w-7 h-7 rounded-full bg-zinc-100 dark:bg-[#333] flex items-center justify-center hover:bg-red-500 hover:text-white transition-colors"><Minus className="w-4 h-4" /></button>
                                                </div>
                                                <div className="flex-1 text-right flex flex-col justify-center">
                                                    <div className="flex justify-between items-start flex-row-reverse">
                                                        <h4 className="font-bold text-sm leading-tight flex-1 line-clamp-2">{isAr ? c.item.title_ar : c.item.title_en || c.item.title_ar}</h4>
                                                        <span className="font-bold shrink-0 tabular-nums ml-4" style={{ color: THEME5_PRIMARY }}>{c.price * c.quantity} {currency}</span>
                                                    </div>
                                                    <span className="text-xs text-zinc-500 mt-1 bg-zinc-200 dark:bg-zinc-800 px-2 py-0.5 rounded-md w-fit font-medium">{c.size_label}</span>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                )}
                            </div>
                            {cart.length > 0 && (
                                <div className="p-5 border-t border-zinc-100 dark:border-zinc-800 bg-gray-50 dark:bg-black/20">
                                    <div className="flex justify-between items-center mb-5 flex-row-reverse">
                                        <span className="font-bold text-zinc-500">{isAr ? "الإجمالي" : "Total"}</span>
                                        <span className="font-bold text-2xl tabular-nums" style={{ color: THEME5_PRIMARY }}>{cartTotal} {currency}</span>
                                    </div>
                                    <div className="space-y-3 mb-5">
                                        <input type="text" placeholder={isAr ? "الاسم" : "Name"} value={customerInfo.name} onChange={e => setCustomerInfo(p => ({ ...p, name: e.target.value }))} className="w-full border rounded-xl px-4 py-3 bg-white dark:bg-[#1E1E1E] text-right border-zinc-200 dark:border-zinc-800 outline-none focus:border-[#4ba3e3]" dir="rtl" />
                                        <input type="tel" placeholder={isAr ? "رقم الموبايل" : "Phone"} value={customerInfo.phone} onChange={e => setCustomerInfo(p => ({ ...p, phone: e.target.value }))} className="w-full border rounded-xl px-4 py-3 bg-white dark:bg-[#1E1E1E] text-right border-zinc-200 dark:border-zinc-800 outline-none focus:border-[#4ba3e3]" dir="rtl" />
                                        <textarea placeholder={isAr ? "العنوان بالتفصيل" : "Address"} value={customerInfo.address} onChange={e => setCustomerInfo(p => ({ ...p, address: e.target.value }))} className="w-full border rounded-xl px-4 py-3 bg-white dark:bg-[#1E1E1E] text-right border-zinc-200 dark:border-zinc-800 outline-none h-20 resize-none focus:border-[#4ba3e3]" dir="rtl" />
                                        <textarea placeholder={isAr ? "ملاحظات إضافية (اختياري)" : "Notes (Optional)"} value={customerInfo.notes} onChange={e => setCustomerInfo(p => ({ ...p, notes: e.target.value }))} className="w-full border rounded-xl px-4 py-3 bg-white dark:bg-[#1E1E1E] text-right border-zinc-200 dark:border-zinc-800 outline-none h-16 resize-none focus:border-[#4ba3e3]" dir="rtl" />
                                    </div>
                                    <button onClick={checkOutWhatsApp} className="w-full bg-[#25D366] text-white py-4 rounded-xl font-bold flex justify-center items-center gap-2 text-lg active:scale-95 transition-all shadow-lg shadow-green-500/30">
                                        <FaWhatsapp className="w-6 h-6" />
                                        {isAr ? "إرسال الطلب عبر واتساب" : "Confirm via WhatsApp"}
                                    </button>
                                </div>
                            )}
                        </motion.div>
                    </motion.div>
                )}

                {/* Delivery Call Modal */}
                {showCallMenu && (
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} className="fixed inset-0 z-[300] bg-black/60 backdrop-blur-md flex items-center justify-center p-4" onClick={() => setShowCallMenu(false)}>
                        <motion.div initial={{ scale: 0.95 }} animate={{ scale: 1 }} exit={{ scale: 0.95 }} className="bg-white dark:bg-zinc-900 w-full max-w-sm rounded-[2rem] overflow-hidden shadow-2xl flex flex-col max-h-[85vh]" onClick={e => e.stopPropagation()}>
                            <div className="p-5 text-center shrink-0" style={{ backgroundColor: THEME5_PRIMARY }}>
                                <div className="w-12 h-12 bg-white/20 rounded-full flex items-center justify-center mx-auto mb-2 backdrop-blur-md">
                                    <span className="text-xl">📞</span>
                                </div>
                                <h3 className="text-xl font-black text-white uppercase">{isAr ? "أرقام التوصيل" : "Delivery"}</h3>
                            </div>
                            <div className="p-3 space-y-2 overflow-y-auto t5-no-scrollbar">
                                {config.phone && (
                                    <a href={`tel:${config.phone}`} className="flex items-center justify-between p-4 bg-zinc-50 dark:bg-zinc-800 rounded-2xl border border-zinc-100 dark:border-white/5 active:scale-95 transition-transform group flex-row-reverse">
                                        <div className="flex flex-col items-end">
                                            <span className="text-base font-black text-zinc-800 dark:text-white tabular-nums">{config.phone}</span>
                                            <span className="text-[9px] text-zinc-400 font-bold">{isAr ? "الرقم الرئيسي" : "Main Number"}</span>
                                        </div>
                                        <div className="w-9 h-9 bg-white dark:bg-zinc-700 rounded-xl flex items-center justify-center text-lg" style={{ color: THEME5_PRIMARY }}>☏</div>
                                    </a>
                                )}
                                {config.phone_numbers?.map((num, i) => (
                                    <a key={i} href={`tel:${num.number}`} className="flex items-center justify-between p-4 bg-zinc-50 dark:bg-zinc-800 rounded-2xl border border-zinc-100 dark:border-white/5 active:scale-95 transition-transform group flex-row-reverse">
                                        <div className="flex flex-col items-end">
                                            <span className="text-base font-black text-zinc-800 dark:text-white tabular-nums">{num.number}</span>
                                            <span className="text-[9px] text-zinc-400 font-bold">{num.label}</span>
                                        </div>
                                        <div className="w-9 h-9 bg-white dark:bg-zinc-700 rounded-xl flex items-center justify-center text-lg" style={{ color: THEME5_PRIMARY }}>☏</div>
                                    </a>
                                ))}
                            </div>
                            <div className="p-4 pt-0 shrink-0">
                                <button onClick={() => setShowCallMenu(false)} className="w-full py-3 text-zinc-400 font-bold text-[10px] uppercase">{isAr ? "إغلاق القائمة" : "Close"}</button>
                            </div>
                        </motion.div>
                    </motion.div>
                )}

                {/* Payment Modal */}
                {showPaymentMenu && (
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} className="fixed inset-0 z-[300] bg-black/60 backdrop-blur-md flex items-center justify-center p-4" onClick={() => setShowPaymentMenu(false)}>
                        <motion.div initial={{ scale: 0.95 }} animate={{ scale: 1 }} exit={{ scale: 0.95 }} className="bg-white dark:bg-zinc-900 w-full max-w-sm rounded-[2.5rem] overflow-hidden shadow-2xl flex flex-col max-h-[90vh]" onClick={e => e.stopPropagation()}>
                            <div className="p-6 pb-2 text-center shrink-0 flex justify-between items-start">
                                <button onClick={() => setShowPaymentMenu(false)} className="w-8 h-8 bg-zinc-100 dark:bg-white/5 rounded-full flex items-center justify-center font-bold active:scale-95 text-zinc-500">✕</button>
                                <div className="text-right">
                                    <div className="w-12 h-12 bg-zinc-100 dark:bg-white/5 rounded-2xl flex items-center justify-center ml-auto mb-3 text-2xl" style={{ color: THEME5_PRIMARY }}>💳</div>
                                    <h3 className="text-xl font-black text-zinc-900 dark:text-white mb-1">{isAr ? "وسائل الدفع" : "Payment Methods"}</h3>
                                    <p className="text-[10px] opacity-60 font-black uppercase tracking-widest">{isAr ? "طرق الدفع المتاحة" : "Available Payment Methods"}</p>
                                </div>
                            </div>
                            <div className="px-5 py-4 space-y-3 overflow-y-auto t5-no-scrollbar">
                                {config.payment_methods && config.payment_methods.length > 0 ? (
                                    config.payment_methods.map((pm) => (
                                        <div key={pm.id} className="block p-4 bg-zinc-50 dark:bg-zinc-800 rounded-2xl border border-zinc-100 dark:border-white/5 text-right transition-all hover:border-[#4ba3e3]/50">
                                            <h4 className="font-black text-sm text-zinc-800 dark:text-white mb-1">{isAr ? pm.name_ar : pm.name_en || pm.name_ar}</h4>
                                            {(pm.desc_ar || pm.desc_en) && (
                                                <p className="text-[10px] font-bold text-zinc-500 dark:text-zinc-400 mb-3 leading-tight">{isAr ? pm.desc_ar : pm.desc_en || pm.desc_ar}</p>
                                            )}
                                            {pm.number && (
                                                <div className="flex items-center justify-between bg-white dark:bg-black/20 px-3 py-2.5 rounded-xl mb-2 border border-zinc-100 dark:border-white/5 group">
                                                    <button
                                                        onClick={() => {
                                                            navigator.clipboard.writeText(pm.number!);
                                                            alert(isAr ? "تم نسخ الرقم!" : "Number copied!");
                                                        }}
                                                        className="text-[10px] font-black uppercase px-2 py-1 rounded-lg active:scale-95"
                                                        style={{ color: THEME5_PRIMARY, backgroundColor: `${THEME5_PRIMARY}1a` }}
                                                    >
                                                        {isAr ? "نسخ" : "Copy"}
                                                    </button>
                                                    <span className="font-black tabular-nums text-xs tracking-widest flex items-center gap-2" dir="ltr">{pm.number}</span>
                                                </div>
                                            )}
                                            {pm.link && (
                                                <a href={pm.link} target="_blank" rel="noopener noreferrer" className="block text-center w-full bg-[#1877F2]/10 text-[#1877F2] font-black text-[10px] py-2.5 rounded-xl active:scale-95 transition-transform mt-2">
                                                    {isAr ? "رابط الدفع / انستا باي" : "Payment Link / InstaPay"}
                                                </a>
                                            )}
                                            {cart.length > 0 && (
                                                <button
                                                    onClick={() => finalizeOrder(isAr ? pm.name_ar : pm.name_en || pm.name_ar)}
                                                    className="w-full text-white font-black py-2.5 rounded-xl active:scale-95 transition-transform mt-2 shadow-md flex items-center justify-center gap-2 text-[11px]"
                                                    style={{ backgroundColor: THEME5_PRIMARY }}
                                                >
                                                    <FaWhatsapp className="w-4 h-4" />
                                                    {isAr ? "تأكيد واستمرار" : "Confirm & Continue"}
                                                </button>
                                            )}
                                        </div>
                                    ))
                                ) : (
                                    <div className="block p-5 bg-white dark:bg-zinc-800 rounded-2xl border border-zinc-100 dark:border-white/5 text-right">
                                        <span className="text-sm font-black text-zinc-800 dark:text-white block mb-1">{isAr ? "كاش عند الاستلام" : "Cash on Delivery"}</span>
                                        <p className="text-[10px] text-zinc-400 leading-tight">{isAr ? "الدفع المباشر كاش للمندوب عند وصول الطلب" : "Pay cash directly to the delivery agent"}</p>
                                    </div>
                                )}
                            </div>
                            <div className="p-5 shrink-0 pt-0">
                                <button onClick={() => setShowPaymentMenu(false)} className="w-full py-4 bg-zinc-900 dark:bg-white text-white dark:text-black rounded-xl font-black text-[11px] active:scale-95 transition-all">{isAr ? "إغلاق" : "Close"}</button>
                            </div>
                        </motion.div>
                    </motion.div>
                )}

                {/* Categories Grid Modal */}
                {showCategoriesModal && (
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} className="fixed inset-0 z-[300] bg-black/60 backdrop-blur-sm flex items-center justify-center p-4" onClick={() => setShowCategoriesModal(false)}>
                        <motion.div initial={{ scale: 0.95 }} animate={{ scale: 1 }} exit={{ scale: 0.95 }} className="w-full max-w-md bg-white dark:bg-[#1E1E1E] rounded-[2rem] overflow-hidden flex flex-col max-h-[85vh] shadow-2xl" onClick={e => e.stopPropagation()}>
                            <div className="p-5 flex justify-between items-center bg-gray-50 dark:bg-black/20 border-b border-zinc-100 dark:border-zinc-800 flex-row-reverse shrink-0">
                                <h3 className="text-xl font-bold flex items-center gap-2 text-zinc-900 dark:text-white">
                                    <LayoutGrid className="w-5 h-5" style={{ color: THEME5_PRIMARY }} />
                                    {isAr ? "جميع الأقسام" : "All Categories"}
                                </h3>
                                <button onClick={() => setShowCategoriesModal(false)} className="w-8 h-8 rounded-full bg-zinc-200 dark:bg-zinc-800 flex items-center justify-center text-zinc-600 dark:text-zinc-300"><X className="w-5 h-5" /></button>
                            </div>
                            <div className="p-4 overflow-y-auto t5-no-scrollbar grid grid-cols-2 gap-3 pb-8">
                                {categories.map((c) => (
                                    <button
                                        key={c.id}
                                        onClick={() => {
                                            setShowCategoriesModal(false);
                                            scrollToSection(c.id);
                                        }}
                                        className="flex flex-col items-center justify-center p-4 border rounded-2xl bg-white dark:bg-zinc-900 border-zinc-100 dark:border-zinc-800 hover:shadow-md transition-all active:scale-95 group gap-2"
                                    >
                                        <div className="w-14 h-14 rounded-full flex items-center justify-center text-3xl group-hover:scale-110 transition-transform" style={{ backgroundColor: `${THEME5_PRIMARY}1a` }}>
                                            {c.emoji || "🍽️"}
                                        </div>
                                        <span className="text-sm font-bold text-center text-zinc-800 dark:text-zinc-200 line-clamp-2 w-full leading-tight">{isAr ? c.name_ar : c.name_en || c.name_ar}</span>
                                    </button>
                                ))}
                            </div>
                        </motion.div>
                    </motion.div>
                )}
            </AnimatePresence>

            <style jsx global>{`
                .t5-scrollbar::-webkit-scrollbar { display: none; }
                .t5-scrollbar { -ms-overflow-style: none; scrollbar-width: none; }
                .t5-no-scrollbar::-webkit-scrollbar { display: none; }
                .t5-no-scrollbar { -ms-overflow-style: none; scrollbar-width: none; }
                
                .t5-shadow-floating {
                    box-shadow: 0 10px 30px rgba(0,0,0,0.1);
                }
                .dark .t5-shadow-floating {
                    box-shadow: 0 10px 30px rgba(0,0,0,0.5);
                }

                .t5-nav-arrow {
                    position: absolute; top: 50%; transform: translateY(-50%); z-index: 50;
                    width: 28px; height: 28px;
                    background: rgba(255, 255, 255, 0.95);
                    border-radius: 50%;
                    display: flex; align-items: center; justify-content: center;
                    box-shadow: 0 4px 10px rgba(0,0,0,0.1);
                    cursor: pointer; transition: all 0.2s;
                    border: 1px solid rgba(0,0,0,0.05);
                }
                .dark .t5-nav-arrow { background: rgba(24, 24, 27, 0.95); border-color: rgba(255,255,255,0.1); }
                
                .t5-item-card {
                    box-shadow: 0 2px 8px rgba(0,0,0,0.04);
                    border-radius: 1rem;
                }
                .dark .t5-item-card { box-shadow: 0 4px 20px rgba(0,0,0,0.3); }

                .t5-bottom-nav {
                    background: rgba(255, 255, 255, 0.96);
                    backdrop-filter: blur(20px);
                    box-shadow: 0 -10px 40px rgba(0,0,0,0.08);
                }
                .dark .t5-bottom-nav { background: rgba(24, 24, 27, 0.96); box-shadow: 0 -10px 40px rgba(0,0,0,0.5); }
            `}</style>
        </div>
    );
}

'use client';

import React, { useState, useEffect, useRef } from 'react';
import { useTheme } from 'next-themes';
import { motion, AnimatePresence } from 'framer-motion';
import {
    Search, MapPin, Phone, List,
    Moon, Sun, ShoppingCart, Plus, Minus, Trash2, X,
    Home, UtensilsCrossed, User, Globe, PhoneCall
} from 'lucide-react';
import { FaWhatsapp, FaInstagram, FaFacebookF } from 'react-icons/fa';
import SharedMarquee from './SharedMarquee';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectFade } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/effect-fade';

// Import necessary types directly from where they are defined, or define locally
type MenuItem = {
    id: string | number;
    title_ar: string;
    title_en?: string;
    description_ar?: string;
    description_en?: string;
    image?: string;
    prices: number[];
    size_labels?: string[];
    extras?: { id?: number | string; name_ar: string; name_en?: string; price: number }[];
    is_available?: boolean;
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    [key: string]: any;
};

// ================= THEME 9 (DIABLO) CONSTANTS =================
// The main accent color from Diablo
const T9_RED = '#e74c3c';

interface CategoryWithItemsType {
    id: string | number;
    name_ar: string;
    name_en?: string;
    items?: MenuItem[];
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    [key: string]: any;
}

interface RestaurantType {
    name: string;
    theme?: string;
    theme_colors?: {
        main_color?: string;
        bg_color?: string;
        text_color?: string;
    };
    cover_images?: string[];
    marquee_enabled?: boolean;
    marquee_text_ar?: string;
    marquee_text_en?: string;
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    [key: string]: any;
}

interface Theme9MenuProps {
    config: RestaurantType;
    categories: CategoryWithItemsType[];
}

export default function Theme9Menu({ config, categories }: Theme9MenuProps) {
    const { theme, setTheme } = useTheme();
    const [mounted, setMounted] = useState(false);
    useEffect(() => setMounted(true), []);

    const isAr = config.default_language === 'ar';
    const isDark = mounted && theme === 'dark';
    const cur = ''; // Removed SAR per user request

    // Theme Variables
    const bgBody = isDark ? '#0f172a' : '#fafafa';
    const bgWhite = isDark ? '#1e293b' : '#ffffff';
    const textDark = isDark ? '#f8fafc' : '#333333';
    const textMuted = isDark ? '#94a3b8' : '#999999';
    const borderColor = isDark ? '#334155' : '#eeeeee';

    // State
    const [activeCategory, setActiveCategory] = useState<string>('all');
    const [searchQuery, setSearchQuery] = useState('');
    const [isSearchOpen, setIsSearchOpen] = useState(false);
    const [isPhoneMenuOpen, setIsPhoneMenuOpen] = useState(false);

    // Drawers & Modals
    const [isCartOpen, setIsCartOpen] = useState(false);
    const [isDrawerOpen, setIsDrawerOpen] = useState(false);
    const [selectedItem, setSelectedItem] = useState<{ item: MenuItem; catName: string; catImg?: string } | null>(null);

    // Item Detail Modal
    const [qty, setQty] = useState(1);
    const [sizeIdx, setSizeIdx] = useState(0);
    const [notes, setNotes] = useState('');
    const [selectedExtras, setSelectedExtras] = useState<{ id: number | string, name: string, price: number }[]>([]);
    const [customerInfo, setCustomerInfo] = useState({ name: '', phone: '', address: '' });

    const [cart, setCart] = useState<{ id: string, item: MenuItem, catName: string, price: number, sizeLabel: string, quantity: number, notes: string }[]>([]);

    // Category refs for scroll spy
    const sectionRefs = useRef<(HTMLDivElement | null)[]>([]);
    const catScrollRef = useRef<HTMLDivElement>(null);

    // Derived
    const filteredCategories = React.useMemo(() => {
        if (!searchQuery) return categories;
        const q = searchQuery.toLowerCase();
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        return categories.map((c: any) => ({
            ...c,
            // eslint-disable-next-line @typescript-eslint/no-explicit-any
            items: c.items?.filter((i: any) =>
                (i.title_ar || '').toLowerCase().includes(q) ||
                (i.title_en || '').toLowerCase().includes(q)
            ) || []
            // eslint-disable-next-line @typescript-eslint/no-explicit-any
        })).filter((c: any) => c.items && c.items.length > 0);
    }, [categories, searchQuery]);

    const activeCatList = (activeCategory === 'all' || searchQuery)
        ? filteredCategories
        : filteredCategories.filter(c => c.id === activeCategory);

    const itemName = (item: MenuItem) => isAr ? item.title_ar : (item.title_en || item.title_ar);
    const catName = (cat: CategoryWithItemsType) => isAr ? cat.name_ar : (cat.name_en || cat.name_ar);

    // Sync scroll
    useEffect(() => {
        if (searchQuery || activeCategory !== 'all') return;
        const handleScroll = () => {
            const scrollPos = window.scrollY + 200;
            let currentSection = sectionRefs.current[0]?.id.replace('cat-', '') || 'all';
            for (const section of sectionRefs.current) {
                if (section && section.offsetTop <= scrollPos) {
                    currentSection = section.id.replace('cat-', '');
                }
            }
            // Auto scroll nav
            if (currentSection !== activeCategory) {
                setActiveCategory(currentSection);
                const activeTab = catScrollRef.current?.querySelector(`[data-cat="${currentSection}"]`);
                if (activeTab && catScrollRef.current) {
                    const navRect = catScrollRef.current.getBoundingClientRect();
                    const tabRect = activeTab.getBoundingClientRect();
                    if (tabRect.left < navRect.left || tabRect.right > navRect.right) {
                        activeTab.scrollIntoView({ behavior: 'smooth', block: 'nearest', inline: 'center' });
                    }
                }
            }
        };
        window.addEventListener('scroll', handleScroll, { passive: true });
        return () => window.removeEventListener('scroll', handleScroll);
    }, [activeCategory, searchQuery]);

    const scrollToSection = (id: string) => {
        setActiveCategory(id);
        if (id === 'all') {
            window.scrollTo({ top: 300, behavior: 'smooth' });
            return;
        }
        const el = document.getElementById(`cat-${id}`);
        if (el) {
            const y = el.getBoundingClientRect().top + window.scrollY - 100;
            window.scrollTo({ top: y, behavior: 'smooth' });
        }
    };

    // Cart Logic
    const addToCart = () => {
        if (!selectedItem) return;
        const basePrice = selectedItem.item.prices[sizeIdx] || 0;
        const extrasTotal = selectedExtras.reduce((sum, ext) => sum + ext.price, 0);
        const finalPrice = basePrice + extrasTotal;
        const label = selectedItem.item.size_labels?.[sizeIdx] || '';

        let finalNotes = notes;
        if (selectedExtras.length > 0) {
            finalNotes += (finalNotes ? '\n' : '') + 'إضافات: ' + selectedExtras.map(e => e.name).join('، ');
        }

        const ci = {
            id: `${selectedItem.item.id}-${sizeIdx}-${selectedExtras.map(e => e.id).join('-')}`,
            item: selectedItem.item,
            catName: selectedItem.catName,
            price: finalPrice, sizeLabel: label, quantity: qty, notes: finalNotes,
        };
        setCart(prev => {
            const ex = prev.find(i => i.id === ci.id && i.notes === ci.notes);
            if (ex) return prev.map(i => i === ex ? { ...i, quantity: i.quantity + ci.quantity } : i);
            return [...prev, ci];
        });
        closeModal();
    };

    const addToCartDirect = (item: MenuItem, catNameStr: string) => {
        const price = item.prices[0] || 0;
        const label = item.size_labels?.[0] || '';
        const ci = {
            id: `${item.id}-0-`,
            item,
            catName: catNameStr,
            price, sizeLabel: label, quantity: 1, notes: '',
        };
        setCart(prev => {
            const ex = prev.find(i => i.id === ci.id && i.notes === '');
            if (ex) return prev.map(i => i === ex ? { ...i, quantity: i.quantity + 1 } : i);
            return [...prev, ci];
        });
    };

    const closeModal = () => { setSelectedItem(null); setQty(1); setSizeIdx(0); setNotes(''); setSelectedExtras([]); };
    const cartCount = cart.reduce((s, i) => s + i.quantity, 0);
    const cartTotal = cart.reduce((s, i) => s + i.price * i.quantity, 0);
    const updateQty = (id: string, n: string, d: number) => {
        setCart(prev => prev.map(i => {
            if (i.id === id && i.notes === n) {
                const nq = i.quantity + d;
                return nq > 0 ? { ...i, quantity: nq } : i;
            }
            return i;
        }).filter(i => i.quantity > 0));
    };

    const handleCardButtonClick = (e: React.MouseEvent, item: MenuItem, catNameStr: string, catImg?: string) => {
        e.stopPropagation();
        if ((item.prices && item.prices.length > 1) || (item.extras && item.extras.length > 0)) {
            setSelectedItem({ item, catName: catNameStr, catImg });
        } else {
            addToCartDirect(item, catNameStr);
        }
    };

    const checkOutWhatsApp = () => {
        if (!config.social_links?.whatsapp && !config.whatsapp_number && !config.phone) {
            alert(isAr ? "عذراً، المطعم لم يقم بتوفير رقم واتساب للطلبات." : "Sorry, the restaurant hasn't provided a WhatsApp number for orders.");
            return;
        }
        if (!customerInfo.name || !customerInfo.phone || !customerInfo.address) {
            alert(isAr ? "⚠️ يرجى إدخال جميع البيانات (الاسم، الموبايل، العنوان) لإتمام الطلب" : "⚠️ Please enter all details to complete the order");
            return;
        }

        let message = `*🧾 فاتورة طلب جديدة - ${config.name}*\n`;
        message += `------------------------------\n`;
        message += `👤 *الاسم:* ${customerInfo.name}\n`;
        message += `📞 *الموبايل:* ${customerInfo.phone}\n`;
        message += `📍 *العنوان:* ${customerInfo.address}\n`;
        message += `------------------------------\n`;
        message += `*📋 الأصناف المطلوبة:*\n\n`;

        cart.forEach((c, idx) => {
            message += `${idx + 1}. ✨ *${itemName(c.item)}*\n`;
            if (c.sizeLabel && c.sizeLabel !== 'عادي') message += `   📏 ${c.sizeLabel}\n`;
            if (c.notes) message += `   📝 ${c.notes}\n`;
            message += `   💵 السعر: ${c.price}\n`;
            message += `   🔢 الكمية: ${c.quantity}\n`;
            message += `   💰 المجموع: *${c.price * c.quantity}*\n\n`;
        });

        message += `------------------------------\n`;
        message += `*💵 الإجمالي المطلوب: ${cartTotal}*\n`;
        message += `------------------------------\n`;

        const waNumber = config.social_links?.whatsapp || config.whatsapp_number || config.phone;
        const formattedNumber = waNumber.replace(/\+/g, '');
        window.open(`https://wa.me/${formattedNumber}?text=${encodeURIComponent(message)}`, '_blank');
    };

    // Body style
    useEffect(() => {
        document.body.style.backgroundColor = bgBody;
        document.body.style.direction = isAr ? 'rtl' : 'ltr';
        return () => {
            document.body.style.backgroundColor = '';
            document.body.style.direction = '';
        };
    }, [bgBody, isAr]);

    return (
        <div className={`min-h-screen pb-24 font-cairo ${isDark ? 'dark' : ''}`} style={{ backgroundColor: bgBody, color: textDark }}>

            {/* ─── SHARED MARQUEE ─── */}
            {config.marquee_enabled && (config.marquee_text_ar || config.marquee_text_en) && (
                <SharedMarquee
                    text={isAr ? (config.marquee_text_ar || config.marquee_text_en || '') : (config.marquee_text_en || config.marquee_text_ar || '')}
                    bgColor={T9_RED}
                />
            )}

            {/* ─── COVER BANNER ─── */}
            <div className="relative w-full h-[220px] md:h-[450px] overflow-hidden bg-gradient-to-br from-gray-800 to-gray-900">
                {config.cover_images && config.cover_images.length > 0 ? (
                    <Swiper
                        modules={[Autoplay, EffectFade]}
                        effect="fade"
                        autoplay={{ delay: 3000, disableOnInteraction: false }}
                        loop={config.cover_images.length > 1}
                        className="w-full h-full absolute inset-0 z-0"
                    >
                        {config.cover_images.map((img: string, idx: number) => (
                            <SwiperSlide key={idx}>
                                <img src={img || 'https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=1000'}
                                    alt={`Cover ${idx}`}
                                    className="w-full h-full object-cover" />
                            </SwiperSlide>
                        ))}
                    </Swiper>
                ) : (
                    <img src={config.cover_url || 'https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=1000'}
                        alt={config.name}
                        className="w-full h-full object-cover absolute inset-0 z-0" />
                )}

                <div className="absolute inset-0 z-10 bg-gradient-to-t from-black/80 via-black/30 to-transparent flex items-end p-6 md:p-12">
                    <div className="flex items-center gap-6 w-full max-w-6xl mx-auto">
                        <img src={config.logo_url} alt="Logo" className="w-20 h-20 md:w-32 md:h-32 rounded-full border-4 shadow-xl flex-shrink-0" style={{ borderColor: T9_RED }} />
                        <div>
                            <h1 className="text-white text-3xl md:text-5xl font-black drop-shadow-md mb-2 tracking-tight">
                                {config.name}
                            </h1>
                            <p className="text-white/90 text-sm md:text-lg font-medium drop-shadow-sm line-clamp-2 max-w-2xl">
                                {config.description}
                            </p>
                            {/* Desktop Socials */}
                            <div className="hidden md:flex gap-3 mt-4">
                                {config.social_links?.whatsapp && <a href={`https://wa.me/${config.social_links.whatsapp}`} className="bg-white/20 hover:bg-white/40 p-2 rounded-full text-white transition-colors"><FaWhatsapp className="w-5 h-5" /></a>}
                                {config.social_links?.instagram && <a href={`https://instagram.com/${config.social_links.instagram}`} className="bg-white/20 hover:bg-white/40 p-2 rounded-full text-white transition-colors"><FaInstagram className="w-5 h-5" /></a>}
                            </div>
                        </div>
                    </div>
                </div>
                {/* Mobile Menu Bars Toggle */}
                <button onClick={() => setIsDrawerOpen(true)} className="absolute top-4 right-4 z-50 md:hidden bg-white/20 backdrop-blur-md p-2 rounded-full text-white shadow-lg">
                    <List className="w-6 h-6" />
                </button>
            </div>

            {/* ─── CATEGORY NAVIGATION (STICKY) ─── */}
            <nav className="sticky top-0 z-40 shadow-sm" style={{ background: bgWhite, borderBottom: `1px solid ${borderColor}` }}>
                <div className="max-w-6xl mx-auto flex items-center justify-between px-4 py-2">

                    {/* Left side (Icons, Search, Dark Mode) */}
                    <div className="flex items-center gap-1.5 md:gap-2 ml-2 md:ml-4 hide-scrollbar order-last md:order-first" dir="ltr" style={{ overflowX: 'auto', minWidth: 'fit-content' }}>
                        {config.map_link ? (
                            <a href={config.map_link} target="_blank" rel="noreferrer" className="w-8 h-8 md:w-9 md:h-9 rounded-full flex shrink-0 items-center justify-center bg-gray-100 dark:bg-slate-800 text-red-500 hover:bg-red-50 dark:hover:bg-slate-700 transition-colors border border-gray-200 dark:border-slate-700">
                                <MapPin className="w-4 h-4 md:w-4.5 md:h-4.5" />
                            </a>
                        ) : (config.latitude && config.longitude && (
                            <a href={`https://www.google.com/maps/search/?api=1&query=${config.latitude},${config.longitude}`} target="_blank" rel="noreferrer" className="w-8 h-8 md:w-9 md:h-9 rounded-full flex shrink-0 items-center justify-center bg-gray-100 dark:bg-slate-800 text-red-500 hover:bg-red-50 dark:hover:bg-slate-700 transition-colors border border-gray-200 dark:border-slate-700">
                                <MapPin className="w-4 h-4 md:w-4.5 md:h-4.5" />
                            </a>
                        ))}

                        {(config.social_links?.facebook || config.facebook_url) && (
                            <a href={config.social_links?.facebook || config.facebook_url} target="_blank" rel="noreferrer" className="w-8 h-8 md:w-9 md:h-9 rounded-full flex shrink-0 items-center justify-center bg-gray-100 dark:bg-slate-800 text-[#1877f2] hover:bg-blue-50 dark:hover:bg-slate-700 transition-colors border border-gray-200 dark:border-slate-700">
                                <FaFacebookF className="text-sm md:text-base" />
                            </a>
                        )}

                        {(config.social_links?.whatsapp || config.whatsapp_number) && (
                            <a href={`https://wa.me/${(config.social_links?.whatsapp || config.whatsapp_number).replace(/\+/g, '')}`} target="_blank" rel="noreferrer" className="w-8 h-8 md:w-9 md:h-9 rounded-full flex shrink-0 items-center justify-center bg-gray-100 dark:bg-slate-800 text-[#25D366] hover:bg-green-50 dark:hover:bg-slate-700 transition-colors border border-gray-200 dark:border-slate-700">
                                <FaWhatsapp className="text-sm md:text-base md:text-lg" />
                            </a>
                        )}

                        {config.website_url && (
                            <a href={config.website_url} target="_blank" rel="noreferrer" className="w-8 h-8 md:w-9 md:h-9 rounded-full flex shrink-0 items-center justify-center bg-gray-100 dark:bg-slate-800 text-gray-600 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-slate-700 transition-colors border border-gray-200 dark:border-slate-700">
                                <Globe className="w-4 h-4 md:w-4.5 md:h-4.5" />
                            </a>
                        )}

                        {(config.phone || (config.phones && config.phones.length > 0)) && (
                            <div className="relative shrink-0">
                                <button
                                    onClick={() => setIsPhoneMenuOpen(!isPhoneMenuOpen)}
                                    className="w-8 h-8 md:w-9 md:h-9 rounded-full flex items-center justify-center bg-gray-100 dark:bg-slate-800 text-[#e74c3c] hover:bg-red-50 dark:hover:bg-slate-700 transition-colors border border-gray-200 dark:border-slate-700"
                                >
                                    <PhoneCall className="w-4 h-4 md:w-4.5 md:h-4.5" />
                                </button>

                                <AnimatePresence>
                                    {isPhoneMenuOpen && (
                                        <motion.div
                                            initial={{ opacity: 0, y: 10 }}
                                            animate={{ opacity: 1, y: 0 }}
                                            exit={{ opacity: 0, y: 10 }}
                                            className="absolute top-full mt-2 left-0 w-48 bg-white dark:bg-slate-800 rounded-xl shadow-2xl border border-gray-100 dark:border-slate-700 py-2 z-[110] overflow-hidden"
                                        >
                                            <div className="px-4 py-2 border-b border-gray-100 dark:border-slate-700 text-xs font-bold text-gray-500 uppercase text-center" dir={isAr ? 'rtl' : 'ltr'}>
                                                أرقام الديلفري
                                            </div>
                                            {config.phones && config.phones.length > 0 ? (
                                                config.phones.map((phoneNum: string, idx: number) => (
                                                    <a
                                                        key={idx}
                                                        href={`tel:${phoneNum}`}
                                                        className="block px-4 py-2.5 text-center text-sm font-bold text-gray-800 dark:text-gray-200 hover:bg-gray-50 dark:hover:bg-slate-700 transition-colors"
                                                    >
                                                        {phoneNum}
                                                    </a>
                                                ))
                                            ) : (
                                                <a
                                                    href={`tel:${config.phone}`}
                                                    className="block px-4 py-2.5 text-center text-sm font-bold text-gray-800 dark:text-gray-200 hover:bg-gray-50 dark:hover:bg-slate-700 transition-colors"
                                                >
                                                    {config.phone}
                                                </a>
                                            )}
                                        </motion.div>
                                    )}
                                </AnimatePresence>
                            </div>
                        )}

                        <div className="w-px h-6 bg-gray-200 dark:bg-slate-700 mx-1 shrink-0"></div>

                        <button onClick={() => setIsSearchOpen(!isSearchOpen)}
                            className="p-2 shrink-0 flex-shrink-0 rounded-full transition-colors w-8 h-8 md:w-9 md:h-9 flex items-center justify-center bg-gray-100 dark:bg-slate-800 hover:bg-gray-200 dark:hover:bg-slate-700"
                            style={{ color: isSearchOpen ? T9_RED : textMuted }}>
                            <Search className="w-4 h-4" />
                        </button>

                        <button onClick={() => setTheme(isDark ? 'light' : 'dark')} className="p-2 shrink-0 flex-shrink-0 rounded-full transition-colors w-8 h-8 md:w-9 md:h-9 flex items-center justify-center bg-gray-100 dark:bg-slate-800 hover:bg-gray-200 dark:hover:bg-slate-700 ml-1">
                            {isDark ? <Sun className="w-4 h-4 text-yellow-400" /> : <Moon className="w-4 h-4 text-slate-700" />}
                        </button>
                    </div>

                    {/* Right side (Categories) - RTL */}
                    <div className="flex-1 overflow-x-auto flex items-center gap-2 pr-2 order-first md:order-last" style={{ scrollbarWidth: 'none' }} ref={catScrollRef}>
                        <button data-cat="all" onClick={() => scrollToSection('all')}
                            className="flex items-center gap-2 px-5 py-2.5 rounded-xl font-bold text-sm shrink-0 whitespace-nowrap transition-all"
                            style={{
                                background: activeCategory === 'all' ? T9_RED : 'transparent',
                                color: activeCategory === 'all' ? '#fff' : textDark
                            }}>
                            {isAr ? 'الكل' : 'All'}
                        </button>
                        {categories.filter(c => c.items && c.items.length > 0).map(cat => {
                            const active = activeCategory === cat.id.toString();
                            return (
                                <button key={cat.id} data-cat={cat.id} onClick={() => scrollToSection(cat.id.toString())}
                                    className="flex items-center gap-2 px-5 py-2.5 rounded-xl font-bold text-sm shrink-0 whitespace-nowrap transition-all"
                                    style={{
                                        background: active ? T9_RED : 'transparent',
                                        color: active ? '#fff' : textDark
                                    }}>
                                    {isAr ? cat.name_ar : (cat.name_en || cat.name_ar)}
                                </button>
                            );
                        })}
                    </div>
                </div>
            </nav>

            {/* ─── SEARCH SECTION ─── */}
            <AnimatePresence>
                {isSearchOpen && (
                    <motion.div initial={{ height: 0, opacity: 0 }} animate={{ height: 'auto', opacity: 1 }} exit={{ height: 0, opacity: 0 }}
                        className="overflow-hidden border-b" style={{ background: bgWhite, borderColor }}>
                        <div className="max-w-6xl mx-auto p-4">
                            <div className="relative">
                                <Search className="w-5 h-5 absolute right-4 top-1/2 -translate-y-1/2" style={{ color: textMuted }} />
                                <input type="text" value={searchQuery} onChange={e => setSearchQuery(e.target.value)}
                                    placeholder={isAr ? 'ابحث عن صنف...' : 'Search items...'}
                                    className="w-full py-3 pr-12 pl-4 rounded-full text-sm outline-none transition-colors border"
                                    style={{ background: bgBody, color: textDark, borderColor: searchQuery ? T9_RED : borderColor }} />
                            </div>
                        </div>
                    </motion.div>
                )}
            </AnimatePresence>

            {/* ─── MAIN CONTENT ─── */}
            <main className="max-w-6xl mx-auto px-4 py-8 flex gap-8">

                {/* ITEMS GRID (Mobile: 2 cols, Desktop: 3-4 cols) */}
                <div className="flex-1 w-full min-w-0">
                    {searchQuery && filteredCategories.length === 0 && (
                        <div className="flex flex-col items-center justify-center py-20 opacity-50">
                            <UtensilsCrossed className="w-16 h-16 mb-4" />
                            <p className="font-bold">{isAr ? 'لا يوجد نتائج' : 'No results found'}</p>
                        </div>
                    )}

                    {activeCatList.map((cat, catIdx) => (
                        <div key={cat.id} id={`cat-${cat.id}`} ref={el => { sectionRefs.current[catIdx] = el; }} className="mb-10 scroll-m-24">
                            {/* Category Header */}
                            <div className="flex items-center gap-3 mb-6 px-1">
                                <h2 className="text-xl md:text-2xl font-black flex items-center gap-2" style={{ color: T9_RED }}>
                                    {catName(cat)}
                                </h2>
                                <span className="text-xs px-2 py-0.5 rounded-full font-bold" style={{ background: bgWhite, color: textMuted, border: `1px solid ${borderColor}` }}>
                                    {cat.items?.length || 0}
                                </span>
                                <div className="flex-1 h-px" style={{ background: borderColor }} />
                            </div>

                            {/* Diablo Style Item Grid */}
                            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-3 md:gap-6">
                                {/* eslint-disable-next-line @typescript-eslint/no-explicit-any */}
                                {cat.items?.filter((i: any) => i.is_available !== false).map((item: any) => (
                                    <div key={item.id} onClick={() => setSelectedItem({ item, catName: catName(cat), catImg: cat.image_url || cat.image })}
                                        className="relative rounded-[14px] md:rounded-[20px] overflow-hidden cursor-pointer group flex flex-col transition-all duration-300 transform hover:-translate-y-1"
                                        style={{ background: bgWhite, boxShadow: isDark ? '0 4px 20px rgba(0,0,0,0.3)' : '0 2px 12px rgba(0,0,0,0.06)', border: `1px solid ${borderColor}` }}>

                                        {/* Top Image Box */}
                                        <div className="w-full h-32 md:h-48 overflow-hidden bg-gray-100 relative">
                                            <img src={item.image_url || cat.image_url || cat.image || 'https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=500'}
                                                alt={itemName(item)}
                                                className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110" loading="lazy" />
                                            {/* Price Badge over image on mobile/small screens (optional) */}
                                            {item.calories && (
                                                <div className="absolute top-2 right-2 bg-black/60 backdrop-blur-md text-white px-2 py-0.5 rounded text-[10px] font-bold">
                                                    {item.calories} {isAr ? 'كالوري' : 'Cal'}
                                                </div>
                                            )}
                                        </div>

                                        {/* Body */}
                                        <div className="p-3 md:p-5 flex-1 flex flex-col text-center">
                                            <h3 className="font-extrabold text-[13px] md:text-base mb-1 md:mb-2 line-clamp-1 leading-tight" style={{ color: textDark }}>
                                                {itemName(item)}
                                            </h3>
                                            {item.desc_ar && (
                                                <p className="text-[10px] md:text-xs line-clamp-2 md:line-clamp-3 mb-3 md:mb-4 min-h-[1.5rem] md:min-h-[2.5rem]" style={{ color: textMuted, lineHeight: 1.4 }}>
                                                    {isAr ? item.desc_ar : (item.desc_en || item.desc_ar)}
                                                </p>
                                            )}

                                            {/* Footer area */}
                                            <div className="mt-auto flex flex-col gap-2 w-full" dir={isAr ? 'rtl' : 'ltr'}>
                                                <div className="flex flex-col gap-1 w-full text-right mb-2 px-1">
                                                    {item.prices.map((p: number, idx: number) => (
                                                        <div key={idx} className="flex justify-between items-center w-full">
                                                            <span className="text-[11px] md:text-sm text-gray-500 font-bold whitespace-nowrap">
                                                                {item.size_labels && item.size_labels[idx] ? item.size_labels[idx] : (isAr ? `الحجم ${idx + 1}` : `Size ${idx + 1}`)}
                                                            </span>
                                                            <span className="font-black text-[13px] md:text-[15px] tracking-tight shrink-0" style={{ color: T9_RED }}>
                                                                {cur}{p?.toFixed?.(0) || p}
                                                            </span>
                                                        </div>
                                                    ))}
                                                </div>
                                                <button onClick={(e) => handleCardButtonClick(e, item, catName(cat), cat.image_url)}
                                                    className="w-full py-2 rounded-lg md:rounded-xl text-[12px] md:text-sm font-bold text-white transition-colors active:scale-95"
                                                    style={{ background: T9_RED }}>
                                                    {isAr ? 'إضافة إلى السلة' : 'Add to Cart'}
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    ))}

                    {/* Footer Inside Main Layout Removed per request */}
                </div>

                {/* SIDEBAR (Desktop Only) */}
                <div className="hidden lg:block w-[300px] shrink-0">
                    {/* About Card */}
                    <div className="rounded-2xl overflow-hidden shadow-sm mb-6 border" style={{ background: bgWhite, borderColor }}>
                        <div className="py-3 px-4 font-black text-center border-b" style={{ color: T9_RED, borderColor }}>
                            {isAr ? 'عن المطعم' : 'About us'}
                        </div>
                        <div className="p-4 space-y-3 text-sm" style={{ color: textDark }}>
                            {(config.social_links?.phone || config.phone) && (
                                <div className="flex gap-3">
                                    <Phone className="w-4 h-4 shrink-0 mt-0.5" style={{ color: T9_RED }} />
                                    <span>{config.social_links?.phone || config.phone}</span>
                                </div>
                            )}
                            {(config.social_links?.location_url || config.address) && (
                                <div className="flex gap-3">
                                    <MapPin className="w-4 h-4 shrink-0 mt-0.5" style={{ color: T9_RED }} />
                                    <span className="line-clamp-2">{config.address || 'Location link'}</span>
                                </div>
                            )}
                            {config.time_open && (
                                <div className="flex gap-3">
                                    <List className="w-4 h-4 shrink-0 mt-0.5" style={{ color: T9_RED }} />
                                    <span>{config.time_open} - {config.time_close}</span>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </main>

            {/* ─── ITEM DETAIL MODAL ─── */}
            <AnimatePresence>
                {selectedItem && (
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}
                        className="fixed inset-0 z-[200] flex items-center justify-center p-4 bg-black/50 backdrop-blur-sm"
                        onClick={closeModal}>
                        <motion.div initial={{ scale: 0.95, y: 20 }} animate={{ scale: 1, y: 0 }} exit={{ scale: 0.95, y: 20 }}
                            className="relative w-full max-w-[500px] max-h-[85vh] rounded-[24px] overflow-hidden flex flex-col shadow-2xl"
                            style={{ background: bgWhite }}
                            onClick={e => e.stopPropagation()}>

                            {/* Close Button */}
                            <button onClick={closeModal} className="absolute top-4 left-4 z-10 w-8 h-8 rounded-full bg-black/50 text-white flex items-center justify-center backdrop-blur-md hover:bg-black/70 transition-colors">
                                <X className="w-5 h-5" />
                            </button>

                            {/* Image Box */}
                            <div className="w-full h-[250px] shrink-0 relative flex items-center justify-center" style={{ backgroundColor: bgBody }}>
                                <img src={selectedItem.item.image_url || selectedItem.catImg || 'https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=600'}
                                    alt={itemName(selectedItem.item)}
                                    className="w-full h-full object-cover" />
                            </div>

                            {/* Scrollable Content */}
                            <div className="p-6 overflow-y-auto" style={{ color: textDark }}>
                                <div className="text-center mb-6">
                                    <h2 className="text-2xl font-black mb-2 leading-tight">{itemName(selectedItem.item)}</h2>
                                    {selectedItem.item.desc_ar && (
                                        <p className="text-sm leading-relaxed" style={{ color: textMuted }}>
                                            {isAr ? selectedItem.item.desc_ar : (selectedItem.item.desc_en || selectedItem.item.desc_ar)}
                                        </p>
                                    )}
                                    <div className="text-2xl font-black mt-3" style={{ color: T9_RED }}>
                                        {cur}{(selectedItem.item.prices[sizeIdx] || 0)}
                                    </div>
                                </div>

                                {/* Size Options */}
                                {selectedItem.item.prices.length > 1 && (
                                    <div className="mb-6">
                                        <h3 className="text-sm font-bold mb-3 pb-2 border-b" style={{ borderColor, color: textDark }}>{isAr ? 'الحجم' : 'Size'}</h3>
                                        <div className="space-y-2">
                                            {selectedItem.item.prices.map((_p: number, idx: number) => {
                                                const label = selectedItem.item.size_labels?.[idx] || (isAr ? `حجم ${idx + 1}` : `Size ${idx + 1}`);
                                                return (
                                                    <label key={idx} className="flex items-center justify-between p-3 rounded-xl border cursor-pointer transition-colors"
                                                        style={{ borderColor: sizeIdx === idx ? T9_RED : borderColor, background: sizeIdx === idx ? (isDark ? 'rgba(231,76,60,0.1)' : '#fceceb') : 'transparent' }}
                                                        onClick={() => setSizeIdx(idx)}>
                                                        <span className="font-bold flex items-center gap-2 text-sm">{label}</span>
                                                        <input type="radio" checked={sizeIdx === idx} readOnly className="hidden" />
                                                    </label>
                                                );
                                            })}
                                        </div>
                                    </div>
                                )}

                                {/* Extras */}
                                {selectedItem.item.extras && selectedItem.item.extras.length > 0 && (
                                    <div className="mb-6">
                                        <div className="flex justify-between items-center mb-3 pb-2 border-b" style={{ borderColor }}>
                                            <span className="text-xs" style={{ color: textMuted }}>{isAr ? '(اختياري)' : '(Optional)'}</span>
                                            <h3 className="text-sm font-bold" style={{ color: textDark }}>{isAr ? 'الإضافات' : 'Extras'}</h3>
                                        </div>
                                        <div className="space-y-2">
                                            {selectedItem.item.extras.map((ext: { id?: number | string, name_ar: string, name_en?: string, price: number }, idx: number) => {
                                                const isSel = selectedExtras.some(e => e.id === (ext.id || idx));
                                                return (
                                                    <label key={idx} className="flex items-center justify-between p-3 rounded-xl border cursor-pointer transition-colors"
                                                        style={{ borderColor: isSel ? T9_RED : borderColor, background: isSel ? (isDark ? 'rgba(231,76,60,0.1)' : '#fceceb') : 'transparent' }}>
                                                        <span className="font-bold text-sm text-center w-20 shrink-0" style={{ color: T9_RED }}>
                                                            {ext.price > 0 ? `+${cur}${ext.price}` : 'مجاناً'}
                                                        </span>
                                                        <div className="flex items-center gap-3">
                                                            <span className="text-sm font-bold">{isAr ? ext.name_ar : (ext.name_en || ext.name_ar)}</span>
                                                            <div className="w-5 h-5 rounded border flex items-center justify-center"
                                                                style={{ borderColor: isSel ? T9_RED : textMuted, background: isSel ? T9_RED : 'transparent' }}>
                                                                {isSel && <X className="w-3 h-3 text-white rotate-45 transform" style={{ transform: 'rotate(0deg)' }} />}
                                                            </div>
                                                        </div>
                                                        <input type="checkbox" className="hidden" checked={isSel}
                                                            onChange={() => {
                                                                if (isSel) setSelectedExtras(p => p.filter(e => e.id !== (ext.id || idx)));
                                                                else setSelectedExtras(p => [...p, { id: ext.id || idx, name: isAr ? ext.name_ar : (ext.name_en || ext.name_ar), price: ext.price }]);
                                                            }} />
                                                    </label>
                                                );
                                            })}
                                        </div>
                                    </div>
                                )}

                                {/* Note */}
                                <textarea value={notes} onChange={e => setNotes(e.target.value)}
                                    placeholder={isAr ? 'ملاحظات خاصة...' : 'Special instructions...'}
                                    className="w-full rounded-xl p-3 outline-none resize-none h-24 text-sm border"
                                    style={{ background: bgBody, color: textDark, borderColor }} />

                                {/* Qty Row */}
                                <div className="flex items-center justify-center gap-6 my-6">
                                    <button onClick={() => setQty(Math.max(1, qty - 1))} className="w-12 h-12 rounded-full border-2 flex flex-col items-center justify-center transition-colors hover:text-red-500 hover:border-red-500"
                                        style={{ borderColor, color: textDark }}>
                                        <Minus className="w-5 h-5" />
                                    </button>
                                    <span className="text-3xl font-black min-w-[40px] text-center">{qty}</span>
                                    <button onClick={() => setQty(qty + 1)} className="w-12 h-12 rounded-full border-2 flex flex-col items-center justify-center transition-colors hover:text-red-500 hover:border-red-500"
                                        style={{ borderColor, color: textDark }}>
                                        <Plus className="w-5 h-5" />
                                    </button>
                                </div>

                                {/* Add Button */}
                                <button onClick={addToCart} className="w-full py-4 rounded-xl text-white font-black text-lg transition-colors active:scale-95"
                                    style={{ background: T9_RED }}>
                                    {isAr ? 'إضافة إلى السلة' : 'Add to Cart'} — {cur}{(((selectedItem.item.prices[sizeIdx] || 0) + selectedExtras.reduce((s, e) => s + e.price, 0)) * qty).toFixed?.(0)}
                                </button>
                            </div>
                        </motion.div>
                    </motion.div>
                )}
            </AnimatePresence>

            {/* ─── CART SLIDE-IN DRAWER (Diablo style, slides from left in RTL) ─── */}
            <AnimatePresence>
                {isCartOpen && (
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}
                        className="fixed inset-0 z-[9991] bg-black/50 backdrop-blur-sm"
                        onClick={() => setIsCartOpen(false)}>
                        <motion.div initial={{ x: isAr ? '-100%' : '100%' }} animate={{ x: 0 }} exit={{ x: isAr ? '-100%' : '100%' }}
                            transition={{ type: 'spring', damping: 25, stiffness: 200 }}
                            className={`absolute top-0 ${isAr ? 'left-0' : 'right-0'} w-full max-w-[350px] h-full flex flex-col shadow-2xl`}
                            style={{ background: bgWhite }}
                            onClick={e => e.stopPropagation()}>

                            {/* Header */}
                            <div className="p-5 text-white flex justify-between items-center font-bold text-lg shadow-md z-10" style={{ background: T9_RED }}>
                                <span>{isAr ? 'عربة التسوق' : 'Shopping Cart'} ({cartCount})</span>
                                <button onClick={() => setIsCartOpen(false)}><X className="w-6 h-6" /></button>
                            </div>

                            {/* Body */}
                            <div className="flex-1 overflow-y-auto p-5 pb-40">
                                {cart.length === 0 ? (
                                    <div className="flex flex-col items-center justify-center h-full text-center" style={{ color: textMuted }}>
                                        <ShoppingCart className="w-16 h-16 mb-4 opacity-50" />
                                        <p className="font-bold text-lg">{isAr ? 'السلة فارغة' : 'Cart is empty'}</p>
                                    </div>
                                ) : (
                                    <div className="space-y-4">
                                        {cart.map((c, i) => (
                                            <div key={i} className="flex gap-3 pb-4 border-b relative" style={{ borderColor }}>
                                                <img src={c.item.image_url || 'https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=200'}
                                                    alt={itemName(c.item)} className="w-16 h-16 rounded-xl object-cover shrink-0 border" style={{ borderColor }} />
                                                <div className="flex-1 min-w-0 flex flex-col">
                                                    <h4 className="font-bold text-sm mb-1 line-clamp-1" style={{ color: textDark }}>{itemName(c.item)}</h4>
                                                    {(c.sizeLabel || c.notes) && (
                                                        <p className="text-[11px] mb-2 line-clamp-2 leading-tight" style={{ color: textMuted }}>
                                                            {c.sizeLabel && <span>{c.sizeLabel} </span>}
                                                            {c.notes && <span>{c.notes}</span>}
                                                        </p>
                                                    )}
                                                    <div className="mt-auto flex justify-between items-center w-full" dir={isAr ? 'rtl' : 'ltr'}>
                                                        {/* Qty Controls */}
                                                        <div className="flex items-center gap-3">
                                                            <button onClick={() => updateQty(c.id, c.notes, 1)} className="w-6 h-6 rounded-md flex items-center justify-center text-xs font-bold border hover:bg-red-50 hover:text-red-500 hover:border-red-500" style={{ borderColor, color: textDark }}><Plus className="w-3 h-3" /></button>
                                                            <span className="font-bold text-sm" style={{ color: textDark }}>{c.quantity}</span>
                                                            <button onClick={() => updateQty(c.id, c.notes, -1)} className="w-6 h-6 rounded-md flex items-center justify-center text-xs font-bold border hover:bg-red-50 hover:text-red-500 hover:border-red-500" style={{ borderColor, color: textDark }}><Minus className="w-3 h-3" /></button>
                                                        </div>
                                                        {/* Price */}
                                                        <span className="font-black text-sm" style={{ color: T9_RED }}>{cur}{(c.price * c.quantity).toFixed?.(0)}</span>
                                                    </div>
                                                </div>
                                                {/* Remove */}
                                                <button onClick={() => updateQty(c.id, c.notes, -c.quantity)} className="absolute top-0 left-0 p-1 text-gray-300 hover:text-red-500 transition-colors">
                                                    <Trash2 className="w-4 h-4" />
                                                </button>
                                            </div>
                                        ))}

                                        {/* Delivery Info Form */}
                                        <div className="mt-6 space-y-3 pt-2">
                                            <h4 className="font-bold text-sm text-right" style={{ color: textDark }}>{isAr ? 'بيانات التوصيل' : 'Delivery Details'}</h4>
                                            <input type="text" placeholder={isAr ? "الاسم" : "Name"} value={customerInfo.name} onChange={e => setCustomerInfo({ ...customerInfo, name: e.target.value })} className="w-full border rounded-xl p-3 text-sm outline-none transition-all focus:border-red-500" style={{ background: bgBody, color: textDark, borderColor }} dir={isAr ? "rtl" : "ltr"} />
                                            <input type="tel" placeholder={isAr ? "الموبايل" : "Phone"} value={customerInfo.phone} onChange={e => setCustomerInfo({ ...customerInfo, phone: e.target.value })} className="w-full border rounded-xl p-3 text-sm outline-none transition-all focus:border-red-500" style={{ background: bgBody, color: textDark, borderColor }} dir="ltr" />
                                            <textarea placeholder={isAr ? "العنوان بالتفصيل" : "Full Address"} value={customerInfo.address} onChange={e => setCustomerInfo({ ...customerInfo, address: e.target.value })} className="w-full border rounded-xl p-3 text-sm outline-none h-20 resize-none transition-all focus:border-red-500" style={{ background: bgBody, color: textDark, borderColor }} dir={isAr ? "rtl" : "ltr"} />
                                        </div>
                                    </div>
                                )}
                            </div>

                            {/* Footer */}
                            {cart.length > 0 && (
                                <div className="absolute bottom-0 w-full p-5 shadow-[0_-5px_15px_rgba(0,0,0,0.05)] border-t z-20" style={{ background: bgBody, borderColor }}>
                                    <div className="flex justify-between items-center mb-4 text-sm font-black" style={{ color: textDark }}>
                                        <span>{isAr ? 'المجموع' : 'Total'}</span>
                                        <span className="text-xl" style={{ color: T9_RED }}>{cur}{cartTotal.toFixed?.(0)}</span>
                                    </div>
                                    <button onClick={checkOutWhatsApp} className="w-full flex justify-center items-center gap-2 py-3.5 rounded-xl text-white font-black text-lg transition-colors active:scale-95"
                                        style={{ background: '#25d366' }}>
                                        <FaWhatsapp className="w-6 h-6" />
                                        {isAr ? 'تأكيد الطلب' : 'Confirm Order'}
                                    </button>
                                </div>
                            )}
                        </motion.div>
                    </motion.div>
                )}
            </AnimatePresence>

            {/* ─── MOBILE DRAWER (Slides from right in RTL) ─── */}
            <AnimatePresence>
                {isDrawerOpen && (
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}
                        className="fixed inset-0 z-[300] bg-black/40 backdrop-blur-sm md:hidden"
                        onClick={() => setIsDrawerOpen(false)}>
                        <motion.div initial={{ x: isAr ? '100%' : '-100%' }} animate={{ x: 0 }} exit={{ x: isAr ? '100%' : '-100%' }}
                            transition={{ type: 'spring', damping: 25, stiffness: 200 }}
                            className={`absolute top-0 ${isAr ? 'right-0' : 'left-0'} w-[85%] max-w-[320px] h-full flex flex-col shadow-2xl`}
                            style={{ background: bgWhite }}
                            onClick={e => e.stopPropagation()}>

                            {/* Drawer Header (Red with overlapping logo) */}
                            <div className="pt-10 pb-8 px-5 flex flex-col items-center relative text-center text-white" style={{ background: T9_RED }}>
                                <button onClick={() => setIsDrawerOpen(false)} className="absolute top-4 left-4 p-1 opacity-80 hover:opacity-100"><X className="w-6 h-6" /></button>
                                <div className="w-20 h-20 bg-white rounded-full p-1 shadow-lg mb-3">
                                    <img src={config.logo_url} alt="Logo" className="w-full h-full object-cover rounded-full" />
                                </div>
                                <h3 className="font-black text-lg drop-shadow-md">{config.name}</h3>
                            </div>

                            {/* Links */}
                            <div className="flex-1 overflow-y-auto px-5 py-4 space-y-1">
                                {[
                                    { label: isAr ? 'الرئيسية' : 'Home', icon: <Home className="w-5 h-5" />, action: () => { window.scrollTo(0, 0); setIsDrawerOpen(false); } },
                                    { label: isAr ? 'المنيو' : 'Menu', icon: <UtensilsCrossed className="w-5 h-5" />, action: () => { document.querySelector('nav')?.scrollIntoView(); setIsDrawerOpen(false); } },
                                    { label: isAr ? 'تسجيل الدخول' : 'Login', icon: <User className="w-5 h-5" />, action: () => { } },
                                    {
                                        label: isDark ? (isAr ? 'الوضع الفاتح' : 'Light Mode') : (isAr ? 'الوضع الداكن' : 'Dark Mode'),
                                        icon: isDark ? <Sun className="w-5 h-5" /> : <Moon className="w-5 h-5" />,
                                        action: () => setTheme(isDark ? 'light' : 'dark')
                                    }
                                ].map((lnk, i) => (
                                    <button key={i} onClick={lnk.action}
                                        className="w-full flex items-center gap-4 py-4 border-b transition-colors font-bold text-sm"
                                        style={{ borderColor, color: textDark }}>
                                        <div style={{ color: T9_RED }}>{lnk.icon}</div>
                                        <span>{lnk.label}</span>
                                    </button>
                                ))}
                            </div>
                        </motion.div>
                    </motion.div>
                )}
            </AnimatePresence>

            {/* ─── FLOATING CART FAB ─── */}
            <button onClick={() => setIsCartOpen(true)}
                className={`fixed bottom-6 left-6 z-[80] w-14 h-14 rounded-full flex flex-col items-center justify-center text-white shadow-[0_4px_20px_rgba(231,76,60,0.4)] transition-transform duration-300 hover:scale-110 active:scale-95 ${cartCount > 0 ? 'scale-100' : 'scale-0'}`}
                style={{ background: T9_RED }}>
                <ShoppingCart className="w-6 h-6" />
                <span className="absolute -top-1 -left-1 w-6 h-6 rounded-full bg-yellow-400 text-black font-black text-[11px] flex items-center justify-center border-2 border-white">
                    {cartCount}
                </span>
            </button>

        </div>
    );
}
